INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (6, '201510/Ovecka Shaun.m4v', 'Ovecka Shaun ve filmu', 16, 0, 'cz', '  ', 0, 'Shawn The Sheep in the Movie', 'http://www.csfd.cz/film/340977-ovecka-shaun-ve-filmu/', 'Ovecka Shaun.jpg', 0, 'Život na farmě je nekonečná nuda. Každý den plyne ve stejném rytmu, Shaun musí poslouchat a dělat co farmář nařídí. Jak jen mít jeden jediný den volna? To chce pořádný plán, takže Shaun vymyslí způsob, jak mít zase jeden odpočinkový den. Ale i plánovat se musí umět a každý plán se může zvrtnout! Shaun a další ovce přijdou s mazaným trikem jak farmáře uspat. Skáčou stále dokolečka přes ohradu, farmář počítá, počítá a počítá své ovečky, až nakonec usne. Ovce potom farmáře opatrně uloží do starého karavanu v rohu hřiště a předstírají, že je noc. Když farmářův věrný pes Bitzer zjistí, co ovce udělaly, celý plán končí. Blitzer se snaží farmáře dostat z karavanu, ale karavan se uvolní a přes všechny překážky vyjede ven na silnici i se spícím farmářem uvnitř. Bitzer vyrazí za karavanem, který ujíždí přímo do velkoměsta. Shaun a ovčí stádo zatím hospodaří na farmě. Farmář a pes se z města stále nevracejí a na farmě zavládne chaos. Stádo se Shaun v čele se rozhodne napravit svoji chybu a vydá se do velkoměsta najít farmáře a jeho věrného Bitzera. Na této záchranné výpravě je čeká mnoho dobrodružství. Budou Shaun a kamarádi schopni najít svého farmáře dřív, než se ztratí navždy?(Bohemia MP)', '2015-10-16 22:04:36.027', 2015, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (7, '201510/DARK_FURY_1.m4v', 'Riddick: Dark Fury', 4, 0, 'cz', 'cz', 'en', 'Riddick: Dark Fury', 'http://www.csfd.cz/film/173962-riddick-temna-hrozba/', 'DARK_FURY_1.jpg', 0, 'Riddick: Temná hrozba představuje spojovací článek mezi filmy Černočerná Tma a Riddick: Kronika Temna. Po opuštění planety obývané krvelačnými tvory se Riddick, Imam a Jack setkají s vesmírnou lodí, jejíž kapitánka loví ty nejhledanější násilníky a vrahy, které však neodevzdává do rukou spravedlnosti, ale dělá si z nich živé sochy. Riddicka samozřejmě zná a chce z něj mít svojí trofej, jenže předtím se chce trochu pobavit a vidět jeho schopnosti v akci...(cyberhawk)', '2015-10-16 22:04:36.033', 2004, '');
