INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2, '201510/DITE 44.m4v', 'Dite 44', 15, 0, 'cz', 'cz', 'en', 'Child 44', 'http://www.csfd.cz/film/336665-dite-cislo-44/', 'DITE 44.jpg', 0, 'Lev Děmidov je válečný hrdina, jeho svědomitá práce pro sovětskou tajnou policii mu zajistila postavení, dobré bydlení pro sebe i pro rodiče a krásnou ženu. Nikdo si ale nemůže být ničím jistý, Děmidov odmítne udat svou ženu a je státní mocí ze dne na den degradován a poslán do vyhnanství. Zde narazí na vraždu, která se podobá jinému případu, označenému za nehodu. S kariérou a životem na padrť začne hledat sériového vraha. Ovšem ve stalinistickém Sovětském svazu v 50. letech něco jako vražda oficiálně neexistuje, musí proto pátrat na vlastní pěst a tajně proti vůli režimu. Jak vyšetřování pokračuje, rozsah rituálních vražd dostává strašlivé rozměry. Přestože jakákoliv pomoc hrozí zatčením, získá nečekaného spojence. Smyčka se postupně utahuje jak kolem vraha, tak kolem hlavních hrdinů.(Bontonfilm CZ)', '2015-10-16 22:04:36.003', 2015, '');
