INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (13, '201510/U2_360_D1.m4v', 'U2 - 360 Live from the Rose Bowl', 1, 0, 'en', '  ', 'en', 'U2 - 360 Live from the Rose Bowl', 'http://www.csfd.cz/film/279778-u2-360-zive-z-rose-bowl/', 'U2_360_D1.jpg', 0, 'I když se skupina U2 na svém turné i letos vyhne naší republice, jejich příznivci si mohou na HBO vychutnat atmosféru koncertu v americkém Pasadena Rose Bowl, kde se sešlo více než 97 000 diváků, a který se přenášel přes stream na youtube.com do celého světa. Konečně se dočkají i čeští fanoušci a budou moci zhlédnout poslední velké turné U2 s názvem „The 360° Tour“. U2 se sice při svém více než dvouletém turné opět vyhnou České republice, ale budou nablízku - například ve Vídni (30.8.2010) nebo v Mnichově (15.9.2010). Mezitím si můžete vychutnat atmosféru jejich koncertu na HBO a to vše v nebývalé kvalitě a s komfortem. Koncert se natáčel na 27 kamer v americkém Pasadena Rose Bowl, kde se sešlo více než 97 000 diváků, a přenášel se přes stream na youtube.com do celého světa, takže celkový počet diváků překročil 10 miliónů!(oficiální text distributora)', '2015-10-16 22:04:36.071', 2009, '');
