INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (780, 'Ali G Indahouse.m4v', 'Ali G Indahouse', 9, NULL, 'cz', '  ', 'en', 'Ali G Indahouse', 'http://www.csfd.cz/film/11932-ali-g-indahouse-film/', 'Ali G Indahouse.jpg', 10, 'Obklopen svými věrnými se ALI G se svou přítelkyní Julií náhle stává největším ze všech hrdinů - nepřemožitelným, nenasytným a samozřejmě neodolatelným pro všechny dívky! ALI G - kontroverzní a podařený filmový hit číslo 1 s originální superstar britské komedie ze Staines! Tak, jak jste ho nikdy před tím neviděli.(oficiální text distributora)', NULL, 2002, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (781, 'Alica v risi divov -- Alice in Wonderland.m4v', 'Alica v risi divov', 4, NULL, 'cz', '  ', 'en', 'Alice in Wonderland', 'http://www.imdb.com/title/tt1014759/', 'Alica v risi divov -- Alice in Wonderland.jpg', 40, 'Directed by Tim Burton.  With Mia Wasikowska, Johnny Depp, Helena Bonham Carter, Anne Hathaway. Nineteen-year-old Alice returns to the magical world from her childhood adventure, where she reunites with her old friends and learns of her true destiny: to end the Red Queen''s reign of terror.', NULL, 2010, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (782, 'Americky prezident.m4v', 'Americky prezident', 6, NULL, 'cz', '  ', 'en', 'The American President', 'http://www.csfd.cz/film/7619-americky-prezident/', 'Americky prezident.jpg', 40, 'Muž potká ženu a tisk sleduje každý jejich pohyb, rivalové útočí a popularita prezidentského úřadu klesá během sedmi týdnů ze 63% na 41%. Když jste prezidentem, každý ví, jak žijete. Michael Douglas se v této úspěšné romantické komedii jako ovdovělý prezident Spojených států zamiluje do ochránkyně životního prostředí (Annette Bening). Režisér Rob Reiner rozehrává příběh jeho týmu (Martin Sheen, Michael J. Fox, David Paymer, Anna Deavere Smith) a škodolibého protivníka (Richard Dreyfuss). Slavnostní večeře, formální protokoly, kouzelné soukromé chvíle, globální krize to vše je v báječné a vtipné komedii o lásce a moci v Americe 90. let.(oficiální text distributora)', NULL, 1995, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (783, 'Arizona Dream.m4v', 'Arizona Dream', 15, NULL, 'en', '  ', 'en', 'Arizona Dream', 'http://www.csfd.cz/film/5449-arizona-dream/', 'Arizona Dream.jpg', NULL, 'Film nás přivádí do prostředí New Yorku, kde žije Axel (Johnny Depp), který se živí jako strážce ryb v zátoce. Jednoho dne ho navštíví bratranec Paul, který Axela požádá, aby se zúčastnil strýcovy svatby pořádané v rodné Arizoně. Jelikož Axel nabídku odmítá, bratranec ho spícího unáší a Axel se ráno probouzí v těžké kocovině v Arizoně, kde se začnou dít neuvěřitelné věci... Realistický film nás přesvědčuje o tom, že i nerealistické sny je možno plnit. Axel sní o životě na Aljašce mezi Eskymáky v absolutním tichu. Elaine (Fay Dunaway) se chce naučit létat. Grace by chtěla zase zemřít. Sny docházejí ke svému naplnění v momentu, kdy Axel podlehne touze postavit letadlo pro Grace, jelikož se do ní bezhlavě zamiloval. Iracionální rovinu filmu, ve kterém vše létá (ryby, dokonce i sanitky mají svá křídla), podbarvuje balkánská hudba Gorana Bregoviće. Film získal Stříbrného medvěda a Zvláštní cenu poroty na MFF v Berlíně v roce 1993.(oficiální text distributora)', NULL, 1992, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (784, 'At ziji duchove.m4v', 'At ziji duchove', 3, NULL, 'cz', '  ', 'en', NULL, 'http://www.csfd.cz/film/5982-at-ziji-duchove/', 'At ziji duchove.jpg', 100, 'Hrdiny veselé dětské hudební komedie jsou chlapci a děvčata, kteří vytáhnou do boje proti některým dospělým ze své obce, když chtějí proměnit starý hrad v zemědělský objekt. Za vydatné pomoci duchů, rytíře Viléma Brtníka z Brtníku a jeho dcerky Leontýnky obývajících zchátralý objekt, a mnoha trpaslíků, které rytíř přivolal, se jim podaří dosáhnout cíle - hrad je opraven a předán do péče dětí. Z Leontýnky, která si stěžuje na osud nesmrtelných bytostí, se díky kopretině darované Dlouhým Jendou z lásky stane normální dívka...(oficiální text distributora)', NULL, 1977, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (785, 'Balik Penez - Where The Money Is.m4v', 'Balik Penez', 15, NULL, 'cz', '  ', 'en', 'Where The Money Is', 'http://www.csfd.cz/film/15051-balik-penez/', 'Balik Penez - Where The Money Is.jpg', NULL, 'Mladá Carol pracuje jako ošetřovatelka v pečovatelském ústavu a ubíjí ji nuda v práci i v manželství s Waynem. Jednoho dne ale do ústavu přivezou záhadného pacienta - je to postarší sympaťák Henry a Carol zanedlouho zjistí, že nový pacient skrývá nějaké tajemství. To vyprovokuje její zvědavost s časem se oba spřátelí. Vyjde ale najevo, že Henry je zkušený zloděj, který se do ústavu dostal proto, aby se vyhnul vězení. Vznikne mezi nimi zvláštní vztah a ten v Carol probudí odvahu a schopnosti, o kterých sama nevěděla. S těmito novými zlodějskými ambicemi Carol přesvědčí Henryho, aby spolu udělali jeho poslední akci. Henry ji a Wayna začne učit a tato zvláštní trojice začne připravovat rafinovanou a nebezpečnou akci. Na jejím konci na ně čeká pořádný balík peněz...(oficiální text distributora)', NULL, 2000, '');
