INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (786, 'Blade Runner Final Cut Documentary.m4v', 'Blade Runner Final Cut Documentary', 4, 23, 'en', '  ', 'en', '', '', 'Blade Runner Final Cut Documentary.jpg', 2, 'Finální tříapůlhodinový dokument o tvorbě jedné z legendárních sci-fi klasik, snímku Ridleyho Scotta, BLADE RUNNER. Dokument je tvořen z 80 různých rozhovorů s lidmi, kteří na filmu pracovali nebo filmaři, které ovlivnil, dosud nikdy nezveřejněných záběrů a jiných ztracených materiálů. Bible pro všechny fanoušky.(3DD!3)', NULL, NULL, NULL);
