INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (830, 'Neodolatelny - Irresistible.m4v', 'Neodolatelny', 15, NULL, 'cz', '  ', 'en', 'Irresistible', 'http://www.csfd.cz/film/226045-neodolatelny/', 'Neodolatelny - Irresistible.jpg', 90, 'Sophie Hartley (Susan Sarandon) je přesvědčena, že ji někdo sleduje. Největší podezření má na manželovu (Sam Neill) spolupracovnici, krásnou Maru (Emily Blunt). Čím déle to trvá, tím více si myslí, že Mara chce její děti, jejího mažela a to nejvzácnější, její život. Ale Sophii nikdo nevěří. Jsou její stavy paranoické? Nebo je Sophie v síti, ze které není úniku. Sophie se musí sama rozhodnout. Jen ona může odhalit pravdu, ta je však strašnější než její nejhorší sen.(oficiální text distributora)', NULL, 2006, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (787, 'Blade Runner Final Cut.m4v', 'Blade Runner Final Cut', 4, 23, 'en', '  ', 'en', NULL, 'http://www.csfd.cz/film/8268-blade-runner/', 'Blade Runner Final Cut.jpg', 1, 'Počátkem 21. století dosáhla společnost Tyrell ve výzkumu robotů fáze Nexus. Její výrobky byly téměř identické s lidmi, nelišily se ani inteligencí. Lidé používali replikanty při nebezpečných výpravách nebo jako otroky při kolonizaci jiných planet. Po krvavé vzpouře replikantů v jedné z mimozemských kolonií byl robotům pod trestem smrti zakázán pobyt na Zemi. Na plnění tohoto nařízení dohlížely speciální jednotky Blade Runner, pověřené vyhledáváním a likvidací replikantů, kteří zákaz poruší. Při svém prvním uvedení před deseti lety ohromil tento snímek diváky svým pohledem na budoucnost a brzy se stal obdivovaným klasickým dílem v žánru sci-fi. Inteligentní a provokativní scénář, volně vycházející z knihy Philipa K. Dicka "Sní androidi o elektrických ovcích?", výtvarná stránka a zvláštní efekty vytvořily svým autorům odrazový můstek k následující hvězdné kariéře. Rovněž pro některé hlavní představitele - zejména Rutgera Hauera - byl tento film významným mezníkem - rolí Roye Battyho se zařadil do kategorie mezinárodních hvězd. Režisér Ridley Scott nebyl spokojen se zásahy studia do konečné podoby filmu, ale jeho snaha o prosazení původní verze přinesla ovoce až po letech.(oficiální text distributora)', NULL, 1982, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (788, 'Cinska odysea - Chinese Odyssey.m4v', 'Cinska odysea', 9, NULL, 'cz', '  ', 'en', 'Chinese Odyssey', 'http://www.csfd.cz/film/37859-cinska-odysea/', 'Cinska odysea - Chinese Odyssey.jpg', 90, 'Honkongský nejlepší film roku 2002. Filmová odysea plná smíchu, lásky, přátelství a akčních scén, které navazují na triky filmu Tygr a Drak.', NULL, 2002, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (789, 'Cokoliv.m4v', 'Cokoliv', 6, NULL, 'cz', '  ', 'en', 'Anything Else', 'http://www.csfd.cz/film/42844-cokoliv/', 'Cokoliv.jpg', NULL, 'Ďalšia romantická komédia z pera osvedčeného Woodyho Allena plná vtipných náhod, nehôd a nedorozumení. Jerry (Biggs) je mladý 21-ročný komediálny spisovateľ žijúci v New Yorku. Aj napriek tomu, že je talentovaný, má problémy preraziť. Zásluhu na tom nesie jeho agent Harvey (DeVito), ktorý má sice o sebe vysokú mienku ale je dosť nepraktický a tiež David (Allen), jeho excentrický a paranoidný učiteľ. Ďalším kameňom úrazu je Amanda (Ricci), jeho priateľka. Od prvého momentu, kedy ju zbadal, vedel, že je do nej zamilovaný. Ale Amandine mnohopočetné neurózy, strach zo záväzkov a frustrácie najrôznejšieho druhu jej zabraňujú, aby bola Jerrymu nablízku, keď to najviac potrebuje. Krízovým bodom sa ukáže byť situácia, keď sa ocitne v malom byte s Amandou, ktorú nečakane navštívi jej matka Paula, ktorá sa rozhodla, že sa stane kabaretnou speváčkou.(oficiální text distributora)', NULL, 2003, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (790, 'Dablici.m4v', 'Dablici', 9, NULL, 'cz', '  ', 'en', 'Tiptoes', 'http://www.csfd.cz/film/45126-dablici/', 'Dablici.jpg', 80, 'Karolína chodí se Stevenem. Steven je fajn, ale když přijde řeč na jeho rodinu, začne být divný. I když Karolína čeká se Stevenem dítě, představení jeho rodičům se stále odkládá. Steven ví, co dělá. Karolína se však nedá odradit. K setkání musí dojít a všechno se začíná neuvěřitelně zamotávat.(oficiální text distributora)', NULL, 2003, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (791, 'Dedictvi aneb Kurvahosigutntag.m4v', 'Dedictvi aneb Kurvahosigutntag', 9, NULL, 'cz', '  ', 'cz', NULL, 'http://www.csfd.cz/film/1524-dedictvi-aneb-kurvahosigutntag/', 'Dedictvi aneb Kurvahosigutntag.jpg', 35, 'Bohuš je rázovitý vesnický chlapík, který žije sám v rozpadající se chalupě. Pracuje jako dřevař, většinu času však se svými kumpány tráví popíjením slivovice a zahálčivým povalováním v trávě. Jeho poklidný a vcelku spokojený život se změní v okamžiku, kdy se objeví elegantní advokát Ulrich, který překvapenému Bohušovi oznámí, že zdědil obrovský majetek.(oficiální text distributora)', NULL, 1992, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (792, 'Diky za kazde nove rano.m4v', 'Diky za kazde nove rano', 9, NULL, 'cz', '  ', 'cz', NULL, 'http://www.csfd.cz/film/8664-diky-za-kazde-nove-rano/', 'Diky za kazde nove rano.jpg', NULL, 'Je to příběh Olgy, jejích přátel, rodiny a vrstevníků, nahlížený s ironií a humorným nadhledem. Do bezstarostného Olžina dospívání zasahují stále více společenské normalizační poměry 70. let. V hořkosladké komedii se střídají bouřlivé komediální situace se slzami. Její hrdinové však už ví, že nepodlehnout, nevzdávat se, mít rád a nezapomenout se smát, je platné a dobré.(oficiální text distributora)', NULL, 1994, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (793, 'Divoka Reka.m4v', 'Divoka Reka', 15, NULL, 'en', '  ', 'en', 'The River Wild', 'http://www.csfd.cz/film/4126-divoka-reka/', 'Divoka Reka.jpg', NULL, 'Hrdinka Gail chce víkendovým výletem do hor oslavit synovy narozeniny a zachránit své nepříliš spokojené manželství. Rozhodne se s manželem a synem jet divokou řeku. Idylická pohoda však bere za své ve chvíli, kdy se rodina setkává se dvěma neznámými muži - Wadem a Terrym, kteří svými vražednými úmysly změní rodinný výlet v životu nebezpečný horor.(oficiální text distributora)', NULL, 1994, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (794, 'Drsny casy.m4v', 'Drsny casy', 15, NULL, 'en', '  ', 'en', 'Harsh Times', 'http://www.csfd.cz/film/191594-drsny-casy/', 'Drsny casy.jpg', NULL, 'Jim David (Christian Bale), ex-mariňák se po letech v akci vrací na ředitelství losangeleské policie, kde mu nabízejí práci související s infiltrací podsvětí. Bohužel se jím však nechá pohltit a musí sebrat veškeré své síly k návratu ze šikmé plochy. Drsný časy jsou výletem do špinavých losangeleských ulic, království drog, děvek a lidské havěti.(oficiální text distributora)', NULL, 2005, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (796, 'Enigma.m4v', 'Enigma', 15, NULL, 'cz', '  ', 'en', 'Enigma', 'http://www.csfd.cz/film/273-enigma/', 'Enigma.jpg', 3, 'Britští členové dešifrovacího týmu v Bletchley Parku (známého ve II. světové válce jako "Station X") stojí v březnu roku 1943 tváří v tvář nejhorší noční můře: nacistické ponorky náhle změnily kód a bitva o Atlantik dostává nové rozměry. Z Ameriky směřují na pomoc Evropě konvoje nákladních lodí, naložených municí a zásobami jídla. Jsou ve vážném ohrožení. Ve snaze zachránit situaci požádají vedoucí činitelé Bletchley Parku o pomoc Toma Jericha (Dougray Scott), brilantního mladého matematika a dešifrátora. Jericho však řeší i svou soukromou záhadu: Claire (Saffron Burrows), žena, kterou miluje, zmizela z Bletchley právě v okamžiku, kdy vládní orgány pojaly podezření, že v Parku je špión. Aby se dostal věci na kloub, požádá o pomoc Hester (Kate Winslet), nejlepší přítelkyni Claire. Společně postupují o krok v předstihu před zpravodajskou službou a zkoumají záhadný život Claire. Dospějí k závěru, který odhaluje mezinárodní a osobní zrady. Enigma, podle bestselleru Roberta Harrise, je první film společnosti Jagged Films a Jaggerova první role producenta na plný úvazek. Mick Jagger poté, co získal filmová práva, zjistil, že vzácný čtyřdiskový šifrovací přístroj, který používalo německé námořnictvo, se objevil na aukci v Sotheby (londýnská aukční síň), a koupil ho. Při produkci byl používán, spolu s třídiskovým šifrovacím přístrojem a dvěmi speciálně vytvořenými replikami.(oficiální text distributora)', NULL, 2001, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (797, 'Farinelli.m4v', 'Farinelli', 6, NULL, 'cz', '  ', 'it', 'Farinelli', 'http://www.csfd.cz/film/35280-farinelli/', 'Farinelli.jpg', 30, 'Snímek režiséra Gérarda Corbiaua Farinelli je nevšední biografií jedné z významných osobností evropské operní scény 18. století. Carlo Broschi, zvaný Farinelli (1705-1782), patřil mezi nejobdivovanější zpěváky své doby; sopranista, vrcholný představitel slohu bel canto, díky svému zmrzačení (byl kastrát) zvládl tři a půl oktávy, a dokázal udržet tón celou minutu. Fascinující a nejednoznačná postava je pro autory průvodcem dobou baroka s jeho pompézností a smyslem pro efekty; hrdina si však zároveň zachovává tajemství, jež tkví v neuchopitelném umění dojímat krásou zpěvu. Právě Farinelliho "božské" umění je ústředním prvkem vyprávění, jež zobecňuje osud skutečné osobnosti v historické době na pozadí vztahů mezi protagonisty a jejich zvláštních "rolí". Souputníkem kastráta, jemuž je dáno okouzlovat a milovat ženy, avšak bez očekávaného naplnění, je jeho starší bratr Riccardo. Muž, který zpěváka v dětství zmrzačil, aby uchoval jeho hlas, je pouze průměrným skladatelem; o to zoufaleji se drží geniálního sourozence, jemuž dělá manažera, společníka i skladatele. Utajovaná žárlivost spolu s neschopností dostát dávnému slibu, že pro zpěváka složí vysněnou operu o Orfeovi, se nakonec postaví mezi bratry, kteří byli nerozluční nejen v práci, ale i v soukromí (Carlo se s Riccardem dělí o ženy, jež dobyl). Dalším aktérem dramatu je Georg Friedrich Händel, léta toužící, aby kastrát zpíval jeho skladby. To je i Farinelliho stále trýznivějším tajným snem (zpěvák dokonce ukradne skladateli jeden jeho opus); Carlo však zůstává věrný bratrovi a londýnskému souboru italského mistra Porpory. Ničivé spory nakonec ochromují tvůrčí schopnosti všech aktérů, svazovaných závistí, strachem ze ztráty tvůrčí potence a touhou po dokonalosti. Pouta lásky a důvěry se však nakonec ukazují být stejně důležitá, jako věčné, a přece tak pomíjivé umění tónů. Corbiau své téma pojednává v přitažlivých, vizuálně dynamických obrazech, v nichž dominuje na jevišti zpívající, androgynní a magický "král tónů" (zpěv tenoristy Dereka Leeho Ragina a sopranistky Ewy Mallas Godlewské byl pro potřeby filmu digitálně upravován). Zřejmá je paralela mezi dobovými zpěváky a moderními hvězdami pop music, které jsou na jedné straně hýčkané a obdivované davem, na straně druhé však trpí osamělostí a pocity nejistoty. Farinelli navíc strádá svou neplodností, jež je v příkrém kontrastu s uvolněnou, požitkářskou atmosférou doby i s jeho tvůrčí potencí. Atraktivitu myšlenkově i vizuálně pozoruhodného snímku umocňují herecké výkony hlavních představitelů, Stefana Dionisiho (Farinelli) a Enrica Lo Versa (Riccardo).(oficiální text distributora)', NULL, 1994, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (799, 'Hitlerova elita - Napola.m4v', 'Hitlerova elita', 15, NULL, 'cz', '  ', 'de', 'Napola - Elite für den Führer', 'http://www.csfd.cz/film/163211-napola-hitlerova-elita/', 'Hitlerova elita - Napola.jpg', 2, 'Berlín, 1942. Friedrichu Weimerovi je sedmnáct a právě ukončil školu. Pochází z chudých poměrů, přesto však sní o tom, že v životě něco dokáže. Jediné, čemu se intenzivně věnuje, je amatérský box. Šanci dostává v okamžiku, kdy se setkává s chápavým Voglerem, který sdílí jeho boxerskou vášeň a učí na elitní nacistické střední škole Nationalpolitische Erziehungsanstalt (Napola). Ten zajistí, aby tam byl Friedrich přijat, a stává se jeho mentorem. Friedrichovým novým přítelem se stává syn vysoce postaveného nacistického pohlavára Albrecht Stein - citlivý mladík, který před ním neskrývá své pochybnosti o ideologii, kterou si ostatní studenti nechávají bezmyšlenkovitě vtloukat do hlavy. V každodenní konfrontaci s tvrdým a námitky nepřipouštějícím drilem se Albrecht stále zřetelněji dostává do konfliktů. Friedrich pochopí, že kamarád si svůj osud zvolil i za cenu sebezničení. Rozhodne se, že využije posledního zbytku svobody, který mu osud nabízí, a pokusí se žít čestný život.(oficiální text distributora)', NULL, 2004, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (800, 'Hrdina - Hero.m4v', 'Hrdina', 15, NULL, 'en', '  ', 'en', 'Hero', 'http://www.csfd.cz/film/38117-hrdina/', 'Hrdina - Hero.jpg', 3, 'Rozpočet historického válečného velkofilmu HERO několikrát překročil prostředky vložené do oscarového filmu TYGR A DRAK. Režisérem filmu je dvojnásobný laureát Zlatého Medvěda a ceny BAFTA , třikrát nominovaného na Zlatou Palmu Zhang Yimou. Hereckými hvězdami jsou Ziyi Zhang (která zazářila v již zmiňovaném filmu Tygr a drak) a Jet Li (Romeo musí zemřít). Děj se odehrává před 2000 lety za vlády dynastie Qin a vypráví příběh hrdinného válečníka a ochránce krále, před kterým stojí obrovský úkol - sjednotit Čínu a krále tak učinit Imperátorem. Producenti při natáčení spolupracovali s čínskou armádou a tak mohly vzniknout jedny z nejefektnějších scén dějin historického válečného filmu. Tvůrci zvedli laťku pořádně vysoko.(oficiální text distributora)', NULL, 2002, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (801, 'Hrisny tanec - Dirty Dancing.m4v', 'Hrisny tanec', 6, NULL, 'cz', '  ', 'en', 'Dirty Dancing', 'http://www.csfd.cz/film/16366-hrisny-tanec/', 'Hrisny tanec - Dirty Dancing.jpg', 50, 'Píše sa rok 1963. Beatles v USA ešte nie sú známi a diskotékam kraľujú latinsko-americké tance. Do letoviska horského hotela prichádza na prázdninový pobyt 17-ročná Frances, prezývaná Baby, so sestrou Lisou a rodičmi. V hoteli sa tancuje úplne iný tanec než na aký je Baby ako dievča zo "slušnej rodiny" zvyknutá - tzv. dirty dancing (hriešny tanec). Baby sa zoznámi s tanečným inštruktorom Johnnym a prežije nielen svoju prvú lásku, ale aj premenu zo škaredého kačiatka na sebavedomú mladú ženu.(oficiální text distributora)', NULL, 1987, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (802, 'Imaginarium Dr. Parnasse -- Dr.m4v', 'Imaginarium Dr. Parnasse', 15, NULL, 'en', '  ', 'en', 'The Imaginarium of Doctor Parnassus', 'http://www.csfd.cz/film/234303-imaginarium-dr-parnasse/', 'Imaginarium Dr. Parnasse -- Dr.jpg', NULL, 'Dr. Parnassus (Christopher Plummer) kdysi dávno vyhrál nesmrtelnost v sázce s ďáblem panem Nickem. O mnoho století později našel pravou lásku a s ďáblem podepsal novou smlouvu. V ní stálo, že se vzdává své nesmrtelnosti výměnou za mládí, ale jeho prvorozené dítě se v den svých šestnáctých narozenin stane majetkem pana Nicka. Nyní se čas Parnassovy dcery Valentiny naplnil a on se trápí, jak jí zachránit před neúprosným osudem. Pan Nick přijíždí, aby si převzal svou cenu. Rád se však vsází, a proto se rozhodne znovu riskovat. Valentinu získá ten, který první svede pět nových duší...(oficiální text distributora)', NULL, 2009, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (803, 'Jak basnici neztraceji nadeji.m4v', 'Jak basnici neztraceji nadeji', 9, 22, 'cz', '  ', 'cz', NULL, 'http://www.csfd.cz/film/154053-jak-basnici-neztraceji-nadeji/', 'Jak basnici neztraceji nadeji.jpg', 5, 'Na začátku příběhu se dozvídáme, že se Štěpán Šafránek musel rozloučit se svými svěřenci a krásnými jeptiškami z Ústavu sociální péče, který se musel v důsledku restitucí odstěhovat z Hradiště. Štěpán nastupuje jako lékař do rychlé záchranné služby, kde v dispečinku kraluje jeho bývalá sestřička výřečná Tonička. Vzápětí mu umírá maminka, která už byla dlouho nemocná. A aby těch katastrof nebylo dost, Štěpána postihne živelná pohroma dočasně se k němu nastěhuje jeho kamarád Kendy, kterému se rozpadlo manželství a jeho bývalá manželka Sylvie jej vykázala z bytu. Ani vztah Štěpána se Superpopelkou Ute, jinak majitelkou lékárny U Černé Madony a dalších čtyř lékáren, není nijak dokonalý. Nevzali se a léty jejich vztah zevšedněl a zešedivěl U Černé Madony však nastoupila nová lékárnice Anička, která je nejen hezká, ale má pocit, že musí Štěpánkovi pomoci a tak začíná nový vztah Kendy se všemožně snaží prosadit a udržet v reklamní branži, a proto opět vtahuje Štěpána do své práce. Jejich kamarád Karabec, úspěšný podnikatel a majitel autosalonu, přemluví Štěpána, aby se přihlásil na konkurs ředitele do místní nemocnice, která je před krachem. Štěpán nejdříve odmítá, ale pak ho to začne zajímat a konkurs vyhraje. Jeho iluze a poctivé úsilí zachránit nemocnici však naráží na zkorumpovanou síť nejrůznějších podvodníků, od ministerských úředníků, politiků až po takzvané podnikatelé, kterým jde jenom o jedno nahrabat si co nejvíce peněz bez ohledu na pacienty, lékaře a lidskou bolest. Jsou mezi nimi i Vendulka-utěšitelka, která na ministerstvu přežila všechny režimy, neschopný, ale všeho schopný Písařík, který se dal na politickou kariéru a další. Otráven politikařením a intrikami Štěpán rezignuje na tuto funkci a vrací se k práci, která má smysl a dává naději k záchrance Mezitím dochází k definitivnímu rozchodu s Ute, ale najednou i Anička, vinou nedorozumění, zmizí ze Štěpánova života Štěpán se rozhodne ji stůj co stůj najít kde však básníci potkávají naději?..(oficiální text distributora)', NULL, 2003, '');
