INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (804, 'Jak basnici prichazeji o iluze.m4v', 'Jak basnici prichazeji o iluze', 9, 22, 'cz', '  ', 'cz', NULL, 'http://www.csfd.cz/film/5247-jak-basnici-prichazeji-o-iluze/', 'Jak basnici prichazeji o iluze.jpg', 2, 'Ve druhé části básnické tetralogie se opět sejdeme se Štěpánem Šafránkem, tentokrát již jako studentem medicíny a jeho přítelem Kendym, studujícím na FAMU. Seznámíme se s novými Štěpánovými spolužáky, Honzou Antošem z Kolína, Moravákem Venošem a černochem Numirou Cassou Thombem, alias Mirečkem. Štěpán usilovně, leč ne vždy úspěšně, bojuje jak s náročným studiem, tak s odmítavou kráskou Jeskyňkou. Zatímco studijní výsledky se po úmorně dřině dostaví, s láskou je to horší. Štěpán ztrácí iluze, ale už ví, že na každém konci je pěkné to, že něco jiného začíná Nedílnou součástí filmu jsou animované dotáčky, jejichž autorem je Adolf Born.(oficiální text distributora)', NULL, 1984, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (805, 'Jak basnikum chutna zivot.m4v', 'Jak basnikum chutna zivot', 9, 22, 'cz', '  ', 'cz', NULL, 'http://www.csfd.cz/film/5248-jak-basnikum-chutna-zivot/', 'Jak basnikum chutna zivot.jpg', 3, 'Zatímco Kendy se jako pomocný režisér vrací do rodného města natáčet s televizním štábem třináctidílný seriál, Štěpán Šafránek, vybaven po promoci maminčinými radami, nastupuje na své první místo - na internu zdejší nemocnice. Jeho nepřizpůsobivost místním poměrům a smysl pro etiku zvoleného povolání však příliš nevyhovuje ostatním už zaběhnutým kolegům. Zatímco ti se škodolibě baví jeho horlivostí a zodpovědností, Štěpán má stále větší problémy s nadřízenými, až je nakonec přesunut na všemi odmítané místo venkovského obvodního lékaře. V Bezdíkově však potkává novou, snad už trvalou a opětovanou lásku, s níž se posléze rozhodne žít a pracovat v severních Čechách.(oficiální text distributora)', NULL, 1987, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (806, 'Jak svet prichazi o basniky.m4v', 'Jak svet prichazi o basniky', 9, 22, 'cz', '  ', 'cz', NULL, 'http://www.csfd.cz/film/5249-jak-svet-prichazi-o-basniky/', 'Jak svet prichazi o basniky.jpg', 1, 'Gymnazisté Štěpán a Kendy mají umělecké ambice, jeden skládá verše a druhý je zhudebňuje. Společně se rozhodnou zorganizovat ochotnické představení, do jehož příprav neváhají zapojit čelné osobnosti města.', NULL, 1982, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (807, 'Jako Jed.m4v', 'Jako Jed', 9, NULL, 'cz', '  ', 'cz', NULL, 'http://www.csfd.cz/film/7044-jako-jed/', 'Jako Jed.jpg', NULL, 'Inženýr Pavel Hnyk je v práci úspěšný, v soukromí však příliš spokojený není. Blíží se mu totiž padesátka a on se nemůže zbavit pocitu, že mu život jaksi proklouzává pod rukama. Nejúčinnější medicínou na takové strasti bývají milostné avantýry, a Pavel si jich také bohatě dopřává. Jeho manželka Alice je povoláním advokátka, ví tedy o takových případech svoje, a protože je to moudrá žena, mlčky je přechází. Jednoho dne se ale na Pavlově pracovišti objeví nová kolegyně - půvabná Slovenka Julie. Přátelský vztah se brzy změní na milostný poměr, a ten postupem času přerůstá v bezhlavou, ničivou vášeň, která nedbá na nic a na nikoho...(oficiální text distributora)', NULL, 1985, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (808, 'Kalamita.m4v', 'Kalamita', 9, 0, 'cz', '  ', 'cz', 'Kalamita', 'http://www.csfd.cz/film/1528-kalamita/', 'Kalamita.jpg', NULL, 'Pestrý, epizodicky bohatý dej filmu je organizovaný okolo candidovského príbehu mladého muža, ktorý sa snaží v živote plnom úplatkárstva a promiskuity, preraziť podľa svojej cti a svedomia. Opúšťa vysokoškolské štúdium, aby nadviazal na rodinnú tradíciu ako strojvodca horskej lokálky. Zmysel pojmu "kalamita" rezonuje takmer vo všetkých jeho ľudských kontaktoch: jeho predstavy o poctivosti, nezištnosti a úprimnosti neustále vstupujú do konfliktu s realitou. Záverečná skutočná kalamita, keď jeho vláčik zavalí horská lavína, poskytne satisfakciu jeho viere v krásu života: ľudia uväznení pod snehom precitnú zo svojej mravnej letargie a pri záchrannej akcii naplnia činom pojem solidarita (vtedy prestane v kalamitnej krajine snežiť a utíchne vietor). NNFilm pulzuje ironickým humorom, zvedavo živou kamerou, ostrým strihom a znamenitou zvukovou rovinou. Rad estrádnych výstupov, gagov, mrazivých satirických čísel, smiešnomilostných dialógov, slovných klauniád a izolovaných mikrosekvencií neslovných etúd (v ktorých Decziho útočná hudba podkladá triešť mihotavých záberov), vytvára výrečne znervózňujúci pohyb Šlapetovej kamery, Brožkove útočné strihy a nekašírované dialógy s bohato odtieňovanou intonáciou reči. Svet Kalamity nemá horkokrvnosť toho v Panelstory - tu sú už skoro všetci "zabývaní vo svojom kupé", len Polívkov usmievavý hľadač cestuje v hlbokom zmysle tohto slova.', NULL, 1981, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (809, 'Kolja.m4v', 'Kolja', 9, NULL, 'cz', '  ', 'cz', NULL, 'http://www.csfd.cz/film/8805-kolja/', 'Kolja.jpg', 25, 'Laskavá komedie o muži, který se domníval, že už nemůže být hůře a netušil, že právě prožívá nejkrásnější chvíle svého života. Františkovi už prošedivěly vousy. Kolja ještě nedosáhl školního věku a setkali se díky tragikomické životní situaci. Ti dva zůstali nakonec sami.Starý mládenec a kluk, kteří si navzájem nerozumějí a to ve všech směrech. Čeká je dlouhá a složitá cesta za úsměvem a láskou...(oficiální text distributora)', NULL, 1996, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (810, 'Konec basniku v Cechach.m4v', 'Konec basniku v Cechach', 9, 22, 'cz', '  ', 'en', NULL, 'http://www.csfd.cz/film/5253-konec-basniku-v-cechach/', 'Konec basniku v Cechach.jpg', 4, 'Lékař Štěpán je pro svou prostořekost vyhozen z nemocnice a stává se nezaměstnaným. Vrací se do rodného městečka a zatímco se jeho dřívější spolužáci a kamarádi pohybují v nové době jako rybky v čerstvé vodě, Štěpán, věren svým životním zásadám, se s dravým dickensovským kapitalismem nemíní ztotožňovat. Svědomí mu brání ve výnosném, ale zcela nemravném obchodu s lidským zdravím. Konečně však potkává dívku, která se patrně stane smyslem jeho života...(oficiální text distributora)', NULL, 1993, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (811, 'Kopytem sem, kopytem tam.m4v', 'Kopytem sem, kopytem tam', 15, NULL, 'cz', '  ', 'cz', NULL, 'http://www.csfd.cz/film/1530-kopytem-sem-kopytem-tam/', 'Kopytem sem, kopytem tam.jpg', 50, 'Choroba AIDS sa stala východiskom pre skúmanie povrchných vzťahov medzi ľuďmi a symbolom smrteľného ohrozenia, stresovej situácie, ktorá odhaľuje podstatu jednotlivých charakterov. Traja sympatickí tridsiatnici veselo užívajú život, namiesto lásky striedajú sexuálne partnerky a cenu života im pripomenie až ohrozenie AIDS. Film o apatii, nihilizme, nivelizácii hodnôt, epikurejskom egocentrizme so samovražednými dôsledkami. Euforické eskapády ako zúfalá snaha premôcť pocit odcudzenia v spoločnosti. Významným prínosom sa stala účasť amatérskeho divadla Sklep, ktorá dodáva filmu originálnu poetiku, hravú recesiu a osobité hrecké prejavy T. Hanáka, M. Šteindlera a D. Vávru. Námetom a scenáristickou spoluprácou sa uviedol mladý absolvent FAMU P. Škapík. V chytilovsky hektickom tempe rozprávania upúta rozháraná kompozícia obrazu (farebne sýtej tónine dominuje červená), nervný strih, dekomponovaná hudba, pulzovanie a ostré jazdy p"sobivej kamery, snímajúcej v najnemožnejších uhloch a sklonoch. Dômyselný humor a efektné pózerstvo sú predvádzané natoľko okázalo, až je vlastná amorálna podstata skutkov prehlušovaná a ostrosť pohľadu na zdeformovanú realitu sa rozptyľuje.(oficiální text distributora)', NULL, 1988, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (812, 'Kral Kalifornie.m4v', 'Kral Kalifornie', 9, NULL, 'cz', '  ', 'en', 'King of Caliornia', 'http://www.csfd.cz/film/227185-kral-kalifornie/', 'Kral Kalifornie.jpg', 70, 'Vo veku 16 rokov je Miranda (Evan Rachel Wood) sklamaná rozchodom svojich rodičov. Keď ju opustila matka, odišla zo školy. Počas pobytu otca v zariadení pre duševe chorých sa zamestnala u McDonald''s. Charlie (Michael Douglas) sa vrátil domov posadnutý predstavou o zakopanom poklade, ktorý mal zakopať španielsky bádateľ Otec Juan Florismarte Garces niekde v Kalifornii na predmestí obytného celku. Čo neurobí dcéra pre svojho (aj keď duševne chorého otca)? Vyzbrojí sa detektorom kovov, kopou príručiek pre hľadačov pokladov a vyberú sa ho hľadať.(jojoping)', NULL, 2007, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (813, 'Kuky se vraci.m4v', 'Kuky se vraci', 3, NULL, 'cz', '  ', 'en', NULL, 'http://www.csfd.cz/film/244402-kuky-se-vraci/', 'Kuky se vraci.jpg', 90, 'Kuky se vrací, film scénáristy a režiséra Jana Svěráka, je dobrodružný i poetický rodinný příběh, v němž ovšem hlavní role přebírají hrdinové ze světa dětské představivosti. Šestiletý Ondra trpí astmatem, a tak ze “zdravotních důvodů“ musí pryč i jeho oblíbená hračka - růžový medvídek Kuky. Když maminka vyhodí Kukyho do popelnice, začne pracovat Ondrova fantazie, v níž prožívá medvídkovy příběhy v neznámém přírodním světě. Je to ale opravdu jen sen malého kluka a nebo se Kuky skutečně vydal za největším dobrodružstvím svého plyšového života? Kuky se vrací představuje v kontextu současné domácí kinematografie skutečně jedinečný snímek, a to nejen pro své zaměření na rodinné publikum spojující dětského i dospělého diváka, ale i pro netradiční a moderní kombinaci hraného a animovaného příběhu, plného akční scén a vtipných dialogů.(oficiální text distributora)', NULL, 2010, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (814, 'Laurel Canyon.m4v', 'Laurel Canyon', 15, NULL, 'cz', '  ', 'en', 'Laurel Canyon', 'http://www.csfd.cz/film/17624-laurel-canyon/', 'Laurel Canyon.jpg', 60, 'Zatímco v ALMOST FAMOUS hraje Francis McDormand ustaranou matku prosazující heslo neber drogy, ve filmu LAUREL CANYON si zahrála drsnou rockerku, pro kterou jsou drogy a volná láska běžnou součástí života. LAUREL CANYON je známé údolí v Hollywoodu, ve kterém mají své luxusní rezidence producenti a hudební hvězdy. V jednom takovém honosném sídle mají nahrávací studio a zároveň bydlí Jane se svým milencem, rockovým guru Ianem. Jejich bohémský život ale jednoho dne naruší Janin syn Sam, který úspěšně dokončil studia medicíny na Harwardu a nyní se vrací do LA dokončit postgraduální studium neuropsychiatrie. Není sám, přivezl si s sebou svou dívku ze studií Alex. Mladý pár ovlivněný konzervativním způsobem života na Harwardu se střetává s jinou společenskou realitou. Jane se bude muset znovu naučit, jaké to je být dobrou matkou svému synovi. V roli mladého medika Sama uvidíte Christiana Balea (Americké psycho, Království ohně), jeho přítelkyni Alex hraje krásná Kate Beckinsale (Pearl Harbor, Underworld, Van Helsing).(oficiální text distributora)', NULL, 2002, '');
