INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (815, 'Lidska skvrna - The Human Stain.m4v', 'Lidska skvrna', 15, NULL, 'cz', '  ', 'en', 'The Human Stain', 'http://www.csfd.cz/film/645-lidska-skvrna/', 'Lidska skvrna - The Human Stain.jpg', 70, 'LIDSKÁ SKVRNA je příběh váženého univerzitního profesora Colemana Silka (Anthony Hopkins), jenž si celým svým životem nesl jedno obrovské skličující tajemství. Tajemství, které se mu podařilo skrývat před svými dětmi, manželkou a přáteli celých 50.let až do doby, kdy do jeho života vstoupil spisovatel Nathan Zuckerman (Gary Sinise). Vypravěč tohoto příběhu, který se zatnul do Silkova tajemství, aby znovu oživil životopis skutečného Colemana Silka. Pravda o jeho identitě exploduje do spalujících a devastujících souvislostí. Divoký intimní cit k mladičké živelné bytosti Faunii (Nicole Kidman) už jen završil velikou tragédii jeho života.(oficiální text distributora)', NULL, 2003, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (816, 'Limonadovy Joe.m4v', 'Limonadovy Joe', 9, NULL, 'cz', '  ', 'cz', NULL, 'http://www.csfd.cz/film/5991-limonadovy-joe-aneb-konska-opera/', 'Limonadovy Joe.jpg', 60, '<strong>Jedna z nejslavnějších a nejoblíbenějších českých filmových komedií, kde správní muži pijí pouze kolalokovu lihuprostou limonádu a zákon má podobu pistolníka, vybaveného přesnou muškou, zářivým úsměvem a lahodným tenorem.</strong><br />', NULL, 1964, '');
