INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (817, 'Lucky Number Slevin (Nabit a zabit).m4v', 'Nabit a zabit', 15, NULL, 'en', '  ', 'en', 'Lucky Number Slevin', 'http://www.csfd.cz/film/189562-nabit-a-zabit/', 'Lucky Number Slevin (Nabit a zabit).jpg', NULL, 'Slevinovi se právě moc nevede. Jeho dům byl určený k demolici a on se vydává za svou přítelkyní, která má však již jiného. Aby se dostal z toho všeho, půjčí si byt svého přítele Nicka Fishera v New Yorku, zatím co on je mimo město. Avšak Nickova nepřítomnost přibude na seznam jeho problémů. New York je nebezpečné místo pro život, s podsvětím plným kriminálníků a gangsterů. Ale dva z nich jsou nejrespektovanější: Rabbi (Ben Kingsley) a Boss (Morgan Freeman). Kdysi partneři a nyní úhlavní nepřátelé. Jeden druhého obviňují z nedávných záhadných vražd bookmakerů. Mezitím se Slevin seznamuje se svou krásnou sousedkou Lindsey. Jejich flirt je však brzy přerušen, když se Bossovi lidé vrátí pro Nicka do jeho bytu. Nikdo neví jak Nick vypadá a Slevin gangstery nedokáže přesvědčit, že není ten koho hledají. Slevin se tak ocitne před Bossem a dostane onu příslovečnou nabídku, která se nedá odmítnout - Slevin zabije Rabbiho syna a jeho dluh bude splacen.(oficiální text distributora)', NULL, 2006, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (818, 'Malery pana uctovnika 1 - Fantozzi.m4v', 'Malery pana uctovnika 1 - Fantozzi', 9, NULL, 'cz', '  ', 'it', 'Fantozzi contro tutti', 'http://www.csfd.cz/film/32729-malery-pana-ucetniho/', 'Malery pana uctovnika 1 - Fantozzi.jpg', 100, 'Bláznivá italská komedie o chlapíkovi, který měl doma škaredou ženu, v práci přísného šéfa, a všechno to snášel víc než statečně.<br /><br />', NULL, 1980, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (819, 'Malery pana uctovnika 2 - Fantozzi.m4v', 'Malery pana uctovnika 2 - Fantozzi', 9, NULL, 'cz', '  ', 'en', 'Fantozzi subisce ancora', 'http://www.csfd.cz/film/32731-malery-pana-ucetniho-2/', 'Malery pana uctovnika 2 - Fantozzi.jpg', 110, 'Pracovní morálka italských úředníků je mimořádně vysoká, neboť představa nezaměstnanosti je všudypřítomná. Fantozziho dcera navíc oznámí, že čeká dítě. Našel se totiž otrlý dobrodruh, který jí v tom pomohl. Fantozzi se proto rozhodne navštívit nemocnici za účelem radikálního zákroku. Omylem na operačním stole skončí náš pan účetní. Po intervenci u prezidenta republiky je však Fantozzimu uříznutý úd zpátky přišit. Navíc čeká na Fantozziho podniková olympiáda.(oficiální text distributora)', NULL, 1983, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (820, 'Mechanik - The Machinist.m4v', 'Mechanik / The Machinist', 15, NULL, 'cz', '  ', 'es', 'El Maquinista', 'http://www.csfd.cz/film/116212-mechanik/', 'Mechanik - The Machinist.jpg', 10, 'Hrdina existenciálního hororu Trevor Reznik je osamělý, uzavřený a tichý mladý muž. Až na to, že vypadá trochu přepadle, vyhlíží jako tisíce obyčejných mladíků, kteří navečer přijdou z práce, často poklábosí se servírkou z kavárny a občas své jednoznačnější touhy uspokojují prostřednictvím profesionálky - a pak jdou spát. Trevor ovšem spát nejde - už rok totiž trpí nespavostí, která však nemá nic společného s banální insomnií, nutící vás uvařit si první kávu za rozbřesku. Fyzicky i psychicky vyčerpaný mladík, pracující v zatuchlé dílně u soustruhu, už celé měsíce bojuje s noční můrou, z níž se nelze probudit. Svou roli v ní hraje děsivá nehoda, již Trevor zavinil (nebo snad ne?), kolegové z práce, Trevorova oblíbená call girl Stevie, tajemný plešoun Ivan a sympatická číšnice Marie. Kdo z nich je však skutečný a nic nepředstírá? Je Trevor obětí nějaké podivné hry, nebo je on sám bezcitným manipulátorem?(oficiální text distributora)', NULL, 2004, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (821, 'Medved.m4v', 'Medved', 1, NULL, 'cz', '  ', 'fr', 'L''Ours', 'http://www.csfd.cz/film/168-medved/', 'Medved.jpg', 1, 'Britská Columbie, 1885. Na velkou medvědici při vybírání medu z hnízda horských včel spadne velký kámen a zabije ji. Její mládě tak musí samo začít objevovat život. Když medvídě potká postřeleného medvědího samce, uzavřou po počáteční antipatii přátelství. Jenže lovci se nechtějí vzdát a sledují stopy už dvou medvědů.(oficiální text distributora)', NULL, 1988, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (822, 'Mercury.m4v', 'Mercury', 15, NULL, 'cz', '  ', 'en', 'Mercury Rising', 'http://www.csfd.cz/film/16355-mercury/', 'Mercury.jpg', 80, 'Agent FBI Art Jeffries byl nespravedlivě převeden na jinou práci, přiníž se dostal do konfliktu s mocnou Národní bezpečností agenturou. Důvodem je devítiletý chlapec Simon geniální autista, kterého měl chránit. Dešifroval totiž přísně tajný kod Mercury zřízení pro práci amerických zpravodajských služeb a fanatický šéf projektu Nick Kudrow je odhodlán zbavit se nejen malého luštitele, ale i všech nopohodlných svědků události. Art se Simonem se proto musí skrývat a v souboji se všemocnou mašinérií riskovat vše. Jeho jedinou nadějí v souboji s časem je Simonův výjimečný matematický talent, pomoc bývalého parťáka a sympatie dívky kterou dvojice uprchlíků náhodou potkala.(oficiální text distributora)', NULL, 1998, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (828, 'Navrat pana Ripleyho.m4v', 'Navrat pana Ripleyho', 15, NULL, 'en', '  ', 'en', 'Ripley Under Ground', 'http://www.csfd.cz/film/134270-navrat-pana-ripleyho/', 'Navrat pana Ripleyho.jpg', NULL, 'Barry Pepper hraje psychopata Toma Ripleye, který padělá umělecká díla přisuzovaná mrtvému malíři, kterého se snaží udržet ještě "naživu". Když jeden milovník umění (Dafoe) objeví jeden z padělků, Ripley přijde na tiskovou konferenci převlečený za mrtvého malíře, aby prokázal, že jeho díla jsou pravá. Nepodaří se mu ho však přesvědčit, tak ho pozve domů a zavraždí. Vše nabere nepříjemně rychlý spád, když policie vedená detektivem Websterem (Wilkinson) přijde hledat zmizelého muže.(oficiální text distributora)', NULL, 2005, '');
