INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (823, 'Miliony - Millions.m4v', 'Miliony', 15, NULL, 'cz', '  ', 'en', 'Millions', 'http://www.csfd.cz/film/135068-miliony/', 'Miliony - Millions.jpg', NULL, 'Po smrti matky se odstěhuje otec (James Nesbitt) se svými syny do nového domu v Anglii. Starší 9letý syn Anthony (Lewis McGibbon) je praktický, mladší 7letý Damian (Alex Etel) věří v Boha a ve svaté, kteří se mu zjevují a mluví s ním. Jednoho dne, kdy si Damian hraje ve svém kartonovém boxu, spadne mu doslova "do náruče z čistého nebe" taška se čtvrt milionem liber. Damian to považuje za dar od Boha a chtěl by nejraději všechny ty peníze rozdat na dobročinné účely a těm, komu se jich nedostává. Svěří se s tím ovšem i svému bratru, který jeho nadšení pro dobročinnost příliš nesdílí. Vše se ještě komplikuje skutečností, že se vše odehrává asi týden před koncem platnosti liber, protože Anglie přechází na eura a navíc původ peněz není tak poetický, jak si Damian myslel...(otula)', NULL, 2004, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (824, 'Million Dollar Baby.m4v', 'Million Dollar Baby', 15, NULL, 'cz', '  ', 'en', 'Million Dollar Baby', 'http://www.csfd.cz/film/182363-million-dollar-baby/', 'Million Dollar Baby.jpg', 30, 'Stárnoucí Frankie Dunn (Clint Eastwood) se pokouší usmířit se svou dcerou, nemá však štěstí. Frankie trénuje mladé a nadějné boxery. Nedaří se mu je ale dostat k mistrovskému zápasu. Okolo ringu Dunnovi pomáhá Eddie Scrap-Iron Dupris (Morgan Freeman), bývalý boxer, kamarád a často i zachránce. Zatímco Frankieho poslední talent zahne k jinému managerovi, v tělocvičně se objeví Maggie Fitzgerald (Hilary Swank). Odstrkovaná a naštvaná, Maggie prahne po tom, aby jí někdo konečně věřil. Dunn jí ale trénovat nechce. Maggie se však na tréninku domluví s Eddiem a postupně si svou houževnatostí získá i Frankieho. Během tréninku se pak oba naučí něco o smutné minulosti toho druhého. Když se ale Maggiin sen začne naplňovat, čeká hrdiny další tragédie. Eastwoodovy snímky charakterizuje autorova tvář. Je v nich tvrdost a smutek, ale prostupuje jimi i nezničitelná a žhavá srdečnost.(oficiální text distributora)', NULL, 2004, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (825, 'Mise 1549 - Sengoku Jietai 1549.m4v', 'Mise 1549 - Sengoku Jietai 1549', 4, NULL, 'en', '  ', 'en', NULL, 'http://www.imdb.com/title/tt0453396/', 'Mise 1549 - Sengoku Jietai 1549.jpg', 60, 'Directed by Masaaki Tezuka.  With Yôsuke Eguchi, Kyôka Suzuki, Haruka Ayase, Masatô Ibu. An accident during tests of an anti-plasma artificial magnetic shield at Japan''s Ground Self Defense Force East Fuji practice range sends the 3rd Special Experimental Company, under Colonel Matoba on a time-slip 460 years into the past, into ''the Age of civil War''. At the same time an imaginary-number anomaly thought to be caused by interference from the past begins eroding the present, opening ...', NULL, 2005, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (826, 'Mlade pusky - Young Guns.m4v', 'Mlade pusky', 15, NULL, 'cz', '  ', 'en', 'Young Guns', 'http://www.csfd.cz/film/15235-mlade-pusky/', 'Mlade pusky - Young Guns.jpg', NULL, 'Na farmě Johna Tanstalla našli domov a práci mladí muži, patřící ke spodině společnosti. Tanstall je navíc vychovává, učí je číst a psát a dodržovat určitá pravidla. Ten, kdo pravidla nedodrží, musí zpátky na ulici. Na farmu přichází nový "přírůstek", sebevědomý mladík zvaný Billy Kid. Na rozdíl od ostatních umí číst, chová se ale poněkud výstředně. Šerif oznamuje Tanstallovi, že ostatní farmáři, Dullen a Murphy, obviňují jeho hochy z přepadávání jejich vozů se zbožím, ke kterým v poslední době dochází, ten však své hochy srdnatě brání. I když mu pak Murphy vyhrožuje osobně, je přesvědčen, že jako poctivý člověk má stejná práva jako ostatní, jimž je trnem v oku. Rozhodne se sejít s guvernérem a odhalit pravé viníky, jenže Murphy je rychlejší. Přepadne Tanstallovu farmu a v přestřelce je Tanstall usmrcen. Jeho muži se rozhodnou Tanstalla pomstít, a tím začíná malá válka mezi Murphyho přívrženci a Tanstallovými hochy...(oficiální text distributora)', NULL, 1988, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (827, 'Na samote u lesa.m4v', 'Na samote u lesa', 9, NULL, 'cz', '  ', 'cz', NULL, 'http://www.csfd.cz/film/6663-na-samote-u-lesa/', 'Na samote u lesa.jpg', 10, 'Hrdiny jsou manželé Oldřich a Věra Lavičkovi, kteří se svými dětmi Petříkem a Zuzankou odjíždějí do Loukova na chalupu svého známého, inženýra Radima Zvona. Chtějí také koupit chalupu a doufají, že jim Zvon pomůže. Nakonec si vyhlédnou malebnou chalupu dědy Komárka, který souhlasí, že ji prodá a zatím pronajme Lavičkovým jednu místnost. Lavičkovi se rozhodnou strávit na chalupě dovolenou. Zatímco Olda považuje pobyt na venkově za nejen zdraví prospěšný a děti se spřátelí s dobrosrdečným dědou Komárkem, Věra je stále rozčilenější. Vadí jí spousta věcí a tak stále důrazněji naléhá na manžela, aby s dědou, který jí zcela zjevně překáží, promluvil o prodeji. Olda na víkend pozve na chalupu svého kamaráda, lékaře Houdka, který zjistí, že děda je zcela zdráv a když se pak ještě navíc seznámí s dědovým stařičkým otcem, pochopí, že dělat si naděje, že tu pan Komárek už dlouho nebude jsou liché. V zimě však děda přesto onemocní a přes veškeré odmlouvání jej Houdek pošle do nemocnice. Když se však celá rodina o něco později vypraví do vsi s notářem, aby projednali prodej, najdou dědu opět doma, zdravého a čilého...(oficiální text distributora)', NULL, 1976, '');
