INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (829, 'Nekonecny Pribeh SK.m4v', 'Nekonecny Pribeh SK', 3, NULL, 'sk', '  ', 'en', NULL, 'http://www.csfd.cz/film/7244-nekonecny-pribeh/', 'Nekonecny Pribeh SK.jpg', 30, 'Pohádkovou fantazii pro malé i velké diváky natočil režisér Wolfgang Petersen v roce 1984, tehdy se značnými finančními náklady kolem 27 milionů dolarů. Film vznikal v mnichovských ateliérech a podílelo se na něm spoustu trikových specialistů. Jde o příběh malého chlapce Bastiena, kterému se v antikvariátě dostane do rukou kouzelná knížka. Chlapec se tam schová před svými pronásledovateli a stařičký majitel si brzy všimne chlapcova zájmu o knihu. Při jejím čtení se Bastien stane součástí Nekonečného příběhu. Příběh apeluje na potřebu porozumění, lidských citů a imaginace, bez nichž není možná existence říše Fantazie, jež je součástí každého z nás. Úspěšný film se dočkal dalších dvou pokračování, jejichž hrdinou je opět dorůstající Bastien.(oficiální text distributora)', NULL, 1984, '');
