INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (831, 'Noznicovoruky Edward -- Edward Scissorhands.m4v', 'Noznicovoruky Edward', 15, NULL, 'cz', '  ', 'en', 'Edward Scissorhands', 'http://www.csfd.cz/film/1073-strihoruky-edward/', 'Noznicovoruky Edward -- Edward Scissorhands.jpg', 20, 'Království za ruce by dal hrdina amerického filmu, který sice není tak úplně člověk, přestože se chová lidštěji než většina lidí v jeho okolí. Edward je výtvorem starého vynálezce, který však zemřel dříve než jej stačil dokončit a nahradit důmyslnou sestavu ostrých břitů lidskýma rukama. Edward zůstal úplně sám v chátrajícím hradě a kdovíjak dlouho trvalo, než jej zcela náhodou objevila podnikavá Peggy a vzala s sebou do města. Přestože Edward vypadá jako nějaké hrozivé monstrum, ve skutečnosti je hodný, milý a velmi plachý. Brzy si získá uznání a obdiv svým střihačským uměním, jenže jak už to chodí, jsou i lidé závistiví a zlí, jímž chlapcova obliba nedá spát, obzvláště když si všimnou náklonnosti, kterou chová k hezké dívce Kim.(oficiální text distributora)', NULL, 1990, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (832, 'Obycejni hrdinove.m4v', 'Obycejni hrdinove', 15, NULL, 'cz', '  ', 'en', 'Imaginary Heroes', 'http://www.csfd.cz/film/135910-obycejni-hrdinove/', 'Obycejni hrdinove.jpg', 100, 'Komediální drama debutujícího režiséra Dana Harrise zobrazuje jeden rok ze života typické rodiny z americké vyšší střední třídy. Scénář filmu Imaginární hrdinové zaujal režiséra Bryana Singera natolik, že Harrisovi nabídl napsat scénář k pokračování úspěšné blockbusterové série X-Men. Snímek Imaginární hrdinové podobně jako filmy Ledová bouře (r. Ang Lee) nebo Obyčejní lidé (r. Robert Redford) zobrazuje prostředí vyšší střední třídy s mnoha jeho pokryteckými nároky, přetvářkou a pozlátkem, jehož obětí se stávají především děti. (FebioFest 2006)(oficiální text distributora)', NULL, 2004, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (833, 'Okrsek 13 - Banlieue 13.m4v', 'Okrsek 13 - Banlieue 13', 4, NULL, 'en', '  ', 'en', NULL, 'http://www.imdb.com/title/tt0414852/', 'Okrsek 13 - Banlieue 13.jpg', 70, 'Directed by Pierre Morel. With Cyril Raffaelli, David Belle, Tony D&#x27;Amario, Bibi Naceri. Set in the ghettos of Paris in 2010, an undercover cop and ex-thug try to infiltrate a gang in order to defuse a neutron bomb.', NULL, 2004, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (834, 'Piano.m4v', 'Piano', 15, NULL, 'cz', '  ', 'en', 'Piano', 'http://www.csfd.cz/film/13942-piano/', 'Piano.jpg', 45, 'Vášeň je síla, vůči které jsme bezmocní. Nostalgická romance o lásce, která vzplála v drsném prostředí novozélandské pustiny. Do nehostinného prostředí bělošských osadníků přijíždí z Anglie za novým mužem němá Ada s nemanželskou dcerkou Florou. Přiváží s sebou milovaný klavír, bez nějž nemůže existovat. Zvláštní, uzavřená a oduševnělá žena se s obchodnickým manželem Stewartem neshodne. Postupně však podléhá jemnému nátlaku jiného muže, který odkoupil od Stewarta její nástroj. Posedle zamilovaný Baines jí postupně piano odprodává, a ona za to platí narůstající povolností. Ta je však stále méně vynucována; později má naplněný milostný vztah za následek prudkou reakci oklamaného manžela. Příroda je vůči bílým osadníkům krajně nepřátelská: neustále prší a všude je spousta bahna. Moře je stále bouřlivé, husté stromy a křoviska jsou jen obtížně prostupná. Vztahy mezi jednotlivými postavami jsou nejednoznačné a jejich charaktery jsou obtížně dešifrovatelné.(oficiální text distributora)', NULL, 1993, '');
