INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (835, 'Podozrenie - Under Suspicion.m4v', 'Podozrenie', 15, NULL, 'cz', '  ', 'en', 'Under Suspicion', 'http://www.csfd.cz/film/12725-podezreni/', 'Podozrenie - Under Suspicion.jpg', 5, 'Ulice tropického přístavu San Juan, na ostrově Portoriko, jsou zaplněné k prasknutí. Koná se tu tradiční festival. Ti, co se veselí ani netuší, že ve městě došlo k dvěma závažným zločinům. V obou případech se stala obětí nezletilá dívka, kterou pachatel před smrtí znásilnil. Bohatý právník Henry Hearst se chystá se svojí o mnoho let mladší ženou Chantal na dobročinný večírek, když se ozve telefon, kterým ho policejní kapitán Victor Benezet vyzve, aby se krátce zastavil na policejní stanici k doplnění výpovědi. Hearst totiž objevil a ohlásil druhou vraždu. Banální vyšetřování se postupně mění v celonoční křížový výslech, při němž je konfrontována i Chantal. Je tu řada indicií, nasvědčujících, že ctihodný a vážený zastánce práva bude postaven mimo zákon. Ve chvíli, kdy se Hearst pod tíhou argumentů dozná, a Chantal připustí, že si dávno všimla manželovy slabosti pro dospívající děvčátka, dostane kapitán Benezet do rukou nezvratný důkaz. Svědčí ve prospěch Henryho Hearsta...(oficiální text distributora)', NULL, 2000, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (836, 'Polibek meho andela.m4v', 'Polibek meho andela', 6, 0, 'cz', '  ', 'en', 'Kissed by an Angel', 'http://www.csfd.cz/film/35819-polibek-meho-andela/', 'Polibek meho andela.jpg', 1, 'Danny je atraktivní mladý muž co se nešťastně zamiloval. Zoufale se snaží vyrovnat s tím, že ho opustila snoubenka.', NULL, 2000, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (837, 'Povoden.m4v', 'Povoden', 15, NULL, 'en', '  ', 'en', 'Hard Rain', 'http://www.csfd.cz/film/12708-povoden/', 'Povoden.jpg', NULL, 'Na Středozápadě USA už mnoho dní nepřetržitě prší. Nejvíce je postiženo městečko Huntinburg, které už je pod vodou. Téměř všichni místní obyvatelé jsou převezeni do bezpečí, a přesto se v městečku pod vodou odehrává velké drama: Tom, který je řidičem auta pro transport peněz, se snaží zachránit tři miliony dolarů z místní banky. Na balík peněz si ale dělá zálusk skupina gangsterů v čele s Jimem. Nepřetržitý liják dramaticky ztěžuje situaci na obou stranách...(oficiální text distributora)', NULL, 1998, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (838, 'Prach a popel - Ash Wednesday.m4v', 'Prach a popel', 15, NULL, 'cz', '  ', 'en', 'Ash Wednesday', 'http://www.csfd.cz/film/43950-prach-a-popel/', 'Prach a popel - Ash Wednesday.jpg', 6, 'Čtyřicet dní před Velikonocemi - na popelečnou středu - značí kněz čela věřících popelcem, křížkem z popela, na znamení pokání a lítosti za hříšný a pomíjivý život. Právě v ty dny se odehrává na Manhattanu příběh nezkušeného mladíka Seana Sullivana, který se neuváženě zaplete s italskou mafií. Jeho starší bratr Francis se ho snaží ze všech sil z nebezpečné situace dostat.(oficiální text distributora)', NULL, 2002, '');
