INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (839, 'Pulnocni beh.m4v', 'Pulnocni beh', 15, NULL, 'en', '  ', 'en', 'Midnight Run', 'http://www.csfd.cz/film/934-pulnocni-beh/', 'Pulnocni beh.jpg', 6, 'Hlavní postavou americké krimi komedie je bývalý policista Jack Walsh, který musí do pátku do půlnoci dodat k losangeleskému soudu mafiánského účetního Jonathana Mardukase řečeného Vévoda, který svému šéfovi Jimu Serranovi ukradl patnáct milionů dolarů, věnoval je na dobročinné účely a nyní se skrývá neznámo kde. To, co zpočátku vypadalo jako jednoduchá fuška, se však velmi rychle začne komplikovat. Nejprve se objeví agent, který jménem FBI Jacka varuje, aby nestrkal nos do vládních záležitostí, potom dva Serranovi zabijáci, zanedlouho také Jackův konkurent Marvin Dorfler, který míní Jackovi přebrat "kšeft" a v neposlední řadě je tu sám Mardukas, který se zcela vymyká Jackovým zkušenostem. Jako zkušený kriminalista ho sice bez potíží najde a zatkne, ovšem zvládnout dobrosrdečného, upovídaného a hlavně všemi mastmi mazaného účetního, který vůbec nevypadá jako někdo koho urputně hledá FBI i mafie, se zdá nad lidské síly.(oficiální text distributora)', NULL, 1988, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (840, 'Rambo - Prva Krv - First Blood.m4v', 'Rambo - Prva Krv', 15, NULL, 'en', '  ', 'en', 'First Blood', 'http://www.csfd.cz/film/16097-rambo-prvni-krev/', 'Rambo - Prva Krv - First Blood.jpg', NULL, 'Voják americké armády John Rambo (Sylvester Stallone) se po letech vrací z pekla vietnamské války zpět do své vlasti. Ale politická situace se mezitím změnila, mladý muž se setkává jen s nepochopením a jednoho dne je šerifem malého podhorského městečka (Brian Dennehy) zatčen pro potulku a odveden na místní policejní stanici. Podaří se mu však utéct a v nedalekých horách svádí se svými pronásledovateli nelítostný boj o holý život.(oficiální text distributora)', NULL, 1982, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (849, 'Sofer slecny Daisy - Driving Miss Daisy.m4v', 'Sofer slecny Daisy', 6, NULL, 'cz', '  ', 'en', 'Driving Miss Daisy', 'http://www.csfd.cz/film/664-ridic-slecny-daisy/', 'Sofer slecny Daisy - Driving Miss Daisy.jpg', 20, 'Pro Daisy, bohatou a tvrdohlavou americkou židovku, která je už příliš stará na to, aby mohla řídit auto, najímá syn v padesátých letech 20. století, kdy se v ulicích i politice opět řeší rasové spory, černošského šoféra. Do rodiny přichází Hoke a musí si před Daisy, která předstírá, že ho nepotřebuje, svou pozici obhájit. Hoke nemůže pochopit její izolovanost od světa a neschopnost uchopit společenské změny. Ona zase nedokáže pochopit, proč jsou lidé jako Hoke neustále rozhořčeni... Až ve chvíli, kdy jde slečna Daisy do domova důchodců a Hoke je už moc starý na to, aby ji vozil, si oba dva uvědomí, jak moc se měli rádi a jakými dobrými přáteli byli...(oficiální text distributora)', NULL, 1989, '');
