INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (841, 'Rychle a Zbesile.m4v', 'Rychle a Zbesile', 15, 37, 'en', '  ', 'en', 'The Fast And The Furious', 'http://www.csfd.cz/film/1616-rychle-a-zbesile/', 'Rychle a Zbesile.jpg', 1, 'Brianovi se konečně podaří stát se členem party vedené Dominicem, který je doslova blázen do nelegálních závodů, pořádaných většinou v noci na periférii Los Angeles. Je to obrovské divadlo pro obecenstvo zběsilých jízd, ale především posedlost pro piloty speciálně upravených superauťáků. S ohlušujícím řevem motorů, řítící se městem několikanásobkem povolené rychlosti, pění všem adrenalin v krvi. Ve hře jsou peníze, prestiž, obdiv dívek. Jenže pro Dominica a jeho kámoše nejsou závody jediným zdrojem financí. A to je hlavní důvod, proč chce policista Brian získat jeho důvěru. V poslední době se množí záhadná přepadení kamionů a policie i FBI chtějí znát pachatele dřív než tiráci vezmou zákon do svých rukou. Ve chvíli, kdy má všechny důkazy o tom, že Dominic je vůdcem gangu, Brian zaváhá s odhalením pravdy. Zamiloval se do jeho sestry Miy a je mu jasné, že jí tím způsobí dvojnásobný šok. Mia neví, čím se ve skutečnosti bratr živí, ani netuší, kdo vlastně Brian je...(oficiální text distributora)', NULL, 2001, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (842, 'S tebou mne bavi svet.m4v', 'S tebou mne bavi svet', 9, NULL, 'cz', '  ', 'cz', NULL, 'http://www.csfd.cz/film/7366-s-tebou-me-bavi-svet/', 'S tebou mne bavi svet.jpg', 30, 'Každoroční pánská jízda tří otců se vlivem razantního zásahu manželek stává něčím jako mateřskou školou v zimní přírodě. Ukáže se, že v jistých situacích jsou otcové lepšími mámami nežli mámy samé, což platí hlavně tehdy, když mámy nejsou v dohledu. Jejich výchovné metody jsou velmi svérázné a vyvolávají plno zmatků a úsměvných situací....(oficiální text distributora)', NULL, 1982, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (843, 'Sedm roku v Tibetu - Seven Years In Tibet.m4v', 'Sedm roku v Tibetu', 15, NULL, 'en', '  ', 'en', 'Seven Years In Tibet', 'http://www.csfd.cz/film/169-sedm-let-v-tibetu/', 'Sedm roku v Tibetu - Seven Years In Tibet.jpg', NULL, 'Příběh dobrodružné cesty rakouského horolezce Heinricha Harrera, který se svým přítelem Petrem Aufschneiterem chtěl dobýt vrchol jednoho z nejvyšších štítu Himalájí - Nanga Parbatu. Po útěku ze zajateckého tábora prošel celými Himalájemi až do tajemného světa Tibetu, kde se stal učitelem mladého Dalajlámy. Mezi těmito dvěma lidmi z úplně odlišných světů se rozvinulo přátelství, které jim přineslo moudrost a pro Harrera znamenalo i hluboký vnitřní přerod. Film je založen na skutečných událostech.(oficiální text distributora)', NULL, 1997, '');
