INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (847, 'Smim prosit.m4v', 'Smim prosit', 6, NULL, 'cz', '  ', 'en', 'Shall We Dance?', 'http://www.csfd.cz/film/116345-smim-prosit/', 'Smim prosit.jpg', 60, 'Přepracovaný chicagský účetní (Gere) je unavený každodenní rutinou, jíž jeho život podlehl. Jednoho dne však spatří krásnou učitelku tance (Lopez) a rozhodne se, že začne chodit na její hodiny tance, aby ji lépe poznal. Jak radost z tance začne procházet jeho životem, pozná, že by to mohl být klíč k záchraně jeho problémového manželství. Své taneční pokroky nakonec uplatní v taneční soutěži na Chicago Crystal Ball Dance Competition. Myslíte, že vyhraje?(oficiální text distributora)', NULL, 2004, '');
