INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (848, 'Smrt panen - The Virgin Suicides.m4v', 'Smrt panen', 15, NULL, 'cz', '  ', 'en', 'The Virgin Suicides', 'http://www.csfd.cz/film/12967-smrt-panen/', 'Smrt panen - The Virgin Suicides.jpg', 40, 'Láska a nenávist, SEX a smrt, vzpomínky a touha. A jedno velké tajemství. Kolem domu Lisbonových se točí všichni kluci. Žije v něm pět krásných sester, kterým jejich rodiče zakazují chodit ven, a chlapci o nich něco vědí jen díky tomu, co vidí přes okna. Tato rodina má temné tajemství, ale nikdo by ho neodhalil, kdyby místní playboy Trip nepozval krásnou Lux Lisbonovou na večírek. Rodiče dokonce povolí děvčatům jít ven a vše vypadá v pořádku. Když se Trip a Lux trochu sblíží, věci naberou nečekaný spád. Kluci teď již nechtějí děvčata jen sledovat. Chtějí je zachránit - i za cenu vlastní nevinnosti.(oficiální text distributora)', NULL, 1999, '');
