INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (851, 'Solokapr - Scoop.m4v', 'Solokapr', 15, NULL, 'en', '  ', 'en', 'Scoop', 'http://www.csfd.cz/film/221858-solokapr/', 'Solokapr - Scoop.jpg', NULL, 'Na vystoupení mága Sida Watermana (Woody Allen) se mimo jiné přišla podívat také studentka žurnalistiky Sondra Pransky (Scarlett Johanson). Iluzionista si ji vybere jako pomocnici pro svůj trik. Jaké je její překvapení, když se v bedně, kde má zmizet, objeví duch zavražděného novináře Joea Strombela (Ian McShane). Jediné co jí Joe stihne říci je to, že ví, kdo je "tarotový vrah", a že proti němu má Sondra najít usvědčující důkazy. Začínající novinářka se, pod vidinou senzačního článku, spolu se Sidem vydá po stopách domnělého vraha – britského aristokrata Petera Lymana (Hugh Jackman). Největší problém v pátrání nastane ve chvíli, kdy se Sondra do Petera zamiluje. Někdo tak sympatický přeci nemůže vraždit... Sólokapr je nová soudobá komedie scenáristy a režiséra Woodyho Allena (Pozor na Harryho/Deconstructing Harry, Match Point – Hra osudu/Match Point, Hollywood v koncích/Hollywood Ending), který do hlavních rolí obsadil nejen sebe, ale i další skvělé herce. V ústřední dvojici se nám představí překrásná Scarlett Johanson (Dívka s perlou/ Girl with a Pearl Earring, Vějíř lady Windermerové/A Good Woman, Match Point – Hra osudu/Match Point) a charismatický Hugh Jackman (X-Men, Kate & Leopold). V roli zemřelého novináře uvidíme Iana McShanea (Jestliže je úterý, musíme být v Belgii/If It''s Tuesday, This Must Be Belgium, Sexy Bestie/Sexy Beast).(oficiální text distributora)', NULL, 2006, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (852, 'Stargate - Hvezdna Brana.m4v', 'Stargate - Hvezdna Brana', 4, NULL, 'en', '  ', 'en', NULL, 'http://www.imdb.com/title/tt0111282/', 'Stargate - Hvezdna Brana.jpg', 10, 'Directed by Roland Emmerich. With Kurt Russell, James Spader, Alexis Cruz, Viveca Lindfors. An interstellar teleportation device, found in Egypt, leads to a planet with humans resembling ancient Egyptians who worship the god Ra.', NULL, 1994, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (853, 'Stripky vzpominek.m4v', 'Stripky vzpominek', 15, NULL, 'cz', '  ', 'en', 'The Bumblebee Flies Anyway', 'http://www.csfd.cz/film/16777-stripky-vzpominek/', 'Stripky vzpominek.jpg', 110, 'Barney Snow se jednoho dne probudí v nemocnici a ze svého života si nepamatuje nic, jen své jméno a opakující se vzpomínky na autonehodu. Budoucnost je nejistá a on si z kliniky vytvoří přechodný domov. Brzy zjišťuje, že všichni ostatní z oddělení umírají na smrtelné choroby - všichni kromě něho. Když další pacient Mazzo požádá Barnyho, aby dělal hostitele jeho setře Cassie, která jej chodí navštěvovat, vznikne mezi nimi hluboké přátelství, které rozkvete v lásku. Pro Barnyho tento vztah znamená nový začátek. Jak mu však střípky vzpomínek začínají vytvářet celistvý obraz, začíná odhalovat záhadu kolem svých podivných snů a upomíná se na svůj předešlý život. Jak bude Barney reagovat, až jednoho dne zjistí celou pravdu o své minulosti a jak se zachová, když ho Mazzo požádá o pomoc, kterou je těžké odmítnout?(oficiální text distributora)', NULL, 1999, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (854, 'Strom snu - The War.m4v', 'Strom snu', 15, NULL, 'cz', '  ', 'en', 'The War', 'http://www.csfd.cz/film/362-strom-snu/', 'Strom snu - The War.jpg', 120, 'Někdy se zdá, že události v našich vzpomínkách jsou jasnější, hřejivější a mají jakoby pomalejší tempo. Stu (Elijah Wood) a Lidia (Lexi Randall) Simmonsovi si s pomocí přátel v koruně STROMU postaví malý domek, ve kterém prožívají chvíle souznění a malých prozření. V těch dnech se z Vietnamu vrací i jejich otec Stephen (Kevin Costner), který chce od základu přestavět rozbořenou stavbu svého života a také naplnit SNY své rodiny. STROM SNŮ je příběhem plným humoru, nostalgie a vynikajících hereckých výkonů. Je to film, který prostě musíte vidět.(oficiální text distributora)', NULL, 1994, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (855, 'Sveraz narodniho lovu.m4v', 'Sveraz narodniho lovu', 9, NULL, 'cz', '  ', 'ru', 'Osobennosti nacionaľnoj ochoty', 'http://www.csfd.cz/film/37948-sveraz-narodniho-lovu/', 'Sveraz narodniho lovu.jpg', 40, 'Mladý Fínsky mladík sa vyberie do Ruska na vysnívaný tradičný ruský lov. Zo sna však rýchlejšie vytriezve, ako z litrov ruskej vodky a samohonky ktorú dobrovoľne vypije lebo patrí k ruským zvykom. Partia, ktorá s zišla na love nemá asi v pláne uloviť nič živé, ale sa riadne spiť do nemoty. Nemožno sa preto čudovať, že i yetti sa zrazu stane skutočným.(andykovac)', NULL, 1995, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (856, 'Sylvia.m4v', 'Sylvia', 15, NULL, 'cz', '  ', 'en', 'Sylvia', 'http://www.csfd.cz/film/134239-sylvie/', 'Sylvia.jpg', 130, 'Píše se rok 1956. Sylvia Plathová studuje na prestižní anglické univerzitě v Cambridgi a píše své první literární pokusy. Potkává zde básníka Teda Hughese a zrodí se osudová láska, která Sylvii provází celý život. I Ted zprvu silně podléhá romantickému vztahu s ženou, která rozumí jeho práci a obdivuje ji, ale po sňatku se leccos změní. Sylvia chce odejít za matkou do Spojených států, Ted dává přednost životu v Anglii, a navíc se vrací ke svým staromládeneckým návykům. Psychicky nevyrovnaná Sylvia nedokáže nevěry milovaného muže přijmout, ale zároveň ho do nich vhání. Útěchu hledá v poezii a ve vlastní tvorbě, píše sbírku Ariel, která dnes patří ve Velké Británii k nejčtenějším, ale sen o štěstí se rozplývá... Filmový příběh na pomezí romance a tragédie poodhaluje mýtus o legendární americké spisovatelce a připomíná jednu z nejničivějších osudových lásek naší doby. Režisérka Christine Jeffsová vsadila na herecké výkony Gwyneth Paltrowové a Daniela Craiga.(oficiální text distributora)', NULL, 2003, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (857, 'Tenka cervena cara - The Thin Red Line.m4v', 'Tenka cervena cara', 15, NULL, 'cz', '  ', 'en', 'The Thin Red Line', 'http://www.csfd.cz/film/6342-tenka-cervena-linie/', 'Tenka cervena cara - The Thin Red Line.jpg', 1, 'Skvělí herci v hlavních rolích – George Clooney, Sean Penn, Nick Nolte a Woody Harrelson – se pouští do nebezpečné akce ve filmu, který přináší drásavě realistický pohled na válečný i morální chaos v Tichomoří během 2. světové války. Film byl v roce 1998 nominován na sedm cen Akademie, včetně ceny za nejlepší film a nejlepší režii (Terrence Malick). Tenká červená linie je filmové mistrovské dílo, které nemá srovnání. Gene Siskel ho označil za “brilantní... hrůzu nahánějící... nejlepší současný válečný film!”(oficiální text distributora)', NULL, 1998, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (858, 'Tenkrat v Mexiku.m4v', 'Tenkrat v Mexiku', 15, NULL, 'en', '  ', 'en', 'Once Upon a Time in Mexico', 'http://www.csfd.cz/film/43393-tenkrat-v-mexiku/', 'Tenkrat v Mexiku.jpg', 5, 'Sága o mýtickém hrdinovi s kytarou - El Mariachim (Antonio Banderas) pokračuje velkolepým akčním filmem Roberta Rodrigueze Kdysi dávno v Mexiku. El Mariachi (Banderas) je poznamenán tragédií a uchýlil se k odloučenému životu v ústraní. Když jej ale zkorumpovaný agent CIA Sands (Johnny Depp) najme k překažení plánu zákeřného bosse Barilla (Willem Dafoe), který má v úmyslu zabít mexického prezidenta, samotářský hrdina je donucen svůj úkryt opustit a vydat se vstříc novému dobrodružství. El Mariachi, kterého i tentokrát doprovázejí dva věrní pomocníci Lorenzo (Enrique Iglesias) a Fideo (Marco Leonardi), má však také vlastní důvod k návratu - krvavou pomstu.(oficiální text distributora)', NULL, 2003, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (859, 'Twin Peaks.m4v', 'Twin Peaks', 15, NULL, 'cz', '  ', 'en', 'Twin Peaks: Fire Walk With Me', 'http://www.csfd.cz/film/6260-twin-peaks/', 'Twin Peaks.jpg', 140, 'Teresa Banksová byla zavražděna. Speciální agent FBI, který zločin vyšetřoval, zmizel beze stopy. Agent Cooper se marně snaží rozmotat případ, obestřený rouškou záhad a tajemství. Jediným výsledkem jeho snažení je neodbytný pocit, že neznámý vrah zaútočí znovu. Uplyne rok. Ocitáme se v městečku Twin Peaks. Pod rouškou poklidné středostavovské idyly bují síly temnot a vládne strach. Nevinné tváře některých středoškolaček skrývají návyk na kokain a neuspořádaný sexuální život. Jedné z nich, Lauře, zbývá posledních sedm dní života...(oficiální text distributora)', NULL, 1992, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (860, 'Valmont.m4v', 'Valmont', 6, NULL, 'cz', '  ', 'fr', 'Valmont', 'http://www.csfd.cz/film/2986-valmont/', 'Valmont.jpg', 70, 'Filmová adaptace slavného románu Choderlose de Laclose Nebezpečné známosti (1782), která na spletitém osudu dvou libertinských intrikánů, vikomta Valmonta a markýzy de Merteuil, zachycuje v poněkud odlehčené poloze obraz aristokratické společnosti několik let před Francouzskou revolucí. Příběh cynických poživačníků je podán v sytých barvách pozdního baroka s důrazem na malebné plenéry, vtipné epizody a bohatou výpravu a kostýmy.(oficiální text distributora)', NULL, 1989, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (861, 'Very bad things - Sest pohrebov a jedna svadba.m4v', 'Sest pohrebov a jedna svadba', 9, NULL, 'cz', '  ', 'en', 'Very bad things', 'http://www.csfd.cz/film/16060-sest-pohrbu-a-jedna-svatba/', 'Very bad things - Sest pohrebov a jedna svadba.jpg', 50, 'Svatba s milovanou ženou je úžasná věc, oddaní kamarádi taktéž. Co zlého se může stát? Jediné, po čem Kyle touží, je vzít si krásnou Lauru. Trochu panovačná a vše kontrolující Laura zase touží po pohádkové svatbě. Dříve než si milenci před oltářem řeknou své ano, je třeba rozloučit se se svobodou... Pět kamarádů naplánovalo divokou jízdu v Las Vegas - hudba, alkohol, drogy, rozbitý nábytek... Náhodná smrt odstartuje sérii vražd - kamarád proti kamarádovi, bratr proti bratru, manžel proti manželce... Události berou rychlý spád. Z děje čiší vtip, ironie, nadsázka. Dokáže člověk, kterého žene strach, opravdu jít přes mrtvoly? Originální, drsný a provokativní thriller s prvky nejčernějšího humoru, nečekanými zvraty a napínavými scénami spojuje tarantinovský styl s paradoxním humorem filmu Mělký hrob.(oficiální text distributora)', NULL, 1998, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (862, 'Vsichni do jednoho.m4v', 'Vsichni do jednoho', 15, NULL, 'cz', '  ', 'cz', NULL, 'http://www.csfd.cz/film/182345-vsichni-do-jednoho/', 'Vsichni do jednoho.jpg', 150, 'Reality-show, která jde ve všem nadoraz. Do velké sklepní místnosti je zavřeno šest účastníků podivné reality-show nazvané Všichni za jednoho. Jejich úkolem je vydržet spolu ve stísněných podmínkách nejméně po dobu jednoho měsíce. Na konci je čeká milionová odměna. Problém je, že pravidla hry nejsou tak docela jasná. Všichni přítomní vytvoří jakousi umělou rodinu: "otcem" je nezaměstnaný popelář Vladimír, "matkou" nesmělá venkovanka Renáta, "dětmi" jsou student sociologie Pavel, do sebe uzavřená dívka Kamila a optimistický dobrodruh Petr. Posledním členem skupiny je "děda", bývalý vězeň z uranových dolů. Skupina prochází těžkými zkouškami vzájemné důvěry, ponorkovými stavy a vypjatými situacemi. Nikdo nemá ponětí o čase, neboť zde není denní světlo ani fungující hodiny. Napětí se zvolna stupňuje až k nesnesení. Pokus o únik z uzavřeného prostoru se nezdaří. Hráčům nakonec dojde, že na povrchu země se zřejmě stalo něco, kvůli čemu zůstali nadobro zapomenutí... Nezávislý český snímek je prvním celovečerním dílem scenáristy a režiséra Karla Žaluda. S nadsázkou odhaluje temné stránky světově rozšířeného televizního žánru reality-show. Lze dokonce mluvit o vizionářské inspiraci: scénář k filmu totiž vznikl dříve, než se tento typ pořadu objevil na českých obrazovkách.(oficiální text distributora)', NULL, 2007, '');
