INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (863, 'Vune zeny.m4v', 'Vune zeny', 6, NULL, 'cz', '  ', 'en', 'Scent of a Woman', 'http://www.csfd.cz/film/935-vune-zeny/', 'Vune zeny.jpg', 10, 'Slepota změnila plukovníka Franka Sladea v zatrpknutého, osamělého a cynického člověka, kterému se už nechce žít. Než se rozhodne definitivně skončit se svým životem, chce se ještě jednou pořádně pobavit. Spolu se studentem Charliem, kterého mu Sladeova rodina najala jako opatrovníka, odchází do New Yorku a jeho poslední výlet nekončí plánovanou sebevraždou. Slade si s Chrisovou pomocí začne znovu uvědomovat krásy života a pomalu se změní na energického muže, jakým kdysi býval. Mezi slepcem a Chrisem postupně vzniká sympatie a důvěra. Slade pomůže chlapcovi vyřešit i jeho školní problémy. Vůně ženy je jednoznačně hereckým a mistrovským sólem Al Pacina. Za svůj výkon slepého důstojníka získal svého prvního Oscara /1993/ a Zlatý glóbus /1993/ za nejlepší mužský herecký výkon.(oficiální text distributora)', NULL, 1992, '');
