INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (864, 'Vykupenie z vaznice Shawshank - Shawshank Redemption.m4v', 'Vykupenie z vaznice Shawshank', 15, NULL, 'cz', '  ', 'en', 'Shawshank Redemption', 'http://www.csfd.cz/film/2294-vykoupeni-z-veznice-shawshank/', 'Vykupenie z vaznice Shawshank - Shawshank Redemption.jpg', 3, 'Strach dělá z lidí vězně. Naděje jim dává křídla. Film natočený podle novely Stephena Kinga. Strhující příběh o přátelství, naději a moudrosti s velmi výraznými hereckými i charakterovými osobnostmi. Andy Dufresne (Tim Robbins) přichází do vězení v Shawshanku jako nový vězeň podezřelý z vraždy. Spřátelí se s doživotně odsouzeným Redem (Morgan Freeman) a společně se snaží přežít v nelidském prostředí vězeňského systému. Odmítají upadnout do rezignace a dodávají naději sobě i spoluvězňům. Film získal mnoho ocenění filmové kritiky a díky svému hluboce humánnímu poselství, skvělým hereckým výkonům a působivému zpracování byl 7 x nominován na Oscara - za nejlepší film, herce v hlavní roli (Morgan Freeman), nejlepší scénář, kameru, hudbu, střih a zvuk.(oficiální text distributora)', NULL, 1994, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (865, 'Wimbledon.m4v', 'Wimbledon', 6, NULL, 'cz', '  ', 'en', 'Wimbledon', 'http://www.csfd.cz/film/117433-wimbledon/', 'Wimbledon.jpg', 80, 'Film Wimbledon je pokračovaním tradície romantických filmov spoločnosti Working Title Films, ako boli Štyri svadby a jeden pohreb, Notting Hill, Denník Bridget Jonesovej a iných. Je to sladká a komická romanca, odohrávajúca sa na tenisovom kurte. Peter Colt (Bettany), je nešťastný chlapík, ale má úspech v profesionálnom a súkromnom živote. Aj keď je v rebríčku niekde úplne vzadu, podarí sa mu získať divokú kartu, ktorá mu umožní zúčastniť sa prestížneho wimbledonského turnaja...(oficiální text distributora)', NULL, 2004, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (866, 'Zakazane ovocie.m4v', 'Zakazane ovocie', 6, NULL, 'cz', '  ', 'en', 'Out of Sight', 'http://www.csfd.cz/film/8565-zakazane-ovoce/', 'Zakazane ovocie.jpg', NULL, 'Čtyřicetiletý Jack Foley by už možná měl myslet na to, aby se usadil. Ale nechce se mu do toho a raději se dál věnuje svému koníčku - vylupování bank. Zvládá to v pohodě a s přehledem, až na jeden drobný problém: Často ho při tom chytnou. Jako recidivistu čeká Jacka několik desetiletí za mřížemi. Jackovi se tam ale nechce. Místo toho chce provést ještě jednu poslední velkou akci a pak spokojeně žít z "vydělaných" peněz. Problém nastane, když se při útěku z vězení setká s pohlednou federální agentkou Karen. Chvíli se dohadují, kdo koho vlastně unesl/zatkl a nakonec se za kuriózních okolností rozejdou. Ovšem Karen našla v sympatickém zločinci zalíbení, zdá se jí o něm a něco ji k němu prostě neodolatelně táhne. Musí ho znovu vidět. Přinejmenším vidět...(oficiální text distributora)', NULL, 1998, '');
