INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (934, 'Detske/Cars 2.m4v', 'Cars 2', 3, NULL, 'cz', '  ', 'en', NULL, 'http://www.csfd.cz/film/241061-auta-2/', 'Cars 2.jpg', 20, 'Hvězdné závodní auto Blesk McQueen a jeho přítel, jedinečný odtahový vůz Burák, se dostanou na úžasná místa, když je jejich cesta zavede za moře. Tam budou soutěžit ve vůbec prvním celosvětovém závodě Grand Prix, kde se má rozhodnout o nejrychlejším autě na světě. Ale cesta na šampionát je plná děr, objížděk a komických překvapení, když se Burák připlete do svého vlastního netušeného dobrodružství - mezinárodní špionáže. Burák, který je rozpolcený tím, že má Bleskovi McQueenovi pomáhat ve vysokém závodu a zároveň se účastnit špionážní mise s nejvyšším utajením, se na své cestě plné akcí dostane do honičky, která ho zavede do ulic Japonska a Evropy, kde ho stopují jeho přátelé a sleduje celý svět. K legraci, která se odehrává v rychlém tempu se přidává pestrá směsice nových účinkujících aut včetně tajných agentů, hrozných darebáků a mezinárodních soupeřů.(oficiální text distributora)', NULL, 2011, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (936, 'Detske/Karlik a tovaren na cokoladu -- Charlie and The Chocolate Factory.m4v', 'Karlik a tovaren na cokoladu', 3, NULL, 'cz', '  ', 'en', 'Charlie and The Chocolate Factory', 'http://www.csfd.cz/film/135983-karlik-a-tovarna-na-cokoladu/', 'Karlik a tovaren na cokoladu -- Charlie and The Chocolate Factory.jpg', 80, 'Charlie Bucket je malý chlapec, pochádzajúci z celkom zbedačenej rodiny, býva na okraji mesta hneď vedľa obrovskej továrne na čokoládu. Charlie miluje čokoládu, aj keď dostáva čokoládový bombón len raz do roka. Jedného dňa však vyhrá aj s ďalšími štyrmi deťmi cestu po fantastickej čokoládovej továrni, ktorú vlastní excentrický Willy Wonka a jeho nemenej zaujímaví zamestnanci.(oficiální text distributora)', NULL, 2005, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (937, 'Detske/Kniha dzungli.m4v', 'Kniha dzungli', 3, NULL, 'cz', '  ', 'en', NULL, 'http://www.csfd.cz/film/277253-kniha-dzungli-maugliho-dobrodruzstvi/', 'Kniha dzungli.jpg', 27, 'Světoznámý román Rudyarda Kiplinga je příběhem malého chlapce Mauglího, který se ztratil v džungli a byl adoptován rodinou vlků. Neustále pronásledován zlým bengálským tygrem Šér Chánem, prožívá Mauglí svá dobrodružství společně se svojí vlčí rodinou pod ochranou černého pantera Baghíry a moudrého medvěda Balúa, který učí zákonům džungle nejen chlapce, ale i celou jeho vlčí smečku.(oficiální text distributora)', NULL, 1990, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (938, 'Detske/Lisiacik Vuk.m4v', 'Lisiacik Vuk', 3, NULL, 'sk', '  ', 'en', NULL, 'http://www.csfd.cz/film/94781-lisacek-lup/', 'Lisiacik Vuk.jpg', 26, 'Malý lišáček Vuk je velký nezbedník. Není však sám, v problémech mu pomáhá věrný kamarád Karak. Dohromady musí čelit velkému nebezpečí v podobě zlého lovce. Podaří se jim to? Musí zachránit přítelkyni lišku a zažijí při tom i mnoho veselých dobrodružství a příhod.(oficiální text distributora)', NULL, 1981, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (939, 'Detske/Lucie postrach ulice.m4v', 'Lucie postrach ulice', 3, NULL, 'cz', '  ', 'en', NULL, 'http://www.csfd.cz/film/7394-lucie-postrach-ulice/', 'Lucie postrach ulice.jpg', 50, 'Dětská pohádková komedie o malé Lucii, protivném vůdci klukovské patry Osvaldovi a dvou kouzelných postavičkách z formely.', NULL, 1984, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (940, 'Detske/Mach Sebestova a Kouzelne sluchatko.m4v', 'Mach Sebestova a Kouzelne sluchatko', 3, NULL, 'cz', '  ', 'en', NULL, 'http://www.csfd.cz/film/9419-mach-sebestova-a-kouzelne-sluchatko/', 'Mach Sebestova a Kouzelne sluchatko.jpg', 70, 'Mach a Šebestová, kreslené postavičky Adolfa Borna ze seriálů, které si oblíbilo už několik generací, se rozhodnou poznat svět lidí. Kouzelné sluchátko splní i toto přání a tak se ti dva, i se psem Jonatánem, ocitnou znenadání ve vilce malého města, kde žije babička s vnukem Jakubem. Docela sami, protože Jakubovi rodiče jsou služebně v Číně. Sluchátko umí hotové zázraky, jenže to, co je v seriálu docela běžné, může mezi lidmi natropit hotový zmatek. Do školy nelze jen tak přijít, nové žáky musí někdo z dospělých přihlásit a už vůbec ji nelze nechat zmizet. Savojská královna musí opustit trůn jen proto, že si Šebestová chtěla sáhnout na její korunu a proměna lidí ve zvířata či neandrtálce budí hotové pozdvižení. Dokud je sluchátko v rukou obrýleného kluka a děvčátka s červenou mašlí, lze všechny omyly do jisté míry rychle napravit. Horší je, když se dostane do nepovolaných rukou...(oficiální text distributora)', NULL, 2001, '');
