INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (942, 'Detske/a zase ta Lucie.m4v', 'a zase ta Lucie', 3, NULL, 'cz', '  ', 'en', NULL, 'http://www.csfd.cz/film/7387-a-zase-ta-lucie/', 'a zase ta Lucie.jpg', 60, 'Další veselá dobrodružství šestileté Lucie a jejích neobyčejných kamarádů, kouzelných panáčků z formely, kteří se jmenují Ferda a Ferda. Zatímco Lucie jako nová vůdkyně party baví společně s formeláky všechny děti, potupený Osvald doma připravuje strašlivou pomstu. Chce se zmocnit kouzelných formeláků, což se mu k jeho škodě také podaří. Formeláci si totiž nenechají nic podobného líbit. Nejprve vybílí lednici, kde má Osvaldova maminka připraveny dobroty na velkou hostinu a potom znemožní Osvaldovo klavírní vystoupení před profesorem Hartmanem. Ale to samozřejmě není konec prázdninového dobrodružství. Sotva se Ferda a Ferda vrátí k Lucii, dobrodružství pokračuje, a tak si mohou děti díky oběma formelákům uprostřed léta například zabruslit.(oficiální text distributora)', NULL, 1984, '');
