INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1071, 'Harry Potter/Harry Potter 1 - and the Philosopher''s Stone 2001.m4v', 'Kamen mudrcu', 4, 3, 'cz', '  ', 'en', 'Philosopher''s Stone', 'http://www.csfd.cz/film/1626-harry-potter-a-kamen-mudrcu/', 'Harry Potter 1 - and the Philosopher''s Stone 2001.jpg', 1, 'Kdo by dnes už alespoň nezaslechl jméno Harryho Pottera, čarodejnického učně, jehož neskutečná popularita mezi dětmi během krátké doby zasáhla i dospělé. Filmovou verzi jeho prvního příběhu zrežíroval Chris Columbus ve spolupráci s autorkou "potterovských" románů J. K. Rowlingovou a pod přísným a podnětným dohledem své dcery Eleanor přenesl na filmové plátno svět Školy čar a kouzel v Bradavicích v plném lesku a hávu hodném největšího kouzelníka na světě. Celý příběh Kamene Mudrců se točí kolem sirotka Harryho Pottera. Tomu krátce po narození zahynou za neobvyklých událostí oba rodiče, a Harry proto musí vyrůstat v rodině strýce Vernona a tety Petunie, kteří mu neustále dávají najevo, že místo kde bydlí, není domov. O to větší překvapení ho čeká, když mu v den jeho jedenáctých narozenin přijde dopis, který je předzvěstí změny v jeho dosavadním životě. Od nynějška není obyčejným chlapcem, i když na to stále vypadá, ale stává se studentem Školy čar a kouzel v Bradavicích. Ani tento nový život však nebude procházka růžovou zahradou, obzvlášť když se začne probouzet Harryho záhadná minulost a její temný hrdina, Lord Voldemort...(oficiální text distributora)', NULL, 2001, '');
