INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1072, 'Harry Potter/Harry Potter 2 - and the Chamber of Secrets 2002.m4v', 'Tajemna komnata', 4, 3, 'cz', '  ', 'en', 'Chamber of Secrets', 'http://www.csfd.cz/film/1624-harry-potter-a-tajemna-komnata/', 'Harry Potter 2 - and the Chamber of Secrets 2002.jpg', 2, 'Harry Potter se po prázdninách vrací do Bradavic a nastupuje do druhého ročníku. A to i přes varování domácího skřítka Dobbyho, podle kterého mu v čarodějné škole hrozí smrt. Harry nedbá nářků skřítka působícího víc škody než užitku, ale potom se skutečně začnou dít podivné věci, na stěnách se objevují neznámé nápisy a několik studentů je přepadeno tajemným přízrakem. Co s tím má společného Tajemná komnata? Stojí za spiknutím opět Voldemort? Kdo je Zmijozelův dědic? Záhadu se Harry vydává rozluštit společně se svými starými známými - Ronem a Hermionou.(oficiální text distributora)', NULL, 2002, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1073, 'Harry Potter/Harry Potter 3 - and the Prisoner of Azkaban 2004.m4v', 'Vezen z Azkabanu', 4, 3, 'cz', '  ', 'en', 'Prisoner of Azkaban', 'http://www.csfd.cz/film/1625-harry-potter-a-vezen-z-azkabanu/', 'Harry Potter 3 - and the Prisoner of Azkaban 2004.jpg', 3, 'Třináctiletý Harry Potter znovu nerad tráví své další prázdniny u rodiny Dursleyových, letos navíc se zlou tetou Marge. I přes přísný zákaz používání kouzel jí Harry promění v obrovský balón a v noci uteče záchranným autobusem do hospody Děravý kotel. Zde se setká s ministrem kouzel Korneliem Popletalem, který ho překvapivě za jeho provinění nepotrestá. Je to proto, že z vězení v Azkabanu uprchl nebezpečný čaroděj Sirius Black, který podle legend přivedl lorda Voldemorta k Harryho rodičům a je tak zodpovědný za jejich smrt. Nyní Voldemort usiluje také o zabití Harryho. A aby nebyl všem hrůzám konec, hostí Bradavická škola hrozivé azkabanské stráže, zvané mozkomoři. Ti mají za úkol ochraňovatt Harryho a další studenty před černým čarodějem. Střetnutí mezi Harrym a záhadným vězněm Siriem Černým se zdá být nevyhnutelné. Harry Potter bude potřebovat veškerou možnou kuráž, kouzla a podporu, aby dokázal odhalit pravdu a vazby na tajemnou minulost.(oficiální text distributora)', NULL, 2004, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1074, 'Harry Potter/Harry Potter 4 - and the Goblet of Fire 2005.m4v', 'Ohnivy pohar', 4, 3, 'cz', '  ', 'en', 'Goblet of Fire', 'http://www.csfd.cz/film/42949-harry-potter-a-ohnivy-pohar/', 'Harry Potter 4 - and the Goblet of Fire 2005.jpg', 4, 'Harryho štvrtý rok na Rockfortskej škole sa celý krúti okolo turnaja, v ktorom sa stretnú študenti troch čarodejníckych škôl v sérii stále náročnejších úloh. Súťažiaci sú vyberaní ohnivou čašou, ktorá tentoraz z Rockfortu vyberie aj Harryho! A aby to všetko nebolo také jednoduché, začína sa hlásiť o slovo aj Voldemort, ktorý je opäť pripravený vrátiť sa!(oficiální text distributora)', NULL, 2005, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1075, 'Harry Potter/Harry Potter 5 - and the Order of Phoenix 2007.m4v', 'Fenixuv rad', 4, 3, 'cz', '  ', 'en', 'Order of Phoenix', 'http://www.csfd.cz/film/197182-harry-potter-a-fenixuv-rad/', 'Harry Potter 5 - and the Order of Phoenix 2007.jpg', 5, 'Harry Potter se vrací do Bradavic, aby nastoupil do pátého ročníku školy čar a kouzel. Tam však zjistí, že většina členů kouzelnické komunity věří, že jeho nedávné setkání s Lordem Voldemortem je obyčejný výmysl, čímž je zásadně zpochybněna Harryho důvěryhodnost. Ba co hůř, ministr kouzel Kornelius Popletal opět jmenoval nového učitele obrany proti černé magii. V lekcích podezřelé profesorky Umbridgeové se však mladí čarodějové nic zásadního nenaučí a proto nemají dostatek znalostí k boji s temnými silami, které se kolem nich stahují. Na Hermioninu a Ronovu výzvu se Harry rozhodne vzít chod věcí do svých rukou. Tajně se začne scházet s malou skupinou studentů, kteří se hrdě nazvali Brumbálova armáda, a učí je, jak se ubránit černé magii. Ačkoliv je sám teprve student, připravuje odvážné mladé čaroděje na nebezpečnou bitvu, která brzy propukne..(oficiální text distributora)', NULL, 2007, '');
