INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1076, 'Harry Potter/Harry Potter 6 - and the Half-Blood Prince 2009.m4v', 'Princ dvoji krve', 4, 3, 'cz', '  ', 'en', 'Half-Blood Prince', 'http://www.csfd.cz/film/224184-harry-potter-a-princ-dvoji-krve/', 'Harry Potter 6 - and the Half-Blood Prince 2009.jpg', 6, 'Povzbuzeni návratem Lorda Voldemorta působí Smrtijedi spoušť jak ve světě mudlů, tak ve světě kouzelníků a Bradavice už dávno nejsou tím bezpečným místem, jakým kdysi bývaly. Harry má podezření, že nebezpečí je přímo v Bradavicích, ale Brumbál se soustředí na přípravu na poslední bitvu, která se rychle blíží. Potřebuje, aby mu Harry pomohl odkrýt důležitý klíč, který vede k rozuzlení Voldemortovy obrany – rozhodující informaci ale zná jen bývalý bradavický profesor Horác Křiklan. Vědom si toho přiměje Brumbál svého starého kolegu, aby se vrátil na své staré místo, a slíbí mu vyšší plat, větší kancelář a navíc možnost učit slavného Harryho Pottera.Mezitím útočí na studenty ještě úplně jiný nepřítel – teenagerovské hormony. Harryho dlouhodobé přátelství s Ginny Weasleyovou přerůstá v hlubší city, ale v cestě stojí její přítel, Dean Thomas, a to nemluvě o jejím velkém bratru Ronovi. Jenže Ron má svých starostí dost s romantickými pletkami – Lavender Brownová nešetří svou láskou k němu a Hermiona v ústraní žárlí, ale je rozhodnuta neprojevit své city za žádnou cenu. A k tomu všemu ještě čokolády napuštěné nápojem lásky, které skončí ve špatných rukách a všechno naprosto změní.Romantika všude vzkvétá, ale jeden ze studentů zůstává od toho všeho stranou, neboť má v hlavě úplně jiné věci. Je rozhodnut vystoupit z davu a zanechat po sobě stopu, byť temnou. Láska je sice ve vzduchu, ale tragédie se blíží a ani Bradavice už nikdy nebudou takové, jaké bývaly.(oficiální text distributora)', NULL, 2009, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1091, 'James Bond/1983 James Bond - Chobotnicka -- Octopussy.m4v', 'Chobotnicka', 15, 15, 'cz', '  ', 'en', 'Octopussy', 'http://www.imdb.com/title/tt0086034/', '1983 James Bond - Chobotnicka -- Octopussy.jpg', 13, 'Directed by John Glen.  With Roger Moore, Maud Adams, Louis Jourdan, Kristina Wayborn. A fake Fabergé egg and a fellow agent''s death lead James Bond to uncover an international jewel-smuggling operation, headed by the mysterious Octopussy, being used to disguise a nuclear attack on N.A.T.O. forces.', NULL, 1983, '');
