INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1077, 'Harry Potter/Harry Potter 7 - and the Deathly Hallows Part 1 2010.m4v', 'Relikvie smrti 1', 4, 3, 'cz', '  ', 'en', 'Deathly Hallows Part 1', 'http://www.csfd.cz/film/230063-harry-potter-a-relikvie-smrti-cast-1/', 'Harry Potter 7 - and the Deathly Hallows Part 1 2010.jpg', 7, 'Harry Potter a Relikvie smrti je sedmým a posledním pokračováním dobrodružství Harryho Pottera, které je očekávanou událostí a bude rozděleno do dvou celovečerních filmů. První část začíná, když se Harry, Ron a Hermiona vydávají na nebezpečnou misi, jejímž cílem je najít a zničit tajemství Voldemortovy nesmrtelnosti - viteály. Spoléhat musí jen sami na sebe, a to víc, než kdy dříve, protože jim už profesoři nepomohou a budou také bez ochrany profesora Brumbála. Navíc jsou mezi nimi temné síly, které se je snaží rozdělit. Mezitím se svět kouzelníků stane nebezpečným místem pro všechny nepřátele Pána zla. Dlouho obávaná válka se zlem začala, Voldemortovi smrtijedi kontrolují ministerstvo kouzel i Bradavice a terorizují a zatýkají všechny, kdo by se jim chtěl postavit. To nejcennější, co Voldemort chce, ale stále hledají: Harryho Pottera. Vyvolený se stal loveným. Smrtijedi hledají Harryho všude, aby ho podle příkazu Voldemorta přivedli... živého. Harryho jedinou nadějí je, že najde viteály dřív, než Voldemort najde jeho. Při pátrání po viteálech objeví starý a téměř zapomenutý příběh - legendu o relikviích smrti. A pokud je legenda pravdivá, mohl by Voldemort získat neomezenou moc a sílu, po které touží.(oficiální text distributora)', NULL, 2010, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1078, 'Harry Potter/Harry Potter 7 - and the Deathly Hallows Part 2 2011.m4v', 'Relikvie smrti 2', 4, 3, 'cz', '  ', 'en', 'Deathly Hallows Part 2', 'http://www.csfd.cz/film/239950-harry-potter-a-relikvie-smrti-cast-2/', 'Harry Potter 7 - and the Deathly Hallows Part 2 2011.jpg', 8, 'Přichází poslední dobrodružství ságy o Harry Potterovi. V tomto výpravném finále eskaluje bitva mezi dobrem a zlem do otevřené války čarodějného světa. Nikdy nebylo v sázce tolik jako teď. Nikdo už není v bezpečí. Je to ale Harry Potter, kdo možná bude muset podstoupit oběť největší, když se přiblíží rozhodující bitva s Lordem Voldemortem. Tady všechno končí ...(oficiální text distributora)', NULL, 2011, '');
