INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1388, 'SK40/SK40-01 Kapitan Dabac -- Captain Dabac.m4v', 'Kapitan Dabac', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/27979-kapitan-dabac/', 'SK40-01 Kapitan Dabac -- Captain Dabac.jpg', 1, 'Dráma mladého dôstojníka slovenskej armády, ktorý si počas bojov na Ukrajine postupne uvedomuje hrôzy fašizmu. Po návrate na Slovensko sa zapojí do SNP. Nedokáže však prekonať postoj samotára, ani pochybnosti o zmysle partizánskej činnosti.', NULL, 1959, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1389, 'SK40/SK40-02 Stastie pride v nedelu -- Luck Will Come On Sunday.m4v', 'Stastie pride v nedelu', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/82767-stastie-pride-v-nedelu/', 'SK40-02 Stastie pride v nedelu -- Luck Will Come On Sunday.jpg', 2, 'Emil, Vrabec a Mišo sú traja priatelia, ktorých najväčšou vášňou je podávať športku. Každú nedeľu, ako mnoho iných nadšencov, očakávajú výsledky a dúfajú, že raz sa im podarí vyhrať slušný obnos peňazí. Každý z nich má svoj sen - Emil túži po „Jave 250“, Vrabcovi by stačil aj „Pionier“ a Mišo dúfa, že sa raz vyberie na svoju veľkú cestu okolo sveta. Ani jeden z nich však doteraz nemal šťastie v hre. Tento stav sa rozhodnú radikálne vyriešiť. Každý z nich vynaloží maximálne úsilie na to, aby získal čo najviac peňazí na podanie športky - pravdepodobnosť výhry sa tým automaticky zvýši. Ale to nie je tak jednoduché, ako by sa na prvý pohľad zdalo. Situácia medzi priateľmi sa ešte viac vyhrotí po tom, ako sa v televíznej estráde zoznámia s atraktívnou Evou Dvorčíkovou. Tá je už dlhší čas platonickou láskou Michala, ale ten dosiaľ nenabral odvahu, aby sa jej prihovoril. A keďže v láske je povolené všetko, chlapci sa rozhodnú losovať. Ten, ktorý vyhrá, získa právo bojovať o Evinu lásku...(oficiální text distributora)', NULL, 1958, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1390, 'SK40/SK40-03 Rodna zem -- Native Land.m4v', 'Rodna zem', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/6299-rodna-zem/', 'SK40-03 Rodna zem -- Native Land.jpg', 3, 'Režisér Josef Mach a slovenský scénárista Ivan Bukovčan vybrali pro první slovenský barevný umělecký film nejzářivější ukázky z nepřeberného bohatství slovenských lidových písní a tanců. Jejich film "Rodná zem" vypráví příběh děvčat a chlapců, kteří byli objeveni v různých krajích Slovenska instruktorem profesionálního národopisného souboru lidových písní a tanců a kteří se sešli v radostném kolektivu mladých lidových umělců. Tak se stane členkou souboru Verona zpod Tater traktoristka Žofka ze Zemplína, dělnice Anička z Detvy, dřevorubec Martin, milý Verony, malíř Ferko, jehož talent objevila Žofka, a desítky jiných. Film "Rodná zem" je oslavou radostné umělecké tvořivosti slovenského lidu a zároveň oslavou krásy slovenské země.(Filmový přehled)', NULL, 1953, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1391, 'SK40/SK40-04 Styridsatstyri -- Fortyfour Mutineers.m4v', 'Styridsatstyri', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/29319-styridsatstyri/', 'SK40-04 Styridsatstyri -- Fortyfour Mutineers.jpg', 4, 'Dramatický príbeh o protidôstojníckej vzbure 71. trenčianskeho pešieho pluku v Kragujevaci v období prvej svetovej vojny. Film získal osobitné čestné uznanie a Cenu FIPRESCI na 11. MFF Karlove Vary 1958.', NULL, 1957, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1392, 'SK40/SK40-05 Stvorylka -- The Quadrille.m4v', 'Stvorylka', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/24871-stvorylka/', 'SK40-05 Stvorylka -- The Quadrille.jpg', 5, 'Ironický pohľad na malomeštiacke časy pred prvou svetovou vojnou. Dámy miestnej smotánky sú potrestané za svoje chvíľkové poblúznenie k dôstojníkom vojenského oddielu, ktorý príde do malého slovenského mestečka v roku 1910, v čase konania mestského plesu.(oficiální text distributora)', NULL, 1955, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1393, 'SK40/SK40-06 Varuj -- Beware.m4v', 'Varuj', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/3171-varuj/', 'SK40-06 Varuj -- Beware.jpg', 6, 'Prvý slovenský hraný film nakrútený po druhej svetovej vojne.', NULL, 1946, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1410, 'SK60/SK60-13 Serif za mrezami -- The Sheriff Behind Bars.m4v', 'Serif za mrezami', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/163929-serif-za-mrezami/', 'SK60-13 Serif za mrezami -- The Sheriff Behind Bars.jpg', 23, 'Dramatický príbeh z prostredia nápravno - výchovného ústavu pre mladistvých. Mladý Jakub sa z falošnej solidarity nechá odsúdiť za krádež namiesto "šerifa" ich partie. Dúfa, že "šerif" jeho gesto ocení. Toto presvedčenie mu dodáva silu vydržať neznesiteľné pomery v ústave.(oficiální text distributora)', NULL, 1965, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1394, 'SK40/SK40-07 Vlcie diery -- Wolve''s Lairs.m4v', 'Vlcie diery', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/28616-vlcie-diery/', 'SK40-07 Vlcie diery -- Wolve''s Lairs.jpg', 7, 'Dráma zo Slovenského národného povstania. Vo filme sú použité autentické snímky zo SNP a postupu Sovietskej armády. Pri nakrúcaní pomáhala Československá armáda a Zväz partizánov. Autori filmu ešte s čerstvými skúsenosťami vytvorili obraz rodiny Svrčinovcov, ktorý dodnes ostal vzorom kolektívneho portrétu a do istej miery symbolizuje celonárodný odpor proti domácemu a nemeckému fašizmu.(oficiální text distributora)', NULL, 1948, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1395, 'SK40/SK40-08 Katka -- Cathy.m4v', 'Katka', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/4996-katka/', 'SK40-08 Katka -- Cathy.jpg', 8, 'Veselohra o mladom dedinskom dievčati, ktoré sa stretáva s novými životnými skúsenosťami vo veľkej továrni. Pomaly si zvyká na nové prostredie aj na nových ľudí a z dedinskej naivky sa stáva mladá žena s novými ideálmi.', NULL, 1949, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1396, 'SK40/SK40-09 Cert nespi -- The Devil Never Sleeps.m4v', 'Cert nespi', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/108821-cert-nespi/', 'SK40-09 Cert nespi -- The Devil Never Sleeps.jpg', 9, 'Slovenský celovečerní hraný film se skládá ze tří samostatných satirických povídek, které byly k zfilmování vybrány ze sbírky Petra Karvaše. Všechny vtipně a pádně odhalují a kritisují nedostatky v našem životě. Jsou to povídky „Smutný káder", „Typický prípad" a „Vel''korysá kampaň". Povídky jsou spojeny záběry ze zasedání schvalovací komise, která si dává filmy promítnout („V rámci komisie"). Členové komise se v jednotlivých postavách na plátně poznávají a v přestávkách mezi promítáním vznášejí své připomínky.(Filmový přehled)', NULL, 1956, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1397, 'SK40/SK40-10 Dazdnik svateho Petra -- St.m4v', 'Dazdnik svateho Petra', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/29355-szent-peter-esernyoje/', 'SK40-10 Dazdnik svateho Petra -- St.jpg', 10, 'Veselohra z druhej polovice 19. storočia o histórii červeného dáždnika, ktorým podľa legendy ochránil svätý Peter osirelú sestru glogovského farára pred prudkým lejakom. O niekoľko rokov neskôr je zo siroty krásna Veronika, o ruku ktorej sa uchádza švárny Juraj Vibra.(oficiální text distributora)', NULL, 1958, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1398, 'SK60/SK60-01 Janosik I.m4v', 'Janosik I', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/84727-janosik-i-ii/', 'SK60-01 Janosik I.jpg', 11, 'Nesmrtelná legenda o zbojnickém hrdinovi Jánošíkovi, který bohatým bral a chudým dával, je stále vděčným námětem různých uměleckých zpracování. Třetí filmová verze, která byla natočena na Kolibě v letech 1962-63, vznikla u příležitosti 250. výročí Jánošíkovy smrti. Námět, scénář i režie této velkolepé historické fresky jsou dílem Palo Bielika - původního hereckého představitele Jánošíka z české Fričovy filmové verze z roku 1935. Zajímavé je, že hlavní roli nesvěřil zkušený herec žádnému z herců, ale mladému lékaři z Iľavy, Františku Kuchtovi. Neokoukaná tvář tak vtiskla celému filmovému vyprávění silný, realistický nádech. Velkorysý projekt, natáčený v exteriérech slovenských hor, s velkou účastí komparsu, náročnými scénickými stavbami (důmyslná zařízení Jánošíkova lágera) a s účastí plejády vynikajících slovenských herců, patří k pokladům slovenské kinematografi.(oficiální text distributora)', NULL, 1962, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1399, 'SK60/SK60-01 Janosik II.m4v', 'Janosik II', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/84727-janosik-i-ii/', 'SK60-01 Janosik II.jpg', 12, 'Nesmrtelná legenda o zbojnickém hrdinovi Jánošíkovi, který bohatým bral a chudým dával, je stále vděčným námětem různých uměleckých zpracování. Třetí filmová verze, která byla natočena na Kolibě v letech 1962-63, vznikla u příležitosti 250. výročí Jánošíkovy smrti. Námět, scénář i režie této velkolepé historické fresky jsou dílem Palo Bielika - původního hereckého představitele Jánošíka z české Fričovy filmové verze z roku 1935. Zajímavé je, že hlavní roli nesvěřil zkušený herec žádnému z herců, ale mladému lékaři z Iľavy, Františku Kuchtovi. Neokoukaná tvář tak vtiskla celému filmovému vyprávění silný, realistický nádech. Velkorysý projekt, natáčený v exteriérech slovenských hor, s velkou účastí komparsu, náročnými scénickými stavbami (důmyslná zařízení Jánošíkova lágera) a s účastí plejády vynikajících slovenských herců, patří k pokladům slovenské kinematografi.(oficiální text distributora)', NULL, 1962, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1400, 'SK60/SK60-02 Piesen o sivom holubovi -- The Song of The Grey Pigeon.m4v', 'Piesen o sivom holubovi', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/27455-piesen-o-sivom-holubovi/', 'SK60-02 Piesen o sivom holubovi -- The Song of The Grey Pigeon.jpg', 13, 'Poviedkový film, ktorý sa odohráva v slovenských horách na konci druhej svetovej vojny. Jeden zo zlatého fondu slovenskej kinematografie. Na svoju dobu modernými prostriedkami filmového rozprávania v kinách oslovovil aj v zahraničí. Film Pieseň o sivom holubovi je celovečerným režijným debutom Stanislava Barabáša. Ako predloha pre film poslúžil scenár Alberta Marenčina a Ivana Bukovčana ktorý získal prvú cenu v štátnej súťaži k pätnástemu výročiu oslobodenia Československa. Pieseň o sivom holubovi pozostáva zo šiestich poviedok, ktoré však vytvárajú kompaktný a súdržný celok. Dej filmu tvorí filmu šesť príbehov - prvý sa začína v lete 1944, posledný sa končí na jar 1945 - no tieto príbehy na seba nadväzujú, ich hrdinami sú vždy predovšetkým tí istí traja vidiecki chlapci rozličného veku, Rudko, Vinco a Martin. Práve skrz nich vnímame všetky udalosti prezentované vo filme. A to aj napriek faktu, že základnou a nosnou témou filmu je Slovenské národné povstanie. Pôsobivo prostredníctvom detských hrdinov vypovedá o hrôzach a nezmyselnosti vojny.(oficiální text distributora)', NULL, 1961, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1408, 'SK60/SK60-11 Skalni v ofsajde -- Soccer Fans.m4v', 'Skalni v ofsajde', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/28165-skalni-v-ofsajde/', 'SK60-11 Skalni v ofsajde -- Soccer Fans.jpg', 21, 'Komediálne ladený príbeh o troch priateľoch, "skalných fanúšikoch", ktorí sa snažia získať vstupenky na významný futbalový zápas s brazílskym klubom. Všetci pracujú v projektovom ústave a robia všetko preto, aby mohli povzbudiť svoje mužstvo na chystanom zápase. Lenže všetko sa im vymyká z rúk...(oficiální text distributora)', NULL, 1960, '');
