INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1411, 'SK60/SK60-14 Zvony pre bosych -- The Bells For the Barefooted.m4v', 'Zvony pre bosych', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/45060-zvony-pre-bosych/', 'SK60-14 Zvony pre bosych -- The Bells For the Barefooted.jpg', 24, 'Dramatický príbeh z konca druhej svetovej vojny. Dvaja partizáni sa po bojovej akcii vracajú k skupine vedúc so sebou mladého nemeckého vojaka. Uprostred beznádejne pustých hôr sa ocitnú ľudia z dvoch nepriateľských táborov. Kým partizáni strácajú kontakt s jednotkou, Nemec stráca nádej, že sa mu podarí vyviaznuť z tejto zložitej situácie.(oficiální text distributora)', NULL, 1965, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1401, 'SK60/SK60-03 Slnko v sieti -- The Sun In a Net.m4v', 'Slnko v sieti', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/28765-slnko-v-sieti/', 'SK60-03 Slnko v sieti -- The Sun In a Net.jpg', 14, 'Třem místům děje zhruba odpovídají tři myšlenkové okruhy: střetávání se Fajola a Bely s nejistotami první lásky, obraz života v přírodě (kde v starci, jehož sítě v dunajském ohbí čekají na kořist, cítí Fajolo ono neposkvrněné cosi, co tak trýznivě hledá) a pokrytectví a lež na senové brigádě (kde družstevníci kradou, vyhýbají se práci a místní rozhlas šíří politické fráze), Szomolányiho kamera v detailech vystihuje atmosféru, evokuje náladu, promyšleně pracuje s hloubkou ostrosti. Průkopnické dílo slovenské kinematografie, kterým byl přehodnocen tehdejší směr vývoje a které naznačilo novou cestu (po desetiletích myšlenkové sterility, výrazového schematismu a povstalecko-budovatelského patosu), bývá považováno za první dílo české a slovenské nové vlny. Přináší nejen nový způsob vyprávění (skládání "rozbíjeného" příběhu s důrazem na obrazové vyjadřování), ale i nový pohled na realitu analytickým zaměřením na city a pocity člověka, historické a společenské souvislosti. Postupy pocitového filmu tlumočí Uher vztah mladých k světu neobvyklou poetickou formulací. Tvůrci narušují vnější dramatismus, napětí je založené na vnitřních kontrastech zdánlivě nezaujatého záznamu (kontrast shonu a klidu, všedností i zvláštností, detailů i celků, kontrast ticha a zvuků). Hledají tak krásu a bolest skutečného citu a každodenního života. Poetickou vnímavost diváka vyvolává i důvěrná reflexe Fajolových vnitřních monologů. Uher uvolnil léta potlačovanou duševní energii slovenského filmu, vyvedl ho z provinční omezenosti a folklorismu.(oficiální text distributora)', NULL, 1962, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1402, 'SK60/SK60-04 Boxer a smrt -- The Boxer And Death.m4v', 'Boxer a smrt', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/28977-boxer-a-smrt/', 'SK60-04 Boxer a smrt -- The Boxer And Death.jpg', 15, 'Film začína zvukom úderov, z tmy sa postupne vynára postava cvičiaceho boxera. Po tréningu sa provizórny ring vďaka uniforme veliteľa koncentračného tábora náhle odkrýva ako súčasť veľkej továrne na smrť. Od prvého záberu filmu sa charakter týchto dvoch miest prelína a mení v príbehu dvoch nerovnocenných protivníkov. Na jeho začiatku majú „šťastie“ obaja: veliteľ tábora Kraft nájde svojho sparingpartnera a väzňovi Komínkovi, ktorého chytili na úteku, tak v poslednej chvíli predĺži život. Pre prvého, disponujúceho absolútnou mocou, je to ideálne vylepšenie tréningových podmienok v pláne povojnového pokračovania boxerskej kariéry. Druhý sa stáva bezcennou „muchou“ vykrmovanou „ako prasa“ pred blízkou porážkou. Silnejúci bývalý boxer Komínek však čoskoro pozná slabiny Krafta. Bezmocný skrýva svoju silu, aby si zachránil život. Veliteľa porazí až v zápase pomsty za umučenie priateľa. Jeho ďalšiu popravu odvráti Kraftova Helga, podľa ktorej by mohla uškodiť manželovmu imidžu fair play športovca. Pre návrat do tábora a smrť sa nakoniec rozhodne sám, keď pochopí, že cenou za jeho prepustenie je poprava 40 spoluväzňov. Film, nakrútený podľa rovnomennej poviedky poľského spisovateľa Józefa Hena, sa stal prvou slovenskou psychologickou drámou z prostredia koncentračného tábora. V koncepte dvojakého ringu i dvojakej hry ako boja o život sa Solan zámerne vyhol konkretizáciám a spektakulárnosti, aby o to viac diskrétnosťou a asketizmom režijného štýlu prenikol k tomu, čo v každej hraničnej situácii rozhoduje: zlyhanie, alebo sila ľudskosti a solidarity. [44. MFFKV 2009](oficiální text distributora)', NULL, 1962, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1403, 'SK60/SK60-06 Drak sa vracia -- Dragon''s Return.m4v', 'Drak sa vracia', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/29049-drak-se-vraci/', 'SK60-06 Drak sa vracia -- Dragon''s Return.jpg', 16, 'Balada o láske, nenávisti a hľadaní cesty z osamelosti. Dramatický príbeh uzavretého a neprístupného hrnčiara Martina Lepiša, ktorého okolie obviňuje zo skutkov, ktorých sa nikdy nedopustil. Po rokoch sa vracia, ale ani jeho statočná záchrana majetku dedinčanov pred lesným požiarom mu neprinesie dôveru, ani miesto medzi ľuďmi.(oficiální text distributora)', NULL, 1967, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1404, 'SK60/SK60-07 Den nas kazdodenny... -- Our Daily Day...m4v', 'Den nas kazdodenny...', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/228527-den-nas-kazdodenny/', 'SK60-07 Den nas kazdodenny... -- Our Daily Day...jpg', 17, 'Dokumentárna rekonštrukcia všedných dní obyčajnej rodiny našich čias. Témou filmu je výchovný vzťah rodičov k deťom ako aj pedagogický vzťah učiteľov k žiakom.', NULL, 1969, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1405, 'SK60/SK60-08 Kristove roky -- The Prime of Life.m4v', 'Kristove roky', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/4720-kristove-roky/', 'SK60-08 Kristove roky -- The Prime of Life.jpg', 18, 'Bohato členená výpoveď o nástojčivej potrebe konečného rozhodnutia človeka na životnej križovatke. Maliar Juraj, Slovák žijúci v Prahe, si po tridsiatke so skrývanou bezradnosťou uvedomuje bezcieľnosť svojho doterajšieho života bez záväzkov a povinností. Postupne si vo vzťahu k blízkym ľuďom uvedomuje svoje životné postavenie, aby nakoniec dospel k pokusu začať žiť svoj život nanovo a lepšie.(POMO)', NULL, 1967, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1406, 'SK60/SK60-09 Slavnost v Botanickej zahrade -- Celebration In the Botanical Garden.m4v', 'Slavnost v Botanickej zahrade', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/27471-slavnost-v-botanickej-zahrade/', 'SK60-09 Slavnost v Botanickej zahrade -- Celebration In the Botanical Garden.jpg', 19, 'Farebná feéria o potrebe zázraku v ľudskom živote, impresívne očarenie prírodou a radosťou zo života a pohybu. Mozaikovité rozprávanie o krčmárke Márii, večnej neveste, vytrvalo a márne vodiacej k oltáru otca svojich ôsmych dcér Pištu a o Pierrovi, ktorý svojím príchodom naruší pokoj vinárskej dediny Babindolu a naučí jej obyvateľov vychutnávať život.(oficiální text distributora)', NULL, 1969, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1407, 'SK60/SK60-10 322.m4v', '322', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/4134-322/', 'SK60-10 322.jpg', 20, 'Slovenská psychologická dráma. Príbeh človeka, ktorý sa ocitne v nemocnici s podozrením na vážnu chorobu. Prehodnocuje svoj doterajší život a jeho strenutia s blízkymi i cudzími ľuďmi vytvárajú aj obraz spoločnosti 60. rokov, plných nádejí i dezilúzií. Autor nastoľuje tu základné problémy ľudského bytia, otázky slobody, samoty, nekomunikatívnosti, hľadania vlastnej identity hrdinov a ich miesta v spoločnosti.(oficiální text distributora)', NULL, 1969, '');
