INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1412, 'SK60/SK60-15 Majster kat -- Master Executioner.m4v', 'Majster kat', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/29329-majster-kat/', 'SK60-15 Majster kat -- Master Executioner.jpg', 25, 'Dramatický príbeh o rivalite medzi dvoma bývalými priateľmi sa odohráva v období vpádu Turkov na Slovensko. Dvoch priateľov rybárov Richardusa /Štefan Kvietik/ a posledného bratislavského kata Emil Targa /Vlado Muller/ spája dlhoročné priateľstvo, ale aj nešťastná náklonnosť k jednej žene Milici. Medzi statočným rybárom a chvastúnskym katom, ktorý sa často a veľmi rád potužoval alkoholom, nastal zlom, keď milovanú Milicu vybral alipaša do hámeru. Utrpenie ľudí sa stupňovalo a Turci v snahe zabraňiť stupňujúcej sa revolte sa na výstrahu rozhodli popraviť najvplyvnejších miestneho kňaza a rebelantov Richardusa a Emila. Pod tlakom veľkej lásky, závisti a nešťastných udalostí Emil zradí priateľa Richardusa a medzi bývalými priateľmi vzniká nenávisť....(oficiální text distributora)', NULL, 1966, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1413, 'SK60/SK60-16 Panna zazracnica -- The Wonder-Maid.m4v', 'Panna zazracnica', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/105928-panna-zazracnica/', 'SK60-16 Panna zazracnica -- The Wonder-Maid.jpg', 26, 'Film zachytáva život mladej generácie umelcov, formujúcej sa uprostred vojny. Anabella - mladé krásne dievča sa zoznámi so skupinou priateľov, rozochvieva v nich túžby, stane sa predmetom ich predstáv. Očarení jej "zázračnosťou" však nevidia Anabellin smútok, ktorý vyústi v jej tragický koniec. Podľa rovnomennej knihy Dominika Tatarku(oficiální text distributora)', NULL, 1966, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1414, 'SK60/SK60-17 Zivy bic -- The Living Scourge.m4v', 'Zivy bic', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/29343-zivy-bic/', 'SK60-17 Zivy bic -- The Living Scourge.jpg', 27, 'Televízne filmové spracovanie rovnomenného románu Mila Urbana, ktoré v roku 1966 nakrútil režisér Martin Ťapák. Milo Urban /1904 - 1982/ - jeden z najvýznamnejších slovenských medzivojnových prozaikov. Detstvo prežil s rodičmi v horárni, takmer', NULL, 1966, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1415, 'SK60/SK60-18 Trie dcery -- Three Daughters.m4v', 'Trie dcery', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/28853-tri-dcery/', 'SK60-18 Trie dcery -- Three Daughters.jpg', 28, 'Vesnický hospodář nechce dělit majetek, a proto pošle tři dcery do kláštera. V rámci kolektivizace mu však majetek začátkem padesátých let vezmou a on hledá ochranu u dcer. Pouze nejmladší mu vyjde vstříc ...', NULL, 1968, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1416, 'SK60/SK60-19 Zmluva s diablom -- A Pact With the Devil.m4v', 'Zmluva s diablom', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/24893-zmluva-s-diablom/', 'SK60-19 Zmluva s diablom -- A Pact With the Devil.jpg', 29, 'Na základe hlúpeho a zlomyseľného žartu upadne päť maturantiek do podozrenia, že sa rozhodli do maturity zbaviť panenstva. Keďže ich obrane nikto neverí, rozhodnú sa Ivana Karbanová (MARCELA PAVELKOVÁ), Aja Trnková (SYLVIA TURBOVÁ) a Olina Dvorská (ZUZANA KOCÚRIKOVÁ) urobiť presne to, z čoho ich všetci vrátane ich malomeštiackych rodičov a učiteľov podozrievajú...(oficiální text distributora)', NULL, 1967, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1417, 'SK60/SK60-20 Vtackovia, siroty a blazni -- Birdies, Orphans And Fools.m4v', 'Vtackovia, siroty a blazni', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/4737-vtackovia-siroty-a-blazni/', 'SK60-20 Vtackovia, siroty a blazni -- Birdies, Orphans And Fools.jpg', 30, 'O lásce a bláznovství dvou kamarádů a jedné dívky Znovuzrození po 30ti letech v komunistickém trezore!První film z Jakubiskové Trilogie o štěstí je plný fikce a expresívních obrazů,kde láska, štěstí, svoboda a smrt jsou základními principy života. Nekonvenční příběh třech přátel - sirot Yoricka, a Andreja a jejich společné lásky Marty se odehrává ve světe plném násilí, cynizmu a krutosti,kde jedinou cestou ke štěstí je bláznovstvíDalšími filmy trilogie jsou Sedím na konári a je mi dobře a Lepšie by bohatý a zdravý ako chudobný a chorý.(oficiální text distributora)', NULL, 1969, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1442, '300-cz.m4v', '300 (cz)', 15, NULL, 'cz', NULL, 'en', '300', 'http://www.csfd.cz/film/221337-300-bitva-u-thermopyl/', '300-cz.jpg', 1, 'Film vychází ze stejnojmenného komiksu, který je dravým převyprávěním příběhu bitvy u Thermopyl, kde se spartský král Leonidas a jeho 300 věrných bojovníků utká s mnohonásobnou přesilou Peršanů pod vedením krále Xerxa. Odvážní Sparťané bojují až do poslední kapky krve, jejich odvaha a sebeoběť způsobí sjednocení Řeků, motivuje k porážce Peršanů a přispěje rozvoji demokracie na Peloponéském poloostrově. Slavný komiks ožívá za přispění nejmodernějších technologií, kombinujících živé herce a metodu modrého plátna, čímž dávný příběh odvahy opět povstává v celé své síle...(oficiální text distributora)', NULL, 2006, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1418, 'SK70/SK70-01 Medena veza -- Copper Tower.m4v', 'Medena veza', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/23839-medena-veza/', 'SK70-01 Medena veza -- Copper Tower.jpg', 31, 'Krátce po Baladě o sedmi oběšených došlo k druhému uměleckému setkání Emílie Vášáryové s nedávno zesnulým režisérem Martinem Hollým. Uprostřed nádherné tatranské přírody, daleko od nastupující normalizace, vznikl romantický příběh, který dodnes společně s volným pokračováním Orlí pírko (1971) patří mezi nejoblíbenější slovenské filmy. Tři kamarádi, Pirin, Valér a „R“, pracují na malé vysokohorské chatě. Poté, co na chatu dorazí Pirinova manželka Saška, je do té doby nerozlučné přátelství tří mužů vystaveno těžkým zkouškám. „Byl to pokus o útěk před životní realitou. Bukovčanův scénář jsem se snažil ještě víc posunout tak, aby vznikla atmosféra nostalgie po něčem krásném, neuchopitelném a nenávratném,“ vzpomínal režisér.(oficiální text distributora)', NULL, 1970, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1419, 'SK70/SK70-02 Orlie pierko -- Eagle Feather.m4v', 'Orlie pierko', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/27542-orlie-pierko/', 'SK70-02 Orlie pierko -- Eagle Feather.jpg', 32, 'Pokračování životních osudů hrdinů filmu Měděná věž, chatařů z tatranské Ztracené doliny. Po návratu z dvouletého vězení, kde se dostali pro pašování, se rozhodnou pomstít se nadporučíkovi Pardekovi.', NULL, 1971, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1427, 'SK70/SK70-09 A pobezim az na kraj sveta -- And I''ll Run to the Ends of the Earth.m4v', 'A pobezim az na kraj sveta', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/28500-a-pobezim-az-na-kraj-sveta/', 'SK70-09 A pobezim az na kraj sveta -- And I''ll Run to the Ends of the Earth.jpg', 40, 'Slovenský film podľa prózy Márie Ďuríčkovej. Príbeh zdravotne postihnutého dievčaťa žijúceho na súčasnej dedine. Od detstva zraňovaná výsmechom spolužiakov a neporozumením najbližších oveľa citlivejšie pozoruje svet okolo seba a iba opatrne nadväzuje nové priateľstvá.(oficiální text distributora)', NULL, 1979, '');
