INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1420, 'SK70/SK70-03 Keby som mal pusku -- If I Had a Gun.m4v', 'Keby som mal pusku', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/27661-keby-som-mal-pusku/', 'SK70-03 Keby som mal pusku -- If I Had a Gun.jpg', 33, 'Film, ktorý vznikol podľa literárnej predlohy Milana Ferka rozpráva o vojnových časoch z pohľadu detských hrdinov a patrí v tomto smere spolu s filmom Stanislava Barabáša Pieseň o sivom holubovi medzi najlepšie filmy slovenskej kinematografie. Na pozadí udalostí na Slovensku medzi vypuknutím Slovenského národného povstania a oslobodením sledujeme osudy malých hrdinov s ich detskými starosťami i radosťami. Stávame sa svedkami ich objavovania okolitého sveta, sexuality, komentovania ľudí a udalostí a v neposlednom rade i túžby po dospelosti, ktorú v tomto prípade paradoxne predstavuje puška. Mozaikovité rozprávanie však okrem dychtivého poznávania mladých hrdinov sprevádzajú nestále stretnutia so smrťou alebo aspoň hrozba jej zjavenia. Film Keby som mal pušku patrí do Zlatého fondu Slovenskej kinematografie a zaslúžili sa o to okrem režiséra Štefana Uhra aj kameraman Stanislav Szomolányi, hudobný skladateľ Iľja Zeljenka, strihač Max Remeň a v neposlednom rade herecké obsadenie(oficiální text distributora)', NULL, 1971, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1421, 'SK70/SK70-04 Lalie polne.m4v', 'Lalie polne', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/29320-lalie-polne/', 'SK70-04 Lalie polne.jpg', 34, 'Lyrizovaná tragikomédia je situovaná do prostredia slovenskej dediny po skončení 1. svetovej vojny. Nadľahčená filmová mozaika rozpráva o vojnových vyslúžilcoch, ktorí sa po návrate z vojny snažia dať svojmu životu zmysel, nájsť opäť šťastie a lásku. Vykorenení vojnou, zbavení domovov, poznajú títo tuláci a žobráci, žijúci zo dňa na deň, cenu slobody a kamarátstva, ale zároveň túžia aj po istote sporiadanej existencie. Jeden z najvýznamnejších filmov slovenskej kinematografie spracováva novelu Vincenta Šikulu "Nebýva na každom vŕšku hostinec".(oficiální text distributora)', NULL, 1972, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1422, 'SK70/SK70-05 Pacho, hybsky zbojnik -- Pacho, The Brigand of Hybe.m4v', 'Pacho, hybsky zbojnik', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/24866-pacho-hybsky-zbojnik/', 'SK70-05 Pacho, hybsky zbojnik -- Pacho, The Brigand of Hybe.jpg', 35, 'Slovenská komédia, ktorá rozpráva príbeh o legendárnom hybskom zbojníkovi Pachovi, sa radí k divácky najúspešnejším filmom. Pacho nie je historická postava. Je len modelom možného a vysnívaného zbojníka, ktorý okrem toho, že bohatým berie a chudobným dáva, neznáša krivdy a feudálny útlak. Častejšie ako svaly používa rozum. Vie si poradiť v každej situácii za pomoci sebe tak vlastnej šikovnosti a prešibanosti. So svojou družinou, pritom zažíva nevšedné dobrodružstvá. Rešpekt a obdiv nevzbudzuje len u mužov. Napriek tomu, že nepatrí záletníkom, ženy, nevynímajúc cisárovnú Máriu Teréziu, sa bijú o jeho priazeň...(oficiální text distributora)', NULL, 1975, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1423, 'SK70/SK70-06 Cervene vino I -- Red Wine I.m4v', 'Cervene vino I', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/27151-cervene-vino/', 'SK70-06 Cervene vino I -- Red Wine I.jpg', 36, 'Televízny film podľa románu Františka Hečka zachytáva sociálny zápas slovenskej dediny na začiatku 20. storočia. Na pomerne širokom časovom rozpätí sledujeme životné príbehy niekoľkých generácií rodiny Habdžovcov, ktorá žije v západoslovenskej vinohradníckej dedine.Najstarší syn Urban utiekol z domu, aby so svojou mladou ženou, ktorú si zobral proti vôli svojich rodičov, začal samostatne žiť a hospodáriť vo Vlčindole. Mladší brat Mikuláš uteká z domu. Urbana vo Vlčindole čakajú okrem niekoľkých chudobných spojencov aj otvorení nepriatelia. Hrozí mu dražba a jeho statočná práca vychádza nazmar. Vrcholom nešťastia je vojna... Niektorí umierajú na fronte, ďalších prekvapí choroba, jediné čo pretrváva sú ľudská láska a nenávisť. Posledné roky prvej svetovej vojny doľahli aj na Habdžovcov a prinútili najmä mladého Marka Habdžu a jeho matku, aby zastali všetky práce doma i vo vinohrade. Urban sa dostal z frontu na dovolenku iba raz. Po jeho odchode prišli prvé údery: najprv umiera ochranca Urbanovej rodiny - Slivnický a s ním zaniká aj činnosť vinohradníckeho spolku...(oficiální text distributora)', NULL, 1976, '');
