INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1425, 'SK70/SK70-07 Ruzove sny -- Pink Dreams.m4v', 'Ruzove sny', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/4146-ruzove-sny/', 'SK70-07 Ruzove sny -- Pink Dreams.jpg', 38, 'Peotický obraz lásky dedinského poštára Jakuba a mladučkej cigánky Jolanky. Sú to hrdinovia, ktorí vstupujú do života s omylmi a kotrmelcami, s humorom a poéziou ich mladosti. Jakub okrem listov a telegramov prináša ľuďom okolo seba i radosť. Je hravý a Jolanka cieľavedomejšia. Je nespokojná, chcela by zmeniť svoj život. Jakub si vie vymýšľať a dokáže presvedčiť, že všetky túžby a sny sú splniteľné. Jakub a Jolanka sa k sebe približujú i vzďaľujú navzájom, nachádzajú sa, aby sa opäť jeden druhému stratili... Skutočný život ich naučí chápať, kde sú hranice sna, túžby a skutočnosti. Lyrická tragikomédia RUŽOVÉ SNY o láske dedinského poštára Jakuba a Rómky Jolanky si podmaňuje divákov svojou poetickou atmosférou a jemným humorom už od roku 1976, kedy ju režisér Dušan Hanák nakrútil podľa svojho a Dušekovho scenára. "Rómeo a Júlia" na dedine bez tragického vyvrcholenia, pričom znepriatelené veronské rody nahradila nevraživosť domácich vidieckych komunít - "bielych" a Rómov, ktorí všemožne komplikujú mladým život. Jolanka sa chce vymaniť zo svojho sveta a žiť usporiadaným spôsobom ako väčšina, Jakub zase sníva o voľnosti a hravosti. Hoci túžia každý po inom, túžia zároveň intenzívne po sebe. Prežijú spolu krásne chvíle, ale aj okamihy trápenia. Ich pokus o spoločný život v meste nakoniec stroskotá. Jolanka sa vracia domov medzi svojich. Cesty milencov sa síce rozchádzajú, ale ich ružové sny zostávajú. Jeden z najúspešnejších filmov slovenskej kinematografie, ovenčený množstvom domácich i zahraničných cien, predstavil dvoch nadaných nehercov - Juraja Nvotu /Jakub/ a Ivu Bittovú /Jolanka/. Lyrické milostné výjavy, rázovitý mikrosvet dedinských figúrok, autentický obraz života v rómskej osade, vizuálne zachytený kameramanom Dodom Šimončičom a hudobne vyjadrený Petrom Hapkom, dotvárajú hravé, láskavé a vtipné rozprávanie o jednej nežnej láske.(oficiální text distributora)', NULL, 1976, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1426, 'SK70/SK70-08 Vitaz -- The Winner.m4v', 'Vitaz', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/27151-cervene-vino/', 'SK70-08 Vitaz -- The Winner.jpg', 39, 'Televízny film podľa románu Františka Hečka zachytáva sociálny zápas slovenskej dediny na začiatku 20. storočia. Na pomerne širokom časovom rozpätí sledujeme životné príbehy niekoľkých generácií rodiny Habdžovcov, ktorá žije v západoslovenskej vinohradníckej dedine.Najstarší syn Urban utiekol z domu, aby so svojou mladou ženou, ktorú si zobral proti vôli svojich rodičov, začal samostatne žiť a hospodáriť vo Vlčindole. Mladší brat Mikuláš uteká z domu. Urbana vo Vlčindole čakajú okrem niekoľkých chudobných spojencov aj otvorení nepriatelia. Hrozí mu dražba a jeho statočná práca vychádza nazmar. Vrcholom nešťastia je vojna... Niektorí umierajú na fronte, ďalších prekvapí choroba, jediné čo pretrváva sú ľudská láska a nenávisť. Posledné roky prvej svetovej vojny doľahli aj na Habdžovcov a prinútili najmä mladého Marka Habdžu a jeho matku, aby zastali všetky práce doma i vo vinohrade. Urban sa dostal z frontu na dovolenku iba raz. Po jeho odchode prišli prvé údery: najprv umiera ochranca Urbanovej rodiny - Slivnický a s ním zaniká aj činnosť vinohradníckeho spolku...(oficiální text distributora)', NULL, 1976, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1428, 'SK70/SK70-10 Postav dom, zasad strom -- Build a House, Plant a Tree.m4v', 'Postav dom, zasad strom', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/4728-postav-dom-zasad-strom/', 'SK70-10 Postav dom, zasad strom -- Build a House, Plant a Tree.jpg', 41, 'Postavím dom, zasadím strom, vykopem studňu, splodím syna... Tak ako hociktorý iný chlap, tak si húdol aj Matúš (Pavel Nový), čudák, podivín. Mladý muž prichádza do hádam najzastrčenejšej slovenskej dediny. Získa kamarátov, bojuje o svoju lásku - krásnu Helenu. A tak Matúš stavia dom, ktorý má zmeniť jeho život...(oficiální text distributora)', NULL, 1979, '');
