INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1429, 'SK80/SK80-01 Fontana pre Zuzana -- The Fountain For Suzanne.m4v', 'Fontana pre Zuzanu', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/29038-fontana-pre-zuzanu/', 'SK80-01 Fontana pre Zuzana -- The Fountain For Suzanne.jpg', 41, 'Hrdiny příběhu je parta patnáctiletých školáků, kteří právě ukončili základní školu a prožívají své poslední prázdniny před tím, než se rozejdou na různé střední školy. Středobodem všeho je opuštěná vyschlá fontána uprostřed sídliště, u níž se všichni scházejí. Hvězdami party, která se tu schází, jsou dvě hezké dívky, Bela a Zuza. Film však sleduje nejen jejich osudy, problémy a pocity a chování v různých životních situacích, jejich lásky a nelásky, omyly a usmiřování. Mozaiku doplňují postavy jejich rodičů, sourozenců i dalších dospělých, mezi nimiž je výraznou postavou zejména starý pan Domorák, který mladé neustále kritizuje. Z party je největší pozornost věnována mladému budoucímu automechanikovi a muzikantu Olinovi, který se z lásky k Zuze rozhodne starou fontánu opravit.(oficiální text distributora)', NULL, 1985, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1430, 'SK80/SK80-02 Chodnik cez Dunaj -- A Path Across The Danube.m4v', 'Chodnik cez Dunaj', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/27112-chodnik-cez-dunaj/', 'SK80-02 Chodnik cez Dunaj -- A Path Across The Danube.jpg', 42, 'Tragický príbeh o osudoch mladých priateľov, Slováka Viktora Lesu (Roman Luknár) a českého žida Ticháčka (Vladimír Hajdu), po vzniku vojnovej Slovenskej republiky a Protektorátu Čechy a Morava. Najprv utekajú pred gestapom preto, lebo zdržali nemeckú vojenskú poštovú zásielku, za čo ich obvinili zo sabotáže. Napokon ich k úteku cez hranice donúti aj to, že v sebaobrane zabili tajného policajta. Počas svojej cesty sa stretávajú s ľuďmi rôznych charakterov, od ktorých závisí ich ďalší život... Snímka získala na 30. Medzinárodnom festivale televíznych filmov v Monte Carle (1990) Cenu Červeného kríža Monegasque osobitnú cenu monackého princa (Prix de la Croix Rouge Monegasque) za film bojujúci za mier, myšlienky humanizmu, tolerancie a znášanlivosti. Film vznikol podľa námetu Vladimíra Körnera, na scenári okrem režiséra a spisovateľa spolupracoval aj Marián Puobiš. Pre kvalitu filmu je rozhodujúce, že tvorcovia využili emocionálnu silu a napätie dramatickej záchrany vlastného života bez toho, aby sa vzdali ucelenej filozofie ľudskej existencie. Hoci je v snímke veľa akcie a napínavých situácií, kde ide obom postavám o život, najpodstatnejším je shakespearovsky tragický, baladický obraz šialenej doby. Mimoriadne vydarený film vďačí za mnohé aj obom ústredným hrdinom. U Viktora dominuje bezstarostnosť a ľahkomyseľnosť, ktoré so sebou takmer automaticky prinášajú utrpenie a bolesť pre iných. Ticháček sa zase vyznačuje úzkostlivou opatrnosťou, takmer úplná strata vlastnej hrdosti a ceny.(oficiální text distributora)', NULL, 1989, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1431, 'SK80/SK80-03 Iba Den -- Only a Day.m4v', 'Iba Den', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/23859-iba-den/', 'SK80-03 Iba Den -- Only a Day.jpg', 43, 'Celovečerný poviedkový film Iba deň z roku 1988 trojice debutujúcich režisérov Michala Ruttkaya, Kvetoslava Hečku a Vladimíra Štrica spája scenár Štefana Uhríka rozprávajúci o osudoch bývalých členov bigbítovej kapely, ktorú pred dvadciatimi rokmi zakázali. Tri poviedky Bicie, Basa a Gitara predstavujú postupne troch kamarátov, ktorí vymenili hudbu za obyčajné zamestnania a snažia sa v živote uspieť každý podľa svojho naturelu. Laco, Pipco a Stano sa dohodnú, že navštívia svojho priateľa - štvrtého člena skupiny, ktorý je hospitalizovaný v psychiatrickej liečebni a zahrajú si v starom zložení. Nemilé prekvapenie, ktoré ich na konci ich snahy čaka je len potvrdením, že "nádherné šesťdesiate roky", na ktoré tak radi spomínajú sú jednoznačne preč a s nimi aj ich hodnoty, idoly a ideály. V čase svojho vzniku bol film Iba deň hodnotený ako autentická výpoveď o životnom pocite generácie, ktorá dospievala v šesťdesiatych rokoch a dozrievala počas normalizácie. Okrem troch rôznych režisérskych pohľadov film divákom ponúka tri výrazné herecké výkony hlavných predstaviteľov: Pavla Zedníčka, Miroslava Donutila a Jaroslava Filipa. Neodmysliteľnou súčasťou filmu je samozrejme hudba. Michala Pavlíčka dopľňajú v čase vzniku filmu mladé kapely Bez ladu a skladu a Zona A.(oficiální text distributora)', NULL, 1988, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1432, 'SK80/SK80-04 Ina laska -- Another Love.m4v', 'Ina laska', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/24638-ina-laska/', 'SK80-04 Ina laska -- Another Love.jpg', 44, 'Mladého medika podmienečne odsúdia a vyhodia zo školy pre nedovolenú interrupciu. Vyberie sa do dediny za dievčaťom, ktorému spravil tento zákrok. Túži sa zbaviť vlastného pocitu viny, že mu išlo iba o peniaze, ktoré si týmto skutkom zarobil. Svoj čin odôvodňuje sám pred sebou tým, že chcel iba pomôcť človeku v núdzi. Rozhodne sa zostať v dedine a postupne zisťuje, že je to skorumpované prostredie, v ktorom je dobro prepletené so zlom. Mladý muž postupne odhaľuje rôzne špinavosti. Rozkrádanie, výnosné kšefty a podvody sú na dennom poriadku, no on sa ich odmieta zúčastňovať.(oficiální text distributora)', NULL, 1985, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1433, 'SK80/SK80-05 Ja milujem, ty milujes -- I Love, You Love.m4v', 'Ja milujem, ty milujes', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/4139-ja-milujem-ty-milujes/', 'SK80-05 Ja milujem, ty milujes -- I Love, You Love.jpg', 45, 'Hanákove filmy sú apoteózou víťazstva človeka nad sebou samým, víťazstva mravnosti a citu nad životnými podmienkami. Jeho postavy hľadajú v sebe silu prekonať to, čo ich obmedzuje, čo bráni splneniu ich túžob, snov a citov. Tento film, zachytávajúci vnútornú ľudskosť postáv z okraja spoločnosti, sa do kín dostal s 9-ročným oneskorením kvôli absurdnému direktívnemu rozhodnutiu o údajnej "ošklivosti" všetkých postáv a pochmúrnosti ladenia. Starý mládenec Pišta premáha handicap svojho nevábneho vzhľadu alkoholom. Verí priateľstvu s kolegom z vlakovej pošty, svetáckym Vincom. Ten však nešťastne zahynie a osamelý muž získa vysnenú priazeň Vincovej milenky Viery. Osudy neobvyklých hrdinov v neutešených prostrediach stanice, slobodárne a na dedine zachytil Hanák s pokorou, povyšujúc ich na umelecký obraz sveta. Pišta hľadá pevný bod v láske - k matke, k priateľovi Vincovi, k Viere. Vo svojom vnútri je schopný lásku dávať i prijímať, navonok však prejavy jeho citu vyznievajú bezradne, neohrabane a smiešne, kvôli čomu sa pokladá za "nulového človeka". Tvorcovia však presvedčivo ukazujú, že človek schopný citu nemôže byť "nulový", len strata viery a ideálov znamená stratu ľudskosti. Postavu "tuláka-filozofa", ktorý životné hodnoty nedeklamuje a neilustruje, ale ich prežíva a intuitívne vyjadruje svojím konaním, usmerňujúc svojou voľnosťou a slobodou postoje a pocity hrdinov v ich zdanlivo bezvýchodiskovej izolácii, herecky vytvára Juraj Nvota ako typ, stelesňujúci aj v iných Hanákových filoch režisérovo smerovanie do vnútra človeka ako do stredu vesmíru. Hlavné mužské úlohy stvárnili juhoslovanský režisér a scenárista M.Jelič a poľský herec a divadelný režisér R. Klosowski, v netradičnej role zaujme I. Janžurová, výrazný je scenáristický podiel spisovateľa Dušana Dušeka.(oficiální text distributora)', NULL, 1980, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1561, '201208/X-men 3 Posledni vzdor -- The Last Stand en.m4v', 'X-men 3 The Last Stand', 4, 28, 'en', 'cz', 'en', 'X-men: The Last Stand', 'http://www.imdb.com/title/tt0376994/', 'X-men 3 Posledni vzdor -- The Last Stand en.jpg', 3, 'Directed by Brett Ratner.  With Patrick Stewart, Hugh Jackman, Halle Berry, Famke Janssen. When a cure is found to treat mutations, lines are drawn amongst the X-Men, led by Professor Charles Xavier, and the Brotherhood, a band of powerful mutants organized under Xavier''s former ally, Magneto.', NULL, 2006, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1434, 'SK80/SK80-06 Juzna posta -- The Southern Mail.m4v', 'Juzna posta', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/26089-juzna-posta/', 'SK80-06 Juzna posta -- The Southern Mail.jpg', 46, 'Južné Slovensko tesne po 2. svetovej vojne bolo plné nepokoja, sociálnych, politických i národnostných rozporov. A práve v mestečku Palánok všetky udalosti, ktoré sa tu odohrávajú, intenzívne prežíva päťročný Janko (PETER ZEMAN), syn colníka Pavla Jurkoviča (JIŘÍ BARTOŠKA) a starostlivej matky (MAGDA VÁŠÁRYOVÁ). Pre senzitívneho chlapca je ospalé mestečko plné dobrodružstiev, radostného očakávania, ale aj tragédií. A v jeho spomienkach zostali všetky rovnako živé a dôležité, hoci má už o vyše 35 rokov viac a je z neho úspešný spisovateľ... Ladislav Ballek sa v 70. rokoch minulého storočia vrátil k tematike malomesta v zbierke autobiografických poviedok Južná pošta a v románoch Pomocník a Agáty. Ich príbehy sa odohrávajú vo fiktívnom mestečku Palánok (Šahy) a Ballek zachytáva osudy ľudí i mesta uprostred oslobodzovania a povojnových udalostí. Tvorcom sa podarilo dodať príbehu podobu zvláštnej kombinácie ilúzie a reality, sna a skutočnosti prostredníctvom toho, že všetko transponujú optikou malého chlapca. Dramatický stavba udalostí nie je v tomto prípade hierarchizovaná, rovnakú vážnosť má chlapcova starosť o poštového holuba, ako existenčné ohrozenie viacerých postáv spojené spletitou vojnovou situáciou. Aj v ďalšej Párnického tvorbe zostala charakteristická kombinácia lyrickej baladickosti s naturalizmom a expresionizmom.(oficiální text distributora)', NULL, 1987, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1435, 'SK80/SK80-07 Nocni jazdci -- Night Riders.m4v', 'Nocni jazdci', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/23842-nocni-jazdci/', 'SK80-07 Nocni jazdci -- Night Riders.jpg', 47, 'Oproti sebe stoja dvaja zásadoví, nekompromisní muži. Jeden má za sebou obyvateľov chudobnej dediny, druhý zákon. Konflikt čaká na vyvrcholenie v záverečnej prestrelke. Výstrely z pušiek nakoniec padnú, no nestane sa tak na divokom západe ale na slovensko-poľskom pohraničí. Michal Dočolomanský v úlohe pašeráka koní a Radoslav Brzobohatý ako pražský colník sa v Hollého Nočných jazdcoch stretli v takmer westernovej konfrontácii, zasadenej do neistých a často dramatických rokov novozaloženej Československej republiky.(oficiální text distributora)', NULL, 1981, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1436, 'SK80/SK80-08 Pasla kone na betone -- She Kept Crying For The Moon.m4v', 'Pasla kone na betone', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/29191-pasla-kone-na-betone/', 'SK80-08 Pasla kone na betone -- She Kept Crying For The Moon.jpg', 48, 'Děj slovenského filmu se odehrává v zapadlé vesnici a líčí život činorodé matky a její dospívající dcery poznamenaný trpkými zkušenostmi s mužskou bezohledností. Zápletka vzdáleně připomíná Feničův debut Džusový román: sedmnáctiletá Pavla (v podání Veroniky Jeníkové) - pocházející z dávné nemanželské známosti - otěhotní s nezodpovědným vojákem z Čech, který se musí ženit s jinou dívkou...(oficiální text distributora)', NULL, 1982, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1437, 'SK80/SK80-09 Pomocnik -- The Assistant.m4v', 'Pomocnik', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/26910-pomocnik/', 'SK80-09 Pomocnik -- The Assistant.jpg', 49, 'Filmový prepis úspešného románu L.Balleka Pomocník. Dej filmu je situovaný do prostredia malého mestečka na južnom Slovensku, kam v raných povojnových rokoch prichádza mäsiar Štefan Riečan, odbojár a statočný človek, aby po skompromitovanom maďarskom živnostníkovi prevzal jeho výnosný obchod - a spolu s ním aj jeho bývalého pomocníka Volenta Lančariča. Na Riečanovom osude zobrazuje film vnútornú drámu človeka, ktorý aj za cenu samoty, hlbokého poníženia a stroskotania osobných vzťahov sa nespreneverilsvojim zásadám. Bohatý dej filmu je ojedinelou výpoveďou o zložitom období povojnovej rekonštrukcie našej krajiny. Príbeh rodiny Riečanovcov, ktorá sa po druhej svetovej vojne presťahovala na juh Slovenska, kde po maďarskom utečencovi získala mäsiarstvo. Po bývalom majiteľovi zdedila aj sebavedomého pomocníka bez pevnejších morálnych zásad, Valenta Lančariča. Jeho výpomoc prinesie rodine bohatstvo, no v konečnom dôsledku spôsobí jej rozpad. Režisér Zoro Záhon spojil vo svojom najúspešnejšom filme silný dramatický príbeh s výbornými hereckými výkonmi, medzi ktorými vynikol najmä predstaviteľ pomocníka Gábor Koncz.(oficiální text distributora)', NULL, 1982, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1438, 'SK80/SK80-10 Sladke starosti -- Sweet troubles.m4v', 'Sladke starosti', 18, 0, 'sk', '  ', 'sk', NULL, 'http://www.csfd.cz/film/4242-sladke-starosti/', 'SK80-10 Sladke starosti -- Sweet troubles.jpg', 50, 'Komédia o poctivom a šikovnom cukrárovi Šimonovi. Skromný starý mládenec sa v praktickom živote stáva ľahkou obeťou svojho šéfa, matky a zástupu vydajachtivých žien. V príbehu sa objavujú osvedčené postupy takmer všetkých dôležitých období filmovej komédie. Počnúc lumiérovskou etudou so záhradnou hadicou cez melancholického tuláka až po postrach okolia - Jacquesa Tatiho. Jurajovi Herzovi sa tak podarilo výrazne obohatiť nielen kolibskú dramaturgiu 80. rokov, ale najmä skromnú tradíciu slovenskej komédie.(oficiální text distributora)', NULL, 1984, '');
