INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1443, '300-en.m4v', '300', 15, NULL, 'en', 'cz', 'en', '300', 'http://www.csfd.cz/film/221337-300-bitva-u-thermopyl/', '300-en.jpg', 2, 'Film vychází ze stejnojmenného komiksu, který je dravým převyprávěním příběhu bitvy u Thermopyl, kde se spartský král Leonidas a jeho 300 věrných bojovníků utká s mnohonásobnou přesilou Peršanů pod vedením krále Xerxa. Odvážní Sparťané bojují až do poslední kapky krve, jejich odvaha a sebeoběť způsobí sjednocení Řeků, motivuje k porážce Peršanů a přispěje rozvoji demokracie na Peloponéském poloostrově. Slavný komiks ožívá za přispění nejmodernějších technologií, kombinujících živé herce a metodu modrého plátna, čímž dávný příběh odvahy opět povstává v celé své síle...(oficiální text distributora)', NULL, 2006, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1444, 'Batman/Batman 1 Begins-cz.m4v', 'Batman zacina', 15, 27, 'cz', NULL, 'en', 'Batman Begins', 'http://www.csfd.cz/film/136224-batman-zacina/', 'Batman 1 Begins-cz.jpg', 1, 'Film popisuje počátky legendy o tajemném Batmanovi a zjevení Temného rytíře coby síly dobra v Gotham City. Zklamaný dědic továren Bruce Wayne cestuje těsně po úmrtí svých rodičů po světě a hledá způsob, jakým by bojoval s nespravedlností a jak by obrátil strach proti těm, kteří okrádají bázlivé. Po čase se vrací zpět do Gotham City a vzápětí vypouští do světa své druhé já, maskovaného bojovníka Batmana, který používá svoji sílu, inteligenci a řadu svých technických vymožeností k boji proti zlověstným silám, které ohrožují město...(oficiální text distributora)', NULL, 2005, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1445, 'Batman/Batman 1 Begins-en.m4v', 'Batman Begins', 15, 27, 'en', 'cz', 'en', NULL, 'http://www.imdb.com/title/tt0372784/', 'Batman 1 Begins-en.jpg', 10, 'Directed by Christopher Nolan.  With Christian Bale, Michael Caine, Ken Watanabe, Liam Neeson. After training with his mentor, Batman begins his war on crime to free the crime-ridden Gotham City from corruption that the Scarecrow and the League of Shadows have cast upon it.', NULL, 2005, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1446, 'Batman/Batman 2 The Dark Knight-cz.m4v', 'Temny rytir', 15, 27, 'cz', NULL, 'en', 'The Dark Knight', 'http://www.csfd.cz/film/223734-temny-rytir/', 'Batman 2 The Dark Knight-cz.jpg', 2, 'Christian Bale se pod režijní taktovkou Christophera Nolana vrací v roli Bruce Waynea, který v kostýmu netopýřího muže Batmana pokračuje v boji se zločinem ve městě Gotham. A jeho nejnebezpečnějším protivníkem je král zločinců Joker (Heath Ledger). Akční dobrodružná fantasy v režii Christophera Nolana navazuje na film Batman začíná a Christian Bale si v ní zopakoval roli Batmana/Bruce Waynea v nekončícím boji se zločinem. Wayne v kostýmu netopýřího muže jde spolu s poručíkem Jamesem Gordonem (Gary Oldman) z Gothamské policie po králi zločinců Jokerovi známého podle symbolu - hrací karty s žolíkem - kterou zanechává na místech činu. Vtipálek s prořízlou tváří (Heath Ledger) má tentokrát v plánu Batmanovi dokázat, že slova Gotham a mír nikdy nebudou stát v jedné větě vedle sebe. Na uvolněné místo po bývalém králi místní mafie usedá Sal Maroni (Eric Roberts) a přebírá vládu nad většinou gangů v Gothamu. Na scéně se rovněž objevuje nový státní návladní Harvey Dent (Aaron Eckhart), který by rozdanými kartami (i s žolíkem v balíčku) mohl pořádně zamíchat...(oficiální text distributora)', NULL, 2008, '');
