INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1454, 'Lord of the Rings/LOTR-1-FOTR-EXT-CZ-A.m4v', 'Spolecenstvo prstenu, ext, 1', 4, 2, 'cz', NULL, 'en', 'Fellowship of The Ring, ext, 1', 'http://www.csfd.cz/film/4711-pan-prstenu-spolecenstvo-prstenu/', 'LOTR-1-FOTR-EXT-CZ-A.jpg', 1, 'Hlavním hrdinou je hobit Frodo Pytlík, který se ve své vlastní domovině zvané Středozemě dostal do samotného centra bitvy mytických proporcí. Bude muset během svého putování prokázat, že moc přátelství a individuální odvahy dokáže úspěšně čelit i těm nejničivějším silám temnoty. Frodova dobrodružství odstartuje dar jeho příbuzného Bilba. Od něj získá magický prsten, který Froda postaví do soboje se silami zla reprezentovanými krutovládcem Sauronem. Ten touží za každou cenu získat prsten, protože je pro něj zdrojem neomezené moci, jedině s jeho pomocí se mu může podařit zotročit lid Středozemě a stát se jeho neomezeným pánem. Ve snaze čelit Sauronovým temným plánům, se musí Frodo spojit se několika dalšími bytostmi. Vytvoří s nimi takzvané Společenstvo složené z jeho přátel, čaroděje, elfa, trpaslíka a dalších pomocníků, se kterými se vydá na cestu na jejímž konci doufají, že se jim podaří prsten jednou provždy zničit. Jejich cesta však prokáže, že nejenom magické prsteny, ale také sounáležitost všech živých bytostí mohou dokázat netušené věci. Musí však čelit nejen nebezpečím, která na ně číhají na územích ovládaných Sauronem, ale i rozkladné moci samotného prstenu.(oficiální text distributora)', NULL, 2001, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2055, '201305/SHREK_THE_THIRD sk.m4v', 'Shrek Treti SK', 3, 33, 'sk', NULL, 'en', 'Shrek The Third', 'http://www.csfd.cz/film/205738-shrek-treti/', 'SHREK_THE_THIRD sk.jpg', 13, 'Za normálních okolností by krásní, mladí a zelení manželé Shrekovi po odstranění zákeřné Kmotřičky víly a jejího nemožného syna Krasoně ve druhém pokračování animované komedie Shrek žili nerušeně a hlavně šťastně až do smrti. Vzhledem k tomu, že se stali protagonisty nejúspěšnějšího animovaného filmu všech dob, nemají na pohodu nárok. Dobrodružství, která si pro ně kreativní scenáristé vymysleli tentokrát, jsou ještě vypečenější, než ta, která oba zlobříci prožili doposud. Posuďte sami: Král Harold má nakahánku, a je proto jasné, že musí vládu v království Za sedmero horami někomu předat. Ještě jasnější je, že nástupcem trůnu se má stát manžel jeho nejjasnější dcery Fiony. Shrek se ale vyhlídkám na kralování brání tak intenzívně, že se raději rozhodne přemluvit Fionina bratrance, teenagera Artuše, aby si královskou korunu nasadil on. Nejdřív ho ale bude muset najít a udělat z něj chlapa. Zatímco Shrek, Oslík a Kocour v botách se vydávají na poměrně dobrodružnou expedici, osiřelá Fiona má úplně jiné starosti. Absence mužského elementu v království se rozhodne zneužít již jednou odpálkovaný Krasoň, který se chce za pomoci záporných pohádkových postav zmocnit vlády v zemi Za sedmero horami násilím. Fiona je ale především Zlobryně a až na druhém místě princezna, a proto se Krasoňovi a jeho zlé armádě postaví. Navíc není sama, v první linii s ní kromě královny Lilien kráčí i svůdné komando princezen (Sněhurka, Popelka, Růženka a další), které jsou odhodlány všechny své přednosti využít boji. Ve Shrekovi Třetím bude prostě pořádně veselo, a to jsme tu ještě nezmínili draslíky, roztomilé potomky Oslíka a Dračice, a jedno překvapení, které Fiona přede všemi tají. Shrek se vždycky opíral o revoluční a naprosto dokonalou animaci a hvězdné hlasové obsazení. Obojí mu zůstalo i ve třetím pokračování, navíc se může pochlubit mimořádně zajímavým hereckým přírůstkem v podání popové ikony současnosti Justina Timberlakea, kterého v české verzi nahradí podobně slavný Vojtěch Kotek. Režie se ujal Chris Miller, který psal ke dvěma předchozím dílům scénáře, a Raman Hui, supervizor animace jedničky i dvojky.(oficiální text distributora)', NULL, 2007, '');
