INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1458, 'Lord of the Rings/LOTR-2-TTT-EXT-CZ-A.m4v', 'Dve veze, ext, 1', 4, 2, 'cz', NULL, 'en', 'The Two Towers, ext, 1', 'http://www.csfd.cz/film/4713-pan-prstenu-dve-veze/', 'LOTR-2-TTT-EXT-CZ-A.jpg', 3, 'Druhý díl filmové ságy o nebezpečném putování jehož cílem má být zničení prokletého prstenu moci začíná tam kde první skončil. Opět je hlavním hrdinou hobit Frodo. Ten se získal od svého příbuzného Bilba magický prsten po kterém touží temný Sauron a tak se dostal do samotného centra souboje mezi silami světla a temnot. Frodo jako vůdce společenství složeného z jeho přátel, kouzelníka, elfa, trpaslíka a dalších pomocníků pokračuje na cestě na vrchol sopky, aby zde prsten jednou provždy zničil. První díl byl nominován na Zlatý glóbus a získal cenu AFI. Díky zájmu diváků překročil zisk 350 milionů dolarů a především přesvědčil příznivce knihy, že tento dnes již klasický příběh lze úspěšně zfilmovat. V nezměněné sestavě vám teď tvůrci předkládají druhý díl fantaskního putování.(oficiální text distributora)', NULL, 2002, '');
