INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1468, 'Detske/Cars.m4v', 'Cars', 3, NULL, 'cz', NULL, 'en', NULL, 'http://www.csfd.cz/film/32954-auta/', 'Cars.jpg', 10, 'Když se začínající, avšak velmi ambiciózní závodní auto Blesk McQueen během cesty na velkolepý šampionát Zlatý píst neočekávaně zatoulá do ospalé Kardanové lhoty u Silnice 66, naučí se, že opravdová radost ze života se neskrývá za cílovou rovinkou, ale na cestě k ní. Auta ze lhoty, s nimiž se v průběhu svého putování napříč Amerikou seznámí, mu ukáží, že na světě existují i důležitější věci než trofeje, sláva a sponzoři.(oficiální text distributora)', NULL, 2006, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1469, 'Detske/Madagascar.m4v', 'Madagascar', 3, 1001, 'cz', '  ', 'en', '', 'http://www.csfd.cz/film/117282-madagaskar/', 'Madagascar.jpg', 1, 'Nový animovaný snímek studia Dreamworks vypráví o čtveřici zvířecích celebrit z Central Park ZOO v New Yorku. Nejlepší přátelé - lev (Ben Stiller), zebra (Chris Rock), žirafa (David Schwimmer) a těhotná hrošice (Jada Pinkett-Smith) - si užívají péče ošetřovatelů do té doby, než jeden z nich zmizí. Zbytek se ho vydává hledat, ale během útěku ze zoologické zahrady jsou polapeni a deportováni do míst, jaká nikdo z nich neočekával - do "domovské" Afriky. Zvířátka se tak musí vypořádat se skutečným životem v přírodě, s problémy, se kterými se během svého života zatím nepotýkala (shánění potravy). Naštěstí nejsou na Madagascaru sami, potkají se tu například s lemurem, který si libuje v pořádání opravdu hlučných rave parties. Avšak ne každý je vítá s otevřenou náručí.(t00x)', NULL, 2005, '');
