INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1480, 'Pirati/Pirati z Karibiku - Na vlnach podivna cz.m4v', 'Na vlnach podivna', 15, 26, 'cz', NULL, 'en', 'Pirates of the Caribbean: On Stranger Tides', 'http://www.csfd.cz/film/252716-pirati-z-karibiku-na-vlnach-podivna/', 'Pirati z Karibiku - Na vlnach podivna cz.jpg', 8, 'Johnny Depp se vrací ke své ikonické roli kapitána Jacka Sparrowa v akčním příběhu plném pravdy, zrady, mládí a odchodů. Když se Jack setká se ženou ze své minulosti (Penelope Cruz), není si jistý, zda jde o lásku - nebo zda tato žena jen „hraje na city“ a využívá ho, aby našla legendární Pramen mládí. Když ho donutí, aby vstoupil na palubu Queen Anne’s Revenge, lodi obávaného piráta Černovouse (Ian McShane), Jack se ocitá v nečekaném dobrodružství, kde neví, koho se obávat více: Černovouse nebo ženy ze své minulosti. Film bude uveden v českých kinech ve 3D s dabingem, ve 2D s dabingem i s titulky.(oficiální text distributora)', NULL, 2011, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1481, 'Pirati/Pirati z Karibiku - Na vlnach podivna en.m4v', 'On Stranger Tides', 15, 26, 'en', 'cz', 'en', '', 'http://www.imdb.com/title/tt1298650/', 'Pirati z Karibiku - Na vlnach podivna en.jpg', 4, 'Directed by Rob Marshall. With Johnny Depp, Pen&#xE9;lope Cruz, Ian McShane, Geoffrey Rush. Jack Sparrow and Barbossa embark on a quest to find the elusive fountain of youth, only to discover that Blackbeard and his daughter are after it too.', NULL, 2011, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1482, 'Pirati/Pirati z Karibiku - Prokleti cerne perly cz.m4v', 'Prokleti cerne perly', 15, 26, 'cz', NULL, 'en', 'Pirates of the Caribbean: The Curse of the Black Pearl', 'http://www.csfd.cz/film/43513-pirati-z-karibiku-prokleti-cerne-perly/', 'Pirati z Karibiku - Prokleti cerne perly cz.jpg', 5, 'Třpytivé vody Karibiku jsou pro darebáckého, ale šarmantního kapitána Jacka Sparrowa nevyčerpatelným zdrojem tajemných a vzrušujících dobrodružství. Jeho idylický život se však razantně změní ve chvíli, kdy se lstivý kapitán Barbossa zmocní jeho lodi Černá perla a napadne město Port Royal, odkud unese guvernérovu okouzlující dceru Elizabeth Swann. Její kamarád z dětství Will Turner se spolu s Jackem vydává na palubě nejrychlejší lodi britské flotily na odvážnou výpravu za záchranou Elizabeth a ukradené Černé perly. V patách je jim Elizabethin snoubenec, sympatický a ctižádostivý Commodore Norrington. Will ale netuší, že je Barbossa i s celou svou pirátskou posádkou pod vlivem prokletí, které jej odsuzuje k věčnému životu ve světě nemrtvých a každou noc jej proměňuje v živého kostlivce. Kletbu může zlomit pouze vrácení ukradené části prokletého pokladu na jeho původní místo. Posádky obou lodí se vydávají vstříc vzrušujícímu střetnutí s Barbossou na tajemný ostrov Isla de Muerta. Když pak pozvednou své meče k souboji s obávanými piráty Karibiku, je v sázce Jackova pomsta, Černá perla, zapomenutý poklad, zlomení kletby nad Barbossou a jeho posádkou, osud britské námořní flotily a v neposlední řadě také životy našich statečných hrdinů.(oficiální text distributora)', NULL, 2003, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1483, 'Pirati/Pirati z Karibiku - Prokleti cerne perly en.m4v', 'The Curse of the Black Pearl', 15, 26, 'en', 'cz', 'en', '', 'http://www.imdb.com/title/tt0325980/', 'Pirati z Karibiku - Prokleti cerne perly en.jpg', 1, 'Directed by Gore Verbinski.  With Johnny Depp, Geoffrey Rush, Orlando Bloom, Keira Knightley. Blacksmith Will Turner teams up with eccentric pirate "Captain" Jack Sparrow to save his love, the governor''s daughter, from Jack''s former pirate allies, who are now undead.', NULL, 2003, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1484, 'Pirati/Pirati z Karibiku - Truhlica mrtveho muze cz.m4v', 'Truhla mrtveho muze', 15, 26, 'cz', NULL, 'en', 'Pirates of the Caribbean: Dead Man''s Chest', 'http://www.csfd.cz/film/194904-pirati-z-karibiku-truhla-mrtveho-muze/', 'Pirati z Karibiku - Truhlica mrtveho muze cz.jpg', 6, 'Kapitán Jack Sparrow (Johnny Depp) zistí, že má krvavú dlžobu u Daveyho Jonesa, kapitána lode duchov nazvanej Lietajúci Holanďan. Ako beží čas, Jack musí nájsť spôsob ako sa zbaviť dlžoby, inač skončí prekliaty večným prekliatím. A akoby toho nebolo dosť, Jackove problémy vedú k stroskotaniu svadby Willa Turnera s Elizabeth Swannovou, ktorého Jack nahovorí aby sa k nemu pripojil na jeho najbližšej výprave.(sliver)', NULL, 2006, '');
