INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1488, '201207/21 gramov -- 21 grams.m4v', '21 gramov', 15, 0, 'cz', NULL, 'en', '21 grams', 'http://www.csfd.cz/film/43073-21-gramu/', '21 gramov -- 21 grams.jpg', NULL, '21 GRAMŮ je druhý celovečerní film režiséra Alejandra Gonzáleze Iñárritu, nominovaného na cenu akademie za film Amores perros - Láska je kurva. Je to příběh naděje a lidství, nezlomnosti i přežití. V okamžiku smrti, a nezáleží na tom, zda se jí bojíte či nikoliv, se hmotnost vašeho těla sníží o 21 gramů. Je to lidská duše, co těchto 21 gramů tvoří? Jsou ti, kteří nás přežijí, o tuto hmotnost těžší? Hlavní role ve filmu 21 gramů ztvárňují Sean Penn (tři nominace na Oscara) a držitelé cen Benicio del Toro a Naomi Wattsová. Všem hercům byly uděleny ceny při premiéře filmu na Mezinárodním filmovém festivalu v Benátkách 2003, kde Sean Penn získal ocenění za nejlepší mužský herecký výkon a Benicio del Toro a Naomi Wattsová (Mulholland Drive) si odnesli ceny publika za nejlepší mužský, resp. ženský herecký výkon. Univerzitní profesor Paul Rivers (Sean Penn) a jeho manželka Mary (Charlotte Gainsbourgová) nacházejí svůj vztah na nejisté hranici mezi životem a smrtí. On je smrtelně nemocný a čeká na transplantaci srdce, zatímco ona se snaží díky umělému oplodnění počít jeho vlastní dítě. Cristina Pecková (Naomi Wattsová), která od doby svého neklidného mládí již dozrála, je milovanou starší sestrou Claudie (Clea DuVallová), hodnou manželkou Michaela (Danny Huston) a milující matkou dvou malých dcer. Její rodina vyzařuje naději a radost. Na mnohem nižší společenské i ekonomické úrovni se nachází bývalý trestanec Jack Jordan (Benicio del Toro) a jeho žena Marianne (Melissa Leová), která nemůže zajistit důstojné zázemí svým dvěma dětem, zatímco Jack řeší problémy s obrácením se k víře. Tragická nehoda způsobí, že se tito lidé z odlišných prostředí, ocitnou v jediném společném světě. V důsledku toho Paul stane tváří v tvář své vlastní smrtelnosti, Cristine se střetává s událostmi, které mohou ovlivnit její přítomný a snad i budoucí život a Jackova víra podstupuje závažnou zkoušku. Pokud se komukoliv z nich podaří získat si zpět ztracenou duševní rovnováhu, může to ostatní stát mnoho. Vůle žít a instinkt nutící hledat podporu v těžké situaci u jiných, zůstává v jejich existenci všudypřítomný.(oficiální text distributora)', NULL, 2003, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1489, '201207/88 minut.m4v', '88 minut', 15, 0, 'cz', NULL, 'en', '88 minutes', 'http://www.csfd.cz/film/227631-88-minut/', '88 minut.jpg', NULL, 'Vysokoškolský profesor Dr. Jack Gramm, který zároveň pracuje jako forenzní psychiatr FBI, dostane ultimátum – zbývá mu 88 minut života. Aby se zachránil, musí Jack použít všechny své dovednosti a zkušenosti, aby zúžil okruh možných podezřelých, mezi něž patří nespokojení studenti, opuštěná milenka i sériový vrah, který sedí v cele smrti. Jack Gramm založil celou svou kariéru na spolehlivém „určování“ povah lidí a faktorů rizik. Svoje expertní osvědčení získal díky případu sériového vraha Jona Forstera a nyní, s pocitem zadostiučinění, čeká na Forsterovu popravu. Ale když se začnou objevovat další podobné vraždy, musí Jack obhájit, že Forster je skutečně vinen, jak celou dobu tvrdil.(oficiální text distributora)', NULL, 2007, '');
