INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1501, '201207/Hogo Fogo Homolka.m4v', 'Hogo Fogo Homolka', 9, 0, 'cz', NULL, 'cz', NULL, 'http://www.csfd.cz/film/7102-hogo-fogo-homolka/', 'Hogo Fogo Homolka.jpg', NULL, 'Nové auto a návštěva na vesnici přinese další rodinná nedorozumění. Druhá část slavné komediální trilogie Jaroslava Papouška. Homolkovi si pořídili auto - teď jsou teprve hogo fogo rodina! Dohadování o tom, kam se pojede na první výlet - děda s Ludvou prosazují fotbal, babi s Heduš dostihy v Chuchli - rozhodne zpráva, že s pradědečkem je zle, že asi umře. A tak se Homolkovi vypraví do jihočeské vesničky rozloučit se s umírajícím pradědečkem. Ten ovšem mezitím odešel na přehlídku norníků, kde si s kamarády vesele připíjí na to, že "život je krásnej". To ovšem Homolkovi netuší a atmosféra v chaloupce houstne. Dochází k dalším sporům a malicherným hádkám, kluci se nudí, a tak vyvádějí - hodí například svoje čepice do vesnické kadibudky a rozzlobená Heduš, babi i prababička je musejí lovit. Pradědeček se stále nevrací, tak se Homolkovi rozloučí s prababičkou a slíbí jí, že se cestou zastaví v lese pradědečka aspoň pozdravit. Zastaví se u rybníka, kde babi vypráví klukům, jak se tu jako mladá topila, což skončí nedobrovolnou koupelí všech...(oficiální text distributora)', NULL, 1970, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1502, '201207/Homolka a Tobolka.m4v', 'Homolka a Tobolka', 9, 0, 'cz', NULL, 'cz', NULL, 'http://www.csfd.cz/film/7103-homolka-a-tobolka/', 'Homolka a Tobolka.jpg', NULL, 'Třetí příběh pražské rodiny Homolkových. Tentokrát si Homolkovi vyjeli na zimní rekreaci do Krkonoš, chtějí se pořádně užít a co nejméně utratit. Děda s babi, Ludva, Heduš a dvojčata si přijíždějí užít zimní radovánky do zotavovny Radost ve Špindlerově Mlýně. Cestou si slíbí, že na sebe budou hodní, a kdo se tomu zpronevěří, bude za trest klečet. Vzápětí nato je samozřejmě potká spousta nepříjemností a zmatků, o které se zaslouží i právě nastupující kulturní referent. Další scénka nastane ve chvíli, kdy dva manželské páry požadují výměnu pokojů s Homolkovými a netrvá to dlouho - a děda musí poprvé klečet. Šňůra nešťastných náhod pokračuje, mimo jiné dojde k menšímu požáru a následnému zaplavení pokoje starých Homolků, přičemž pomoc manželských párů je odhalena jako zištná - stále si chtějí pokoje vyměnit. Po odmítnutí se složí a nabídnou Homolkům peníze. Na to Homolkovi přistoupí a těší se, jak se rozšoupnou. Pohádají se ale o to, jak peníze rozdělit a následně zjistí, že tobolku s penězi ztratili. Pospíchají na cvičnou louku, hrabou se v závějích a pokoušejí se najít ztracené peníze...(oficiální text distributora)', NULL, 1972, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1503, '201207/Hudba z Marsu.m4v', 'Hudba z Marsu', 9, 0, 'cz', NULL, 'cz', NULL, 'http://www.csfd.cz/film/4995-hudba-z-marsu/', 'Hudba z Marsu.jpg', NULL, 'Zaměstnanci nábytkářského podniku MARS uvítají vzácnou návštěvu najatou hudbou. Jejich vychloubání, jaký by oni sestavili orchestr, je bráno vážně, proto dostanou malý dárek - hudební nástroje pro třicet muzikantů.(oficiální text distributora)', NULL, 1955, '');
