INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1504, '201207/Krakatit.m4v', 'Krakatit', 15, 0, 'cz', NULL, 'cz', NULL, 'http://www.csfd.cz/film/9458-krakatit/', 'Krakatit.jpg', NULL, 'Čapkova předloha se v těsně poválečném kontextu dočkala zřetelné aktualizace: mimořádně ničivá třaskavina v rukou válečných štváčů, které zajímají jen zisk, se může snadno stát osudnou rozbuškou, ohrožující celý svět. Režisér Otakar Vávra soustředí pozornost na horečnaté představy vyčerpaného a navíc zraněného protagonisty inženýra Prokopa, jenž se ze všech sil snažil zabránit zneužití svého vynálezu. I když po vypravěčské stránce (snově stylizované sekvence, které přibližují hrdinovo narušené vnímání, prozrazují inspiraci ponurým francouzským poetismem) film přece jen zestárnul, jeho výpověď si dodnes uchovává svou naléhavost. Román Karla Čapka Krakatit zfilmoval Otakar Vávra již záhy po druhé světové válce a pojal jej jako horečnatě expresivní varování před jadernou zkázou. V roce 1980 se k tomuto dílu vrátil znovu, tentokrát však zdůraznil násilnou aktualizaci výchozího textu ve smyslu zákeřných imperialistických piklů. Hlavní hrdina vědec Prokop se ocitá ve smrtelném ohrožení, když chce zabránit zneužití svého epochálního vynálezu třaskaviny Krakatit dvěmi skupinami lidí, kteří s její pomocí chtějí ovládnout svět.(oficiální text distributora)', NULL, 1948, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1505, '201207/Laska nebeska -- Love actually cz.m4v', 'Laska nebeska', 6, 0, 'cz', NULL, 'en', 'Love actually', 'http://www.csfd.cz/film/43677-laska-nebeska/', 'Laska nebeska -- Love actually cz.jpg', NULL, 'Láska nebeská, nová romantická komedie ve stylu Čtyři svatby a jeden pohřeb, Notting Hill a Deník Bridget Jonesové, nás zavede do současného předvánočního Londýna, kde se odehraje příběh skládající se z mnoha nádherných milostných historek - někdy romantických, někdy smutných, někdy pošetilých. Žádná nepostrádá svůj osobitý humor. Všude, kam se podíváte, způsobuje láska chaos. Od nově jmenovaného ministerského předsedy, který se zamiluje 30 vteřin poté, co vejde do úřadu na Downing Street, až po smolařského prodavače sendvičů, který nemá s dívkami ve Velké Británii prostě štěstí a raději proto odjíždí do Wisconsinu; od spisovatele, který dostal košem a vypaří se do jižní Francie utěšit své bolavé srdce, až po stárnoucí rockovou hvězdu, která se snaží o come-back za každou cenu; od nevěsty, které dělá starosti nejlepší přítel jejího muže, až po ženu, které dělá starosti její manžel; od chlapce, který se zakoukal do nejkrásněší dívky ve škole, až po jeho nevlastního otce, který se zakokukal do Claudie Schiffer. Tyto londýnské životy a lásky se setkají, smísí a nakonec vše vyvrcholí na Štědrý večer s romantickými, dráždivými a komickými důsledky pro všechny zúčastněné.(oficiální text distributora)', NULL, 2003, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1506, '201207/Laska nebeska -- Love actually en.m4v', 'Love actually', 6, 0, 'en', 'cz', 'en', NULL, 'http://www.imdb.com/title/tt0314331/', 'Laska nebeska -- Love actually en.jpg', NULL, 'Directed by Richard Curtis.  With Hugh Grant, Martine McCutcheon, Liam Neeson, Laura Linney. Follows the lives of eight very different couples in dealing with their love lives in various loosely and interrelated tales all set during a frantic month before Christmas in London, England.', NULL, 2003, '');
