INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1507, '201207/Laska Online.m4v', 'Laska Online', 6, 0, 'cz', NULL, 'en', NULL, 'http://www.csfd.cz/film/220904-laska-on-line/', 'Laska Online.jpg', NULL, 'Ida a Sandor žijí každý zcela odlišně. Ida bydlí uprostřed velkoměsta se svou matkou trpící depresemi. Tráví svůj čas nakupováním oblečení a chozením na různé párty. Jako atraktivní a oblíbená dívka si vždycky najde způsob, jak se dostat do těch nejexkluzívnějších míst. Na druhou stranu Sandor, plachý samotář, nikdy s žádnou dívkou nic neměl, nikdy nebyl pařit a ani se nikdy neopil. Studuje klasický balet a předstírá, že mu nevadí, když mu spolužáci říkají "šukající král teploušů". I přesto mají Sandor s Idou společného více, než si oba myslí. Oba jsou osamoceni a nepochopeni. Když se spolu seznámí, vzniká mezi nimi váhavé přátelství, něco, o co se mohou oba opřít v stále se měnícím světě.(oficiální text distributora)', NULL, 2005, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1508, '201207/Milenec -- L''Amant.m4v', 'Milenec', 6, 0, 'cz', NULL, 'fr', ' L''Amant', 'http://www.csfd.cz/film/162-milenec/', 'Milenec -- L''Amant.jpg', NULL, 'Saigon, konec 20. let. Patnáctiletá francouzská školačka se vrací z prázdninového pobytu na venkově u matky. Na přívozu přes Mekong upoutá pozornost bohatého mladého Číňana a ten jí nabídne odvoz do Saigonu. Již během společné cesty do města mezi nimi vzniká výrazné napětí, jež záhy přerůstá v intenzivní vztah - spíše erotický, než citový. Nezkušená dívka, pocházející z nemajetných poměrů, začíná prostřednictvím svého sedmadvacetiletého přítele objevovat svět fyzické lásky i svět peněz. Zatímco Číňan zcela propadá jejímu kouzlu a zamiluje se, dívka stále dává najevo, že ji k němu váží jen sex a peníze. Styky mladičké bílé dívky se světáckým Číňanem ale nemohou zůstat dlouho utajeny. Mladík je podle tradice zaslíben čínské nevěstě z nejlepších kruhů, jiný sňatek by ho vyloučil z otcovy závěti. Dívka se stává terčem útoků matky a staršího bratra, kteří ovšem přes svůj odpor a opovržení neváhají těžit z Číňanovy štědrosti. Vztah francouzské dívky a jejího čínského milence je předem určen k zániku, ale když se dívka po mladíkově svatbě vrací s rodinou do Francie, nahlížíme poprvé za masku její zdrženlivosti - přese všechno ji k milenci poutal i silný cit.(oficiální text distributora)', NULL, 1992, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1509, '201207/Mladi, krasni a bohati.m4v', 'Mladi, krasni a bohati', 6, 0, 'cz', '  ', 'en', '', 'http://www.csfd.cz/film/269240-utek/', 'Mladi, krasni a bohati.jpg', NULL, 'Mousse a Louis jsou mladí, krásní, bohatí a zamilovaní. Jejich život ale ovládly drogy. Jednoho dne se předávkují a Louis umírá. Mousse přežije a brzy zjistí, že je těhotná. Dobře situovaná rodina Louise se s tím nehodlá smířit a trvá na interrupci. Mousse se cítí ztracená a utíká daleko z Paříže, do domu na venkově. O několik měsíců později ji následuje i Louisův bratr. Jak dopadne útěk obou před pokryteckou společností...?', NULL, 2009, '');
