INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1510, '201207/Muz a jeho pes -- Un homme et son chien.m4v', 'Muz a jeho pes', 15, 0, 'cz', NULL, 'fr', 'Un homme et son chien', 'http://www.csfd.cz/film/241289-muz-a-jeho-pes/', 'Muz a jeho pes -- Un homme et son chien.jpg', NULL, 'Charles a jeho pes. Jeanne, atraktivní vdova, se kterou Charles žije, mu oznámí, že se znovu vdává, a vyhodí ho z domu. Byla jeho velkou láskou, byla manželkou jeho přítele z dob služby u mariny. Leila, mladá uklízečka z domu, těhotná, ani neví s kým, je Charlesovi velkou oporou, ale nemůže mu nabídnout víc než soucit a milý úsměv. Charles jí chce vracet mnohem víc, ale tak jako mnozí v jeho věku pro okolí nic neznamená. Charles se ocitá i se svým psem na ulici. Nikdo mu nepodá pomocnou ruku a jeho hrdost mu nedovolí, aby tu svou sám pro pomoc natáhl. Vždycky si myslel, že je mužem, který se svým psem po boku bude bojovat dál...(Festival francouzského filmu)', NULL, 2008, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1511, '201207/Obsluhoval jsem anglickeho krale.m4v', 'Obsluhoval jsem anglickeho krale', 9, 0, 'cz', NULL, 'cz', NULL, 'http://www.csfd.cz/film/221330-obsluhoval-jsem-anglickeho-krale/', 'Obsluhoval jsem anglickeho krale.jpg', NULL, 'Tématem Hrabalova slavného románu je životní dráha číšníka, později hoteliéra a nakonec cestáře v době politických zvratů první půle dvacátého století. Mnohokrát v životě pocítí, že jím druzí opovrhují pro malý vzrůst, pro sympatie k Němcům, pro jeho češství, proto, že má původ odloženého dítěte, i pro osobitou životní filozofii. Nikdy však nezatrpkne, neoplácí nenávistí, nemstí se. Touží po bohatství a uznání ve své profesi, touží po milující ženě - ale dostává se mi jich jen krátce. Není uznán ani jako árijec, ani jako milionář...(oficiální text distributora)', NULL, 2006, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1512, '201207/Pavucina lzi -- Gospel of Deceit.m4v', 'Pavucina lzi', 15, 0, 'cz', NULL, 'en', 'Gospel of Deceit', 'http://www.csfd.cz/film/231252-pavucina-lzi/', 'Pavucina lzi -- Gospel of Deceit.jpg', NULL, 'Pokojnú oslavu Emily (Alexandra Paul) a jej manžela Teda (J.C.MacKenzie) pozoruje už dlhšiu dobu neznámi muž. Emily ho pozýva ďalej a manželia mu poskytnú dočasné ubytovanie na fare, kde je Ted kazateľom. Luke (Corey Sevier) spočiatku nijako nezasahuje do ich životov. Silne veriaca Emily spolu s Tedom venujú všetok svoj čas a prostriedky založeniu novej cirkvi, svojou snahou získavajú pre Teda možnosť sérií vystúpení v televízii. A tak, zatiaľ čo je Ted pracovne zaneprázdnený, Emily postupne podľahne kúzlu mladého a atraktívneho Luka, vďaka čomu sa jej manželský život začína rúcať a na povrch sa dostávajú hriechy minulosti. Je však toto všetko len obyčajná náhoda alebo dôkladne premyslený plán?(rudess)', NULL, 2006, '');
