INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1520, '201207/Terminator 3 cz.m4v', 'Terminator 3 Rise of the Machines', 4, 29, 'cz', NULL, 'en', 'Terminator 3 - Rise of the Machines', 'http://www.csfd.cz/film/15908-terminator-3-vzpoura-stroju/', 'Terminator 3 cz.jpg', 13, 'Od momentu, keď sa Johnovi Connorovi (Nick Stahl) podarilo odvrátiť súdny deň a zachrániť ľudstvo pred zničením, uplynulo 10 rokov. V súčasnosti dvadsaťdva ročný Connor žije inak, ako väčšina ľudí - bez domova, bez kreditných kariet, bez mobilného telefónu a bez práce. Bez akýchkoľvek záznamov o svojej existencii. Žije spôsobom, ktorý znemožňuje, aby ho vystopoval Skynet. Z tieňa budúcnosti však jedného dňa vystúpi T-X (Kristanna Loken), doposiaľ najvyspelejší vražedný stroj Skynetu, ktorý vyslali z budúcnosti, aby dokončil prácu svojho predchodcu T-1000. Vo svojej ľudskej - ženskej podobe je T-X rovnako krásna, ako aj v skutočnosti neľútostná. Je mnohonásobne silnejšia, nebezpečnejšia a deštruktívnejšia, než ktorýkoľvek Terminátor, ktorý tu bol pred ňou. Teraz však nie je jediným cieľom Skynetu John Connor. Nič netušiaca veterinárka Kate Brewsterová (Claire Danesová) sa skoro stane svedkom toho, ako jej dávna minulosť a nádejná súčasnoť vyústi do nepredstaviteľnej budúcnosti. Pokiaľ ovšem dokáže uniknúť vražednej T-X. Keď si Connor a Kate uvedomia, že do príchodu súdneho dňa zostávajú iba tri hodiny, ich jedinou nádejou na prežitie sa stáva replika Terminátora (Arnold Schwarzenegger) - tajomného a teraz už zastaralého robota, ktorý sa Connora v minulosti pokúšal zabiť. Spoločnými silami musia zvíťaziť nad technologicky vyspelejšou T-X a odvrátiť tak hrozbu súdneho dňa... alebo odsúdiť celé ľudstvo na zánik.(oficiální text distributora)', NULL, 2003, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1521, '201207/Terminator 3 en.m4v', 'Terminator 3 Rise of the Machines', 4, 29, 'en', 'cz', 'en', 'Terminator 3 - Rise of the Machines', 'http://www.imdb.com/title/tt0181852/', 'Terminator 3 en.jpg', 3, 'Directed by Jonathan Mostow.  With Arnold Schwarzenegger, Nick Stahl, Kristanna Loken, Claire Danes. A robotic warrior from a post-apocalyptic future travels back in time to protect a 20-year old drifter and his future wife from an most advanced robotic assassin and to ensure they both survive a nuclear attack.', NULL, 2003, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1522, '201207/Vrchni prchni.m4v', 'Vrchni prchni', 9, 0, 'cz', NULL, 'cz', NULL, 'http://www.csfd.cz/film/8545-vrchni-prchni/', 'Vrchni prchni.jpg', NULL, 'Knikupec Dalibor Vrána věru nemá lehké žití. Líbí se ženám, a ty se zase líbí jemu, a protože je Dalibor mužem činu, značnou část jeho poctivě nabytého výdělku polknou alimenty. Svou svízelnou finanční situaci se rozhodne vylepšit způsobem zcela originálním. Využije toho, že si jej lidé v restauracích často pletou s číšníkem a začne inkasovat. Netrvá dlouho a stane se nepolapitelným fantomem pražských (a později i mimopražských) restaurací, postrachem liknavých číšníků. Je stále odvážnější a úspěšně se mu daří před manželkou i okolím maskovat zdroj příjmů tvrzením, že si vydělává v nočních podnicích hrou na housle. V Karlových Varech se mu podaří husarský kousek, když zorganizuje honičku místních číšníků sám na sebe. Jenže jak se říká, tak dlouho se chodí pro vodu, až...(oficiální text distributora)', NULL, 1980, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1523, '201208/Americka krasa -- American Beauty en-en.m4v', 'American Beauty', 15, 0, 'en', 'en', 'en', 'American Beauty', 'http://www.imdb.com/title/tt0169547/', 'Americka krasa -- American Beauty en-en.jpg', NULL, 'Directed by Sam Mendes.  With Kevin Spacey, Annette Bening, Thora Birch, Wes Bentley. Lester Burnham, a depressed suburban father in a mid-life crisis, decides to turn his hectic life around after developing an infatuation for his daughter''s attractive friend.', NULL, 2000, '');
