INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1525, '201208/Americka krasa -- American Beauty.m4v', 'Americka krasa', 15, 0, 'cz', NULL, 'en', 'American Beauty', 'http://www.csfd.cz/film/6654-americka-krasa/', 'Americka krasa -- American Beauty.jpg', NULL, 'Na první pohled jsou Burnhamovi šťastnou rodinou, jejíž život je zalit sluncem. Pohodlný dům, auto kvalitní značky, půvabná dospívající dcera, studující na správné škole, rodiče úspěšní ve svém povolání. Nikdo se tedy nediví, že paní Burnhamová (Annette Bening) je věčně samý úsměv - alespoň na veřejnosti. Ve skutečnosti je ale všechno jinak. Obraz dokonalosti matce Carolyn a dceři Jane neustále kazí otec Lester (Kevin Spacey). Není tím ramenatým, snědým "plejbojem" a co je horší, začíná stále častěji dávat najevo, že se mu nedaří a že je nespokojen se svým životem. Dokud jen odmítá roli, kterou stovky jemu podobných odevzdaně hrají, aby měli klid, je to pro rodinu sice obtížné, ale únosné. Ještě včera nerozhodný a zakřiknutý Lester se ovšem rozhodne "krizi středního věku" řešit nečekaně radikálně. Dojde mu totiž, o co v životě jde: o splnění snů a tužeb, které člověk má. Lester už nikdy nebude ubožák!(oficiální text distributora)', NULL, 1999, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1526, '201208/Cernocerna tma -- Pitch Black en.m4v', 'Pitch Black', 4, 0, 'en', 'cz', 'en', 'Pitch Black', 'http://www.imdb.com/title/tt0134847/', 'Cernocerna tma -- Pitch Black en.jpg', NULL, 'Directed by David Twohy.  With Radha Mitchell, Cole Hauser, Vin Diesel, Keith David. A prisoner transport ship and its crew are marooned on a planet full of bloodthirsty creatures that only come out to feast at night. But then, they learn that a month-long eclipse is about to occur.', NULL, 2000, '');
