INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1545, '201208/Gran Torino.m4v', 'Gran Torino', 15, 0, 'cz', NULL, 'en', 'Gran Torino', 'http://www.csfd.cz/film/240479-gran-torino/', 'Gran Torino.jpg', NULL, 'Film vypráví příběh neústupného válečného veterána Walta Kowalského, který si s přibývajícím věkem uvědomuje, že na nový svět už nestačí. Musí se konfrontovat jak se sousedy rozličného etnického původu, tak se svými hluboce zakořeněnými předsudky. Všechny staré sousedy postupně nahradili imigranti, kterými Walt ze srdce pohrdá. Podzim svého života tak spíš přežívá než dožívá. Až do oné noci, kdy se mu někdo pokusí ukrást jeho chloubu – nablýskaný sporťák Gran Torino. Elegantní tvary auta zapůsobí na Waltova neprůbojného souseda Thaa, který si krádeží snaží získat respekt v partě mladých výtržníků. Thaova rodina trvá na tom, aby si chlapec u Walta svůj nerozvážný čin odpracoval. Walt nakonec podlehne a vyzve mladíka k tomu, aby své aktivity nasměroval ve prospěch okolí. Díky Thaovi a jeho dobrosrdečné rodině Walt prozře a snad poprvé v životě se na své sousedy dívá bez předsudků. Veterán postupně odkryje i tu část duše, kterou skrývá již od války...(oficiální text distributora)', NULL, 2008, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1546, '201208/Kmotr 2 -- The Godfather 2 en.m4v', 'The Godfather 2', 15, 1003, 'en', 'cz', 'en', 'The Godfather 2', 'http://www.imdb.com/title/tt0071562/', 'Kmotr 2 -- The Godfather 2 en.jpg', 2, 'Directed by Francis Ford Coppola.  With Al Pacino, Robert De Niro, Robert Duvall, Diane Keaton. The early life and career of Vito Corleone in 1920s New York is portrayed while his son, Michael, expands and tightens his grip on his crime syndicate stretching from Lake Tahoe, Nevada to pre-revolution 1958 Cuba.', NULL, 1974, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1547, '201208/Kmotr 2 -- The Godfather 2.m4v', 'Kmotr 2', 15, 1003, 'cz', '  ', 'en', 'The Godfather 2', 'http://www.csfd.cz/film/1645-kmotr-ii/', 'Kmotr 2 -- The Godfather 2.jpg', 102, 'Jsme větší než U. S. Steel. Al Pacino a Robert De Niro v hlavních rolích pokračování slavné mafiánské ságy, oceněné šesti Oscary. Mimořádný úspěch Kmotra (1972) ve světových kinech logicky vedl k okamžitým úvahám o natočení pokračování, k němuž koneckonců vybízel i relativně otevřený konec originálu. Coppola, který tímto snímkem získal v Hollywoodu nesmírný respekt, ovšem nejdřív svého vlivu využil k realizaci osobního projektu Rozhovor (vyznamenaného mj. Zlatou palmou na MFF v Cannes, ale komerčně neúspěšného). Scénář Kmotra II, jenž je znovu společným dílem autora původní předlohy a režiséra, rozvíjí dvě dějové linie. Další osudy nového kmotra Michaela Corleona (Al Pacino), ovládajícího své impérium z rozsáhlého sídla v nevadském Lake Tahoe. A začátky mladého Vita Corleona (Robert De Niro) v New Yorku 20. let, jež jsou obsaženy i v Puzově původní knize. Michael musí čelit nejen svým konkurentům z podsvětí, zejména Hymanu Rothovi (pozoruhodná kreace slavného hereckého pedagoga Lee Strasberga), který využívá informací zhrzeného Michaelova bratra Freda (John Cazale), ale také zvýšenému zájmu orgánů spravedlnosti, jež v průběhu druhé poloviny 50. let, kdy se Kmotr II odehrává, zintenzivnily svou kampaň proti organizovanému zločinu. Michael své obchodní zájmy uhájí, přes nedobrovolné opuštění hazardních aktivit na revoluční Kubě, ovšem cena je vysoká. Předčasně zestárlý, zahořklý kmotr je ještě nelítostnějším šéfem než jeho otec, ale rodinné vazby, tvořící páteř prvního filmu, zmizely. Michael zůstává osamocený. Bez bratra, kterého nechal zabít, i bez manželky, která od něho odešla. Kmotr II je stejně výjimečným dílem jako originál, na který navazuje. Mnozí dokonce tvrdí, že lepším. Coppola se tu jako režisér představuje ve špičkové formě, navíc posílené neotřesitelným postavením režisérské hvězdy. Už nemusel bojovat o výši rozpočtu jako v případě prvního filmu (který se z finančních důvodů měl původně odehrávat v současnosti), ani o herce, jež hodlal angažovat. Ve všem měl naprosto volnou ruku. Prozřetelným se ukázalo obsazení málo známého Roberta De Nira jako mladého Vita Corleona, jehož elektrizující herectví tu získává na přesvědčivosti také citlivým využitím sicilského dialektu, který v raných scénách nahrazuje angličtinu. Jeho osud v nové zemi, kam musel uprchnout ze sicilské vesničky, jež mu dala jméno, ostře kontrastuje s údělem jeho syna. Vito buduje svoji pozici prakticky z ničeho, dokáže využít každé příležitosti, neštítí se násilných prostředků, pokud je považuje za nutné a efektivní, ale základem je pro něho rodina. Pokračování Kmotra se stalo prvním "sequelem", který získal Cenu Akademie za nejlepší film. Nezůstalo ovšem u jediného Oscara. Kmotr II v tomto ohledu překonal originál celkem se šesti soškami; další cenu mu Akademie udělila za nejlepší režii (ta Coppolovi v prvním případě unikla ve prospěch Boba Fosseho za Kabaret), scénář, výpravu, hudbu (slavný italský skladatel Nino Rota, opominutý u prvního dílu kvůli zařazení již použitých motivů z jiného filmu, tady spolupracoval s režisérovým otcem Carminem Coppolou) a konečně za fenomenální De Nirův výkon ve vedlejší roli.(oficiální text distributora)', NULL, 1974, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1548, '201208/Mash The Movie.m4v', 'MASH: The Movie', 15, 0, 'en', 'cz', 'en', 'Mash The Movie', 'http://www.imdb.com/title/tt0066026/', 'Mash The Movie.jpg', 0, 'Directed by Robert Altman.  With Donald Sutherland, Elliott Gould, Tom Skerritt, Sally Kellerman. The staff of a Korean War field hospital use humor and hijinks to keep their sanity in the face of the horror of war.', NULL, 1970, '');
