INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1551, '201208/Sin City cz.m4v', 'Sin City: Mesto hriechu', 15, 0, 'cz', NULL, 'en', 'Sin City', 'http://www.csfd.cz/film/178904-sin-city-mesto-hrichu/', 'Sin City cz.jpg', NULL, 'Sin City - Město hříchu. John Hartigan (Bruce Willis), jeden z mála nezkorumpovaných policistů v tomto městě, se poslední noc své služby snaží zachránit Nancy Callahanovou, věk 11 let, z rukou Juniora (Nick Stahl) - pedofilního sadisty a sériového vraha, k tomu ovšem také nedotknutelného synáčka senátora Roarka. Hartigan uspěje, zachrání Nancy a zbaví Juniora obou jeho zbraní. Sám ale málem nevyvázne. Jeho parťák Bob, další jméno na výplatní listině vládce města Roarka, do něj vyprázdní zásobník. Taková je první dějová linie Sin City filmu, na který davy fanoušků legendy noir comicsu Franka Millera čekaly dlouhých třináct let. Už první ukázka, která spatřila světlo světa v Millerově podání třístránková povídka The Customer Is Always Right předurčila film v očích tisíců čtenářů ke zkultovnění. To ale není ani zdaleka vše. Autorská dvojice Miller-Rodriguez se rozhodla oživit i další z nezapomenutelných příběhů ze světa, který Miller stvořil a Rodriguez mu propadl, ze světa kouzelné černobílé kresby, krásných žen, drsných mužů, šeptajícího deště a brutálního násilí. Čeká nás tak setkání s Marvem (Mickey Rourke), bijcem, kterého byste si nejspíš za souseda nevybrali, jehož budeme sledovat na cestě za pomstou. Pomstou Goldie, pouliční holky, která voněla jako anděl, a byla zavražděna v posteli, kterou tu noc sdílela se zjizveným hromotlukem, který té noci poprvé zažil, co to je někoho milovat. Posledním do nesvaté trojice hrdinů je Dwight McCarthy (Clive Owen), který se ocitne ve špatnou dobu ve špatné společnosti a zaplete se do bitvy mezi dámami ze Starého Města, vedenými krásnou a inteligentní (a o to nebezpečnější) Gail (Rosario Dawson), s ne zrovna vzornými strážci zákona v čele s Jackem (Benicio Del Toro). Rok 2005 bude patřit grafickým románům Franka Millera.(Kryšpín)', NULL, 2005, '');
