INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1555, '201208/Tahle zeme neni pro stary -- No Country For Old Men.m4v', 'Tato krajina neni pro stary', 15, 0, 'cz', NULL, 'en', 'No Country For Old Men', 'http://www.csfd.cz/film/226762-tahle-zeme-neni-pro-stary/', 'Tahle zeme neni pro stary -- No Country For Old Men.jpg', NULL, 'Původně si chtěl Lleywelyn Moss (Josh Brolin) v noci jenom zalovit. Jenže v místech, kde se prázdná pustina dělí díky hraničním kamenům na Texas a Mexiko, měl „štěstí“ na jiný úlovek. Mezi zkrvavenými těly v rozstřílených autech leželo několik balíků čistého heroinu a dva miliony dolarů v hotovosti. Neodolal pokušení a započal tím zběsilou štvanici na sebe i svoji mladou ženu. V jejich stopách se vydává stárnoucí šerif Ed Tom Bell (Tommy Lee Jones), který ví, že pokud dvojici nenajde dřív než profesionální zabiják Anton Chigurh (Javier Bardem), bude s nimi zle.(Isherwood)', NULL, 2007, '');
