INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1557, '201208/Vecny svit neposkvrnene mysli -- Eternal Sunshine of the Spotless Mind en.m4v', 'Eternal Sunshine of the Spotless Mind', 6, 0, 'en', 'cz', 'en', 'Eternal Sunshine of the Spotless Mind', 'http://www.imdb.com/title/tt0338013/', 'Vecny svit neposkvrnene mysli -- Eternal Sunshine of the Spotless Mind en.jpg', NULL, 'Directed by Michel Gondry.  With Jim Carrey, Kate Winslet, Tom Wilkinson, Gerry Robert Byrne. A couple undergo a procedure to erase each other from their memories when their relationship turns sour, but it is only through the process of loss that they discover what they had to begin with.', NULL, 2004, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1558, '201208/Vecny svit neposkvrnene mysli -- Eternal Sunshine of the Spotless Mind.m4v', 'Vecny svit neposkvrnene mysli', 6, 0, 'cz', NULL, 'en', 'Eternal Sunshine of the Spotless Mind', 'http://www.csfd.cz/film/70254-vecny-svit-neposkvrnene-mysli/', 'Vecny svit neposkvrnene mysli -- Eternal Sunshine of the Spotless Mind.jpg', NULL, 'Život osamělého a nesmělého Joela se od základu změní, když potká excentrickou a temperamentní Clementine. Ta je výstřední ve všech směrech a k bláznivým kouskům stále úspěšněji vyzývá i Joela, jenže je rovněž nevyrovnaná a výbušná a jednoho dne se rozhodne Joela zbavit a začít znovu. Na experimentální klinice se podrobí vymazání paměti, a jelikož pokus dopadne úspěšně, rozhodne se zdrcený Joel pro stejné řešení. Nemá ale štěstí, tentokrát se zákrok vymkne lékaři z rukou a dílo zkázy dokoná jeho asistent, který se místo pečlivého monitorování mozkové činnosti klienta nechá svést k divokým erotickým hrátkám. Režisér umí mistrovsky rozrušovat časoprostorovou kontinuitu a nahrazovat ji asociativním spojováním útržků, jež pocházejí z různých vrstev paměti, a zde se tento princip uplatní při rekonstrukci celého vztahu ústřední dvojice. Odvíjí se pozpátku, k některým zážitkům se film opakovaně vrací, mnohé deformuje v závislosti na Joelových rozjitřených emocích.(oficiální text distributora)', NULL, 2004, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1562, '201208/X-men 3 Posledni vzdor -- The Last Stand.m4v', 'X-men 3 Posledni vzdor', 4, 28, 'cz', NULL, 'en', 'X-men: The Last Stand', 'http://www.csfd.cz/film/194903-x-men-posledni-vzdor/', 'X-men 3 Posledni vzdor -- The Last Stand.jpg', 23, 'Když jsi mutantem, máš jen dvě možnosti, co s tím dělat. Skrývat to, či se držet v ústraní. Dnešek je den, kdy se to ale změnilo a světlo světa spatřil téměř zázračný lék proti mutantství. Bohužel odvrhnout nechtěný a často proklínaný dar a stát se opět člověkem není tak jednoduché, jak na první pohled vypadá. A zloduch Magneto (Ian McKellen) na vyléčení určitě nepomýšlí. Z předchozích dílů nám dobře známá skupina mutantů pod vedením profesora Charlese Xaviera (Patrick Stewart) je nyní posílena o dva nové bojovníky Šelmu (Kelsey Grammer) a Anděla (Ben Foster) a budou to vážně potřebovat. X-meni musí rozpoutat nejstrašlivější válku jakou kdy svět viděl, jen aby mohli navždy ukončit všechny ostatní.(monolog)', NULL, 2006, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1563, '201208/X-men Origins Wolverine en.m4v', 'X-men Origins Wolverine', 4, 28, 'en', 'cz', 'en', 'X-men Origins: wolverine', 'http://www.imdb.com/title/tt0458525/', 'X-men Origins Wolverine en.jpg', 4, 'Directed by Gavin Hood.  With Hugh Jackman, Liev Schreiber, Ryan Reynolds, Danny Huston. A look at Wolverine''s early life, in particular his time with the government squad Weapon and the impact it will have on his later years.', NULL, 2009, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1564, '201208/X-men Origins Wolverine.m4v', 'X-men Origins Wolverine', 4, 28, 'cz', NULL, 'en', 'X-men Origins: Wolverine', 'http://www.csfd.cz/film/224880-x-men-origins-wolverine/', 'X-men Origins Wolverine.jpg', 24, 'Film X-Men Origins: Wolverine je dějově zasazen na samý počátek ságy o známých mutantech X-Menech. Hugh Jackman se vrací v roli, která z něj udělala filmovou hvězdu; v roli Logana neboli nelítostného mutanta Wolverina obdařeného zázračnou schopností regenerace a také adamantiovými drápy na rukou. Zatímco tři předchozí filmy byly zasazeny do nepříliš vzdálené budoucnosti, hlavní část nového příběhu se odehrává v 70. letech minulého století. Děj se vrací ke počátkům Wolverinovy hrdinské kariéry i tragickým událostem jeho života, z nichž jej nejvíce poznamená smrt osudové lásky Kayle Silverfox. Její smrt způsobí Wolverinovo zapletení s tajným vládním vojenským programem Weapon X. S projektem, jenž mu navždy zcela změní život. Pod velením Williama Strykera (Danny Huston) je zformována tajná vojenská jednotka Team X složená výhradně z mutantů. Jedním z nich je také Victor Creed alias Sabretooth (Liev Schreiber), nezkrotná bytost s neuvěřitelnou sílou, s nímž musí Wolverine svést svůj nejtěžší souboj.(oficiální text distributora)', NULL, 2009, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1565, '201208/Zelena mile -- Green Mile en.m4v', 'Zelena mile -- Green Mile', 15, 0, 'en', 'cz', 'en', 'Green Mile', 'http://www.imdb.com/title/tt0120689/', 'Zelena mile -- Green Mile en.jpg', NULL, 'Directed by Frank Darabont.  With Tom Hanks, Michael Clarke Duncan, David Morse, Bonnie Hunt. The lives of guards on Death Row are affected by one of their charges: a black man accused of child murder and rape, yet who has a mysterious gift.', NULL, 2000, '');
