INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1579, '201209/Citiyen Kane en.m4v', 'Citizen Kane', 15, 0, 'en', 'cz', 'en', 'Citizen Kane', 'http://www.csfd.cz/film/9611-obcan-kane/', 'Citiyen Kane en.jpg', NULL, 'Neposlušné dítě Hollywoodu Orson Welles natáčel toto legendární drama, které se stalo milníkem ve vývoji filmové řeči i vypravěčských postupů, již ve svých pětadvaceti letech. Welles svou režijní prvotinu pojal jako rekonstrukci osudů fiktivního magnáta Kanea, sestavovanou novinářem - využívá k tomu filmové týdeníky, rozmluvy s pamětníky atd. Postupně upřesňovaný obraz titulního hrdiny ztrácí svou původní gloriolu - ukazuje se, že peníze a moc ještě nezaručují štěstí. Občan Kane vzbudil v době svého uvedení skandál, protože tiskový magnát miliardář William Randolph Hearst vztáhl vyprávěný příběh na sebe... Z devíti nominací na Oscara v roce 1941 získal film sice jen jednu (za scénář, na němž spolupracoval sám Welles s Hermanem J. Mankiewiczem), ale od té doby suverénně vítězí v mnoha prestižních anketách nejrespektovanějších světových kritiků.(oficiální text distributora)', NULL, 1941, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1580, '201209/Muzi v ciernom -- Men In Black en.m4v', 'Men In Black', 4, 0, 'en', 'cz', 'en', 'Men In Black', 'http://www.imdb.com/title/tt0119654/', 'Muzi v ciernom -- Men In Black en.jpg', NULL, 'Directed by Barry Sonnenfeld.  With Tommy Lee Jones, Will Smith, Linda Fiorentino, Vincent D''Onofrio. A streetwise NYPD detective joins a secret organization that polices extraterrestrial affairs on Earth.', NULL, 1997, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1581, '201209/Muzi v ciernom -- Men In Black.m4v', 'Muzi v ciernom', 4, 0, 'cz', NULL, 'en', 'Men In Black', 'http://www.csfd.cz/film/11893-muzi-v-cernem/', 'Muzi v ciernom -- Men In Black.jpg', NULL, 'Dělat tuhle práci chce nejen chladnokrevné IQ a bleskové reflexy, ale také zatraceně dobrý žaludek. Když se totiž do neposlušné potvůrky z vesmíru střelí speciální pistolí, změní se ve zlomku sekundy v odporný sliz, který zaneřádí okolí v okruhu několika metrů. "K" (Tommy Lee Jones), veterán supertajné vládní agentury MiB (Men in Black), která má na starost ostrahu planety Země před vetřelci z vesmíru, by mohl vyprávět. Schopný newyorský polda "J" (Will Smith) by mu nejspíš nevěřil ani slovo, kdyby ovšem jednoho večera na vlastní kůži náhodou nezažil, jaké to je pronásledovat mimozemšťana. Žijí mezi námi a maskují se - vysvětlil "K" - "někdo na ně musí dávat pozor" - dodal a vybral si mladého poldu za svého nového partnera. Právě včas! Nedaleko New Yorku přistál létající talíř a v něm nejodpornější a nejnaštvanější padouch celého vesmíru. Osud země zase jednou visí na vlásku...(oficiální text distributora)', NULL, 1997, '');
