INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1584, '201210/Terminator 1.m4v', 'Terminator 1', 4, 29, 'cz', NULL, 'en', 'The Terminator', 'http://www.csfd.cz/film/1249-terminator/', 'Terminator 1.jpg', 1, 'Z roku 2029 je do Los Angeles roku 1984 vyslán zabijácký stroj podobný člověku. Má za úkol zabít Sarah Connorovou dřív, než přivede na svět syna, který se má později stát vůdcem lidí ve válce proti strojům. Lidé žijící v budoucnosti ovšem do současnosti také vyslali svého muže, který se bude snažit Sarah zachránit.(oficiální text distributora)', NULL, 1984, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1585, '201210/Terminator 2 Den zuctovani -- The Judgement Day en.m4v', 'Terminator 2 The Judgement Day', 4, 29, 'en', 'cz', 'en', 'Terminator 2 The Judgement Day', 'http://www.imdb.com/title/tt0103064/', 'Terminator 2 Den zuctovani -- The Judgement Day en.jpg', 2, 'Directed by James Cameron.  With Arnold Schwarzenegger, Linda Hamilton, Edward Furlong, Robert Patrick. A cyborg, identical to the one who failed to kill Sarah Connor, must now protect her teenage son, John, from a more advanced cyborg, made out of liquid metal.', NULL, 1991, '');
